:root {
    --liveblog-border-color: #000;
}

/* Liveblog */
/*circle thing*/
.is-article-page span.artstyle__labels__live:before
 { border-color: var(--liveblog-border-color); }
/*live blog titel (oorlog in oekra√Øne*/
.is-article-page .artstyle__labels__sublabel
 { color: var(--color); }
/*Live label*/
.is-article-page .live-blog__banner__block
 { color: var(--color-dark); }
/*Bekijk nieuwe updates*/
.is-article-page .live-blog__alert--info
,.is-article-page .live-blog__update__amount
 { color: var(--color-dark) !important; }
/*klok icoontje*/
.is-article-page .live-blog__moment:after
 { background-color: var(--background-grey); color: var(--color);  }
/*tijd*/
.is-article-page .live-blog__moment__meta span
 { color: var(--color); }

/*social media content*/
.is-article-page .artstyle__disabled-embed
 { background-color: var(--background-dark-grey) !important; border-color: var(--liveblog-border-color); }
/*social media replacement teksts*/
.is-article-page .artstyle__disabled-embed h4
,.is-article-page .artstyle__disabled-embed p
{ color: var(--color); }

/*infographics*/
.is-article-page .live-blog__embed__content
 { background-color: var(--background-lighter-grey); border: solid 1px var(--liveblog-border-color); }





