style.css:
@-moz-document domain("deutschlandfunk.de"), domain("deutschlandfunkkultur.de")
{

body, .b-header, .b-header::before, .b-section-bottom-page-area{ background-color: #222}

/* light Boxes */ 
.b-article-extended-emphasis .article-extended-emphasis-default, .b-navigation, .b-teaser-xl, .b-teaser-l, .b-teaser-m .teaser-m-box-container,
.b-section-article-head-area .section-article-head-area-box, .nlform{
    background-color: #333;
	color: #ddd;
}

.b-audio-player, .b-navigation .navigation-sub,
.b-article-extended-emphasis, .b-embed-switch, .b-navigation .navigation-sub-menu{
    background-color: #404040
}

hr{
 background-color: #ccc;
}

.b-thema-teaser .info-media-label .media-duration, .b-headline.has-subline .headline-title, .b-navigation .navigation-expand-button, .b-thema-teaser .info-media-label .media-duration, .b-content-loader .content-loader-btn-more .content-loader-btn-more-text, .audio-player-btn-toggle-open, .audio-player-btn-close, .b-navigation .navigation-menu .navigation-menu-link, .b-image-credits, .b-footer-area-series .footer-area-series-content, .b-pre-footer .pre-footer-link, .b-label{
 color: #ccc; 
}
body, a, .header-date, .b-teaser-l .teaser-l-title, .b-teaser-m .teaser-m-top-area .teaser-m-heading-wrapper .teaser-m-title, .b-teaser-audiotech .teaser-audiotech-content-area .teaser-audiotech-title, .b-section-top-page-area .section-top-page-area-title, .b-schedule-item-heading, .b-teaser-program .teaser-program-title{
 color: #ddd;   
}

/* DLF Blau */
.u-sub-line, .b-headline.is-blue .headline-title, .b-headline.is-blue-kicker .headline-kicker, .b-thema-teaser .info-media-label .media-title, .pre-footer-title, .b-headline.is-black-kicker .headline-kicker, .b-headline::before, .b-teaser-m.is-teaser-content-from-df .teaser-m-heading-wrapper .teaser-headline-text, .b-teaser-l.is-teaser-content-from-df .teaser-l-content-wrapper .teaser-headline-text{
 color: #00b4fe;   
}
.b-content-loader .content-loader-btn-more .content-loader-btn-more-text{
 border-color: #00b4fe;
}
.b-button.is-deutschlandfunk,.b-sharing-menu .menu-toggle-btn {
 background-color: #00b4fe;
}

/* DLF Kultur */
.b-btn-swiper.is-next.is-very-light-bg{
	background-image: linear-gradient(to left, rgb(130, 130, 130) 0%, rgba(130, 130, 130, 0) 100%)
}
.b-btn-swiper.is-prev.is-very-light-bg{
	background-image: linear-gradient(to right, rgb(130, 130, 130) 0%, rgba(130, 130, 130, 0) 100%)
}
.b-teaser-headline, .b-section-article-head-area .section-article-head-area-subtitle, .b-pre-footer .pre-footer-title{
    color: #ff6400;
}
.b-footer::before {
	background-color: #ff6400;
}

}
