        --dt-blue: #0095f6;
        --dt-accent: #7289da;
        --ig-primary-text: #ccc;
        --ig-primary-background: var(--dt-dark);
        --ig-secondary-background: var(--dt-dark);
        --ig-highlight-background: var(--dt-input);
