/* ==UserStyle==
@name         Blue Royal Quest
@version      20220405.08.25
@namespace    userstyles.world/user/tesloz
@description  Синий стиль для форума Royal Quest
@author       tesloz
@license      No License
==/UserStyle== */

@-moz-document domain("1cgames.net") {
body#forum .ql-snow .ql-stroke,
    body#forum #pagination .inner-pagination .pages-pagination .item,
    body#forum .pagination-forum .inner-pagination .pages-pagination .item,
    #forum-topic,
    #forum-topic .name-user p,
    #forum-topic .role-user,
    #forum-menu .title-forum-menu-item,
    #forum-menu .item,
    #forum-menu .forum-menu-item .game .game-title .text,
    #forum-menu .search-icon .input-search,
    #forum-submenu .forum-submenu-text,
    #forum-submenu .forum-submenu-info,
    #comment #commentEditor,
    #sorter .item-sorter.active,
    #sorter .item-sorter .arrow svg path,
    .preview-topic,
    .text-message li,
    .message .text-message p,
    .message .title-message,
    .message .top-message p,
    .message .foot-message span,
    .message .quote .title-message,
    .quote .text-quote,
    .text-create .title-text-create p,
    .profile-full .profile-info .name,
    .profile-full .profile-info .tag,
    .actions .item-actions:hover,
    .ql-snow .ql-editor code,
    .ql-snow .ql-editor pre,
    .ql-snow .ql-tooltip,
    .ql-editing,
    .ql-editing input,
    .popup .popup-inner h4,
    .popup a.item-form,
    .popup form .item-form {
        color: #141414;
    }
    body#forum #pagination .inner-pagination .num-pagination,
    body#forum #pagination .inner-pagination .num-pagination .input,
    body#forum .pagination-forum .inner-pagination .num-pagination .input,
    #forum-menu .inner-result-search-faq .item-result-search-faq a,
    #forum-topic .details-user .details-item .text p,
    #comment .title-comment p,
    #comment .title-comment p span,
    #sorter .item-sorter p,
    #advancedSearch .two-btn,
    .text-create .item-text-create .title-item-text-create,
    .text-create .item-text-create .input-create-toggle .input,
    .popup .form-text p {
        color: #5A5A5A;
    }
    body#forum #comment .ql-snow.ql-toolbar button.ql-cancel,
    body#forum #pagination .inner-pagination .pages-pagination .item.active,
    body#forum .pagination-forum .inner-pagination .pages-pagination .item.active,
    #sorter .item-sorter.active p,
    #advancedSearch .two-btn.active,
    #forum-menu .advanced-search-btn .advanced-btn,
    .subcategories span,
    .text-create .foot-text-create .inner-foot .btn-item {
        color: #f5f5f5;
    }
    .text-create .foot-text-create .inner-foot p {
        color: #C0C0C0;
    }
    #forum-menu .item.menu-active .title-accordion,
    #forum-menu .item.target .title-accordion,
    #forum-menu .item:hover .title-accordion,
    #forum-menu .menu-active,
    .quote .text-quote a,
    .message .foot-message span:hover,
    .ql-editing .ql-action:hover,
    .ql-snow a:hover {
        color: #185886;
    }
    .preview-topic .preview-topic-paginator .item-preview-topic-paginator:hover,
    #forum-menu .inner-result-search-faq .item-result-search-faq a:hover,
    .ql-editing .ql-action,
    .ql-snow a {
        color: #185886;
    }

    /* ===== Background ===== */
    #content,
    #forum-topic,
    #forum-menu .search-icon,
    #comment .inner-comment,
    .popup a.item-form,
    .popup form .item-form {
        background: #ececec;
    }
    body#forum .pagination-forum .inner-pagination,
    body#forum #pagination .inner-pagination,
    #forum-submenu,
    #forum-menu #resultSearchFaq,
    #advancedSearch .two-btn,
    .ql-snow .ql-tooltip {
        background: #ececec;
    }
    body#forum .pagination-forum .inner-pagination .num-pagination,
    body#forum .pagination-forum .inner-pagination .num-pagination .input,
    body#forum #pagination .inner-pagination .num-pagination,
    body#forum #pagination .inner-pagination .num-pagination .input,
    body#forum #comment .ql-snow .ql-toolbar button,
    body#forum #comment .ql-snow.ql-toolbar button,
    body#forum .ql-container.ql-snow,
    body#forum .ql-snow .ql-toolbar button,
    body#forum .ql-snow.ql-toolbar button,
    #forum-menu,
    #comment #commentEditor,
    #sorter .item-sorter,
    #emoji-palette #tab-panel,
    #emoji-palette #tab-toolbar,
    .text-create .item-text-create .input-create-toggle .input,
    .profile-full,
    .ql-editing input,
    .popup .popup-inner {
        background: #f5f5f5;
    }
    body#forum #comment .ql-snow.ql-toolbar button.ql-cancel,
    .text-create .foot-text-create .inner-foot .btn-item,
    .ql-editing {
        background: #141414;
    }
    #comment #commentEditor .ql-editor ol li:before,
    #comment #commentEditor .ql-editor ul li:before,
    #sorter .item-sorter.active,
    #advancedSearch .two-btn.active,
    #forum-menu .advanced-search-btn .advanced-btn,
    .subcategories span {
        background: #5A5A5A;
    }
    #forum-topic .details-user .details-item.line {
        background: #787878;
    }
    body#forum #pagination .inner-pagination .pages-pagination .item.active,
    body#forum .pagination-forum .inner-pagination .pages-pagination .item.active {
        background: #185886;
    }

    /* ===== Background color ===== */
    #forum-menu .search-icon .input-search,
    .text-message,
    .ql-snow .ql-editor code,
    .ql-snow .ql-editor pre {
        background-color: #ececec;
    }
    body#forum #comment .ql-snow .ql-toolbar button.ql-active,
    body#forum #comment .ql-snow.ql-toolbar button.ql-active,
    .popup a.submit,
    .popup form input.submit {
        background-color: #185886;
    }

    /* ===== Arrows ===== */
    #forum-menu .forum-menu-item .game .game-title .arrow:after {
        border-right: 1px solid #141414;
        border-top: 1px solid #141414;
    }
    body#forum #pagination .inner-pagination .pages-pagination .item span.arrow.right:before,
    body#forum .pagination-forum .inner-pagination .pages-pagination .item span.arrow.right:before {
        border-left: 2px solid #5A5A5A;
        border-top: 2px solid #5A5A5A;
    }
    body#forum #pagination .inner-pagination .num-pagination .go-page:before,
    body#forum .pagination .inner-pagination .num-pagination .go-page:before,
    body#forum .pagination-forum .inner-pagination .num-pagination .go-page:before {
        border-left: 2px solid #787878;
        border-top: 2px solid #787878;
    }
    body#forum #pagination .inner-pagination .pages-pagination .item span.arrow.left:before,
    body#forum .pagination-forum .inner-pagination .pages-pagination .item span.arrow.left:before {
        border-right: 2px solid #5A5A5A;
        border-bottom: 2px solid #5A5A5A;
    }

    /* ===== Icons ===== */
    #content.swich-topic #swich-forum-submenu {
        background: url(data:image/svg+xml;base64,) no-repeat 50% 50% #f5f5f5;
    }
    #content.third-position #swich-forum-submenu {
        background: url(data:image/svg+xml;base64,) no-repeat 50% 50% #f5f5f5;
    }
    .preview-topic .preview-topic-details span.user {
        background-image: url(data:image/svg+xml;base64,);
        opacity: .75;
    }
    .preview-topic .preview-topic-details span.data {
        background-image: url(data:image/svg+xml;base64,);
        opacity: .75;
    }
    .preview-topic .preview-topic-details span.comments {
        background-image: url(data:image/svg+xml;base64,);
        opacity: .75;
    }
    #forum-menu #forum-menu-collapse {
        background: url(data:image/svg+xml;base64,) no-repeat 50% 50%;
    }
    #forum-menu .item {
        background: url(data:image/svg+xml;base64,) no-repeat 0 3px;
    }
    #forum-menu .panel .item {
        background: url(data:image/svg+xml;base64,) no-repeat 2px 4px;
    }
    .subcategories {
        background: url(data:image/svg+xml;base64,) no-repeat 2px 12px;
    }
    .profile-full .close-friend {
        background: url(data:image/svg+xml;base64,) no-repeat 50% 50%
    }
    #forum-menu .search-icon .search-icon-on {
        background: url(data:image/svg+xml;base64,) no-repeat calc(100% - 14px) 50%;
    }
    #forum-menu.search .search-icon .search-icon-clear,
    #comment .title-comment .close-comment,
    .popup .title-closet {
        background: url(data:image/svg+xml;base64,) no-repeat 0 50%
    }
    #forum-topic .details-user .icon svg path {
        fill: #5A5A5A;
    }
    .preview-topic .preview-topic-details {
        opacity: .75;
    }
    .preview-topic .preview-topic-paginator .item-preview-topic-paginator:hover {
        opacity: .85
    }
    .text-message li {
        text-shadow: none;
    }
    #sorter .item-sorter.active .arrow svg path {
        fill: #ececec
    }

    /* ===== Misc ===== */
    .ql-snow .ql-stroke {
        stroke: #ececec;
    }
    body#forum .ql-snow .ql-fill,
    body#forum .ql-snow .ql-stroke.ql-fill {
        fill: #5A5A5A;
    }

    body#forum .ql-snow .ql-stroke {
        stroke: #5A5A5A;
    }
    .ql-snow .ql-tooltip input {
        border: #f5f5f5;
    }
    .ql-snow .ql-tooltip {
        border: 1px solid #141414;
        box-shadow: 0 0 5px #141414;
    }
    .ql-editing {
        background: #ececec!important;
        color: #141414!important;
    }
    body#forum #pagination .inner-pagination .pages-pagination .item span,
    body#forum .pagination .inner-pagination .pages-pagination .item span,
    body#forum .pagination-forum .inner-pagination .pages-pagination .item span {
        color: inherit;
    }
    blockquote,
    .ql-snow .ql-editor blockquote {
        border-left: 3px solid #185886;
    }
    /*Misc added by me;Thanks Armitage for core*/
    body#forum header, #main-menu li ul li>ul.two-level, #main-menu .children-toggle, footer {
        background: #185886;
    }
    #main-menu li .link-main-menu.active, #main-menu li:hover>.link-main-menu {
        color: #f2930d;
    }
    #main-menu li .item-desktop a.btn{
        background: #f2930d;
    }
    #main-menu li .item-desktop a.btn:hover{
        color: #0d0812;
    }
    .message .text-message a{
        color: #2577b1;
        text-decoration: none;
    }
    .message .text-message a:hover{
        color: #185886;
        text-decoration: underline;
    }
    #forum-topic .ava-user{
        width: 90px;
        height: 90px;
        border-radius: unset;
        border: 1px solid #fff;
        -webkit-box-shadow: 0px 2px 2px rgb(0 0 0 / 10%);
        -moz-box-shadow: 0px 2px 2px rgba(0,0,0,0.1);
        box-shadow: 0px 2px 2px rgb(0 0 0 / 10%);
    }
    .profile-full .profile-ava-img{
        border-radius: unset;
        border: 1px solid #fff;
        -webkit-box-shadow: 0px 2px 2px rgb(0 0 0 / 10%);
        -moz-box-shadow: 0px 2px 2px rgba(0,0,0,0.1);
        box-shadow: 0px 2px 2px rgb(0 0 0 / 10%); 
    }
    .show-topic.active h3{
        color: #185886;
    }
    #main-menu li ul li:hover>ul.two-level li{
        border-bottom: 1px solid #fff;
    }
    #main-menu li ul li:hover>ul.two-level li:nth-of-type(1){
        border-top: 1px solid #fff;
    }
    #main-menu li ul li:hover>ul.two-level li:last-of-type{
        border:none;
    }
    #main-menu ul.two-level li a.btn-main-menu:hover{
        color: #f2930d;
        border: 1px solid #f2930d;
    }
    .item-footer a.duration:hover{
        color: #f2930d;
    }
    .info-user p.role-user.Admin{
        font-style: normal;
        padding: 1px 6px;
        border: 1px solid transparent;
        border-radius: 2px;
        text-align: center;
        color: #fff !important;
        background: #d80000;
        border-color: #a50000;
    }
    footer .item-footer ul li .title-footer{
        color: #fff;
    }
    footer .item-footer ul li a.red{
        background: #f2930d !important;
    }
    #main-menu .toggle .item-toggle:hover{
        color: #f2930d;
    }
    #forum-topic .topic-comments{
        border-width: 1px;
        border-style: solid;
        border-top-color: #dfdfdf;
        border-right-color: #d8d8d8;
        border-bottom-color: #cbcbcb;
        border-left-color: #d8d8d8;
        border-radius: 4px;
        margin-bottom: 10px;
    }
    #forum-topic .topic.main{
        border-width: 1px;
        border-style: solid;
        border-top-color: #dfdfdf;
        border-right-color: #d8d8d8;
        border-bottom-color: #cbcbcb;
        border-left-color: #d8d8d8;
        border-radius: 4px;
    }
    #forum-topic .topic-content{
        border-bottom-color: #cbcbcb;
    }
    .onscroll:hover ::-webkit-scrollbar-thumb {
        background-color: #185886;
    }
    body#forum #comment .ql-snow.ql-toolbar button.ql-send {
        background: #185886;
        color:#fff;
    }
    .text-create .foot-text-create .inner-foot .btn-item.red{
        background: #185886;
        color:#fff;
    }
    body#forum #comment .ql-snow.ql-toolbar button.ql-send:hover, .text-create .foot-text-create .inner-foot .btn-item.red:hover{
        background:#f2930d;
    }
    .actions .item-actions.red{
       color: #185886; 
    }
    .breadcrumbs .red {
        color: #185886;
    }
    #content{
        margin-bottom: 0px;
    }
}
