style.css:
@-moz-document domain("www.clickcritters.com") {
body {
    background: url(https://www.clickcritters.com//images/layout/traptor_sky/wallpaper2.png) left top repeat, url(https://i.imgur.com/joHmbUb.png) left top repeat-x;
    background-image: url(https://www.clickcritters.com//images/layout/traptor_sky/wallpaper2.png), url(https://i.imgur.com/joHmbUb.png);
    background-position-x: left, left;
    background-position-y: top, top;
    background-size: initial, initial;
    background-repeat-x: repeat, repeat;
    background-repeat-y: repeat, no-repeat;
    background-attachment: initial, initial;
    background-origin: initial, initial;
    background-clip: initial, initial;
    background-color: #851B88;
}

#megaWrapper {
    background-image: none;
}



#megaHeader {
    background-image: none;
}

#imgHeaderLeft {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url('https://i.imgur.com/yWa9AX2.png') no-repeat;
    width: 1200px;
    height: 125px;
    padding-left: 1200px;
    margin-left: auto;
    margin-right: auto;
}



#imgHeaderRight {
    height: 0px;
}

textarea {
    font-size: 10pt;
    font-family: Verdana;
}

td,
p,
body {
    font-size: 8pt;
    font-family: Verdana;
    text-align: center;
}

a:hover {
    color: #6b4665;
    text-decoration: none;
}

#megaAd {
    width: 160px;
    background-color: #e0d0df;
    vertical-align: top;
    margin-left: -50px;
}

#megaAd iframe {
    position: absolute;
    right: 0px;
    padding-top: 9px;
}

#megaTopNav,
#megaSearch {
    background-color: #711873;
    background-image: url('https://i.imgur.com/6dUJmfH.jpg');
}

#megaSidebar {
    background-color: #864e87;
}

#megaUserinfo {
    background-color: #b696b7;
    background-image: url('https://i.imgur.com/OAXkwKa.jpg');
    background-repeat: repeat-x;
}


#megaContent,
#megaFooter {

    background-color: #ba93b7;
    /* The color of the background that is usually white is set here. White is #fff */
}

#megaFooter {
    padding-left: 15%;
}

#megaBottomLeft {
    background-color: #875186;
}

#megaBottomLeftImg {
    background-image: url('https://i.imgur.com/biAR4vb.png');
}

#megaBottomRight {
    width: 160px;
    background-color: #ba93b3;
}

#megaBottomRightImg {
    background-image: url('https://i.imgur.com/i2YnPDc.png')
}

h1 {
    background-color: #a64ba8;
    border: 2px solid #5d0c60;
}

#topNavLinks li ul.subnav {
    background: #a84ba5;
    border: 2px solid #6b4667;
}

#topNavLinks li ul.subnav li {
    margin: 0;
    padding: 0;
    border-top: 1px solid #4f0a4f;
    /*--Create bevel effect--*/
    border-bottom: 1px solid #4f0c4f;
    /*--Create bevel effect--*/
    clear: both;
    width: 170px;
    height: 20px;
}

#topNavLinks li ul.subnavi li a {
    color: #fceafb;
}

html #topNavLinks li ul.subnav li a:hover {
    background-color: #6a466b;
    color: #fcebfc;
}

div.forum {
    color: #6b466b;
    background-image: url('https://i.imgur.com/0Ze8sKb.jpg');
    background-repeat: repeat-x;
    background-color: #a3759d;
}

div.forum div {
    background-image: none;
}

div.forum a {
    color: #280e28;
}

div.category {
    vertical-align: center;
    background-color: #6b4667;
    background-image: url('https://i.imgur.com/6dUJmfH.jpg');
    background-repeat: repeat-x;
}

div.category div {
    background-color: #ba93b7;
    background-image: none;
}

#forumtable tbody {
    background-color: #6b4663;
}

#forumtable tr.unread {
    background-color: #dbb6d2;
}

#forumtable tr .off {
    background-color: #ead7e7;
}

#forumtable tr a:hover {
    color: #fff;
    text-decoration: none;
}

#forumtable tr:hover {
    background-color: #b285ad;
}

form input {
    background-color: #f7e6f4;
}

textarea,
select,
input {
    background-color: #fceaf9;
    font-family: verdana;
}

input[type=submit] {
    border-radius: 5px;
    background: linear-gradient(#fceaf9, #c6a1c0);
}

#mail_heading tr,
#mail_heading td {
    background-color: #d381b9;
    border: solid 1px #490335;
}
}
