/* ==UserStyle==
@name			gandalf
@author			Quentin QUero
@version		1.0.1
@namespace		cascadea3187230757
@preprocessor	default
==/UserStyle== */

@-moz-document domain("gandalf.epitech.eu")
{
	#page {
		padding-top: 120px;
	}
	
	.tag_cloud .s20 {
		font-size: 1.2em;
	}
	
	#block-region-content .block, #block-region-side-post .block {
		overflow: hidden;
	}
	.coursebox.panel.hover .panel-body .coursebox-content h3 {
		font-size: 1em;
	}
	
	.text-info{
		color: #0072bc !important;
	}
	
	
	.courses .coursebox {
		background-color: #f0f0f0 !important;
		padding: 4px;
		border-radius: 12px;
	}
	
	#page {
		background-image: none !important;
		background-color: #000000;
	}
	
	.breadcrumb ul i, .breadcrumb li.lastli span {
		color: #0272bc;
		text-decoration: none;
	}
	
	::selection {
		color: #000;
		background-color: #5cc0de !important;
	}
	
	div.activity-wrapper {
		border-left: 1px solid #CCC !important;
		border-radius: 10px;
	
	}
	
	.activity img.iconlarge {
		height: 30px;
		width: 30px;
	}
	
	
	
	.ishidden .badge-info {
		color: #000;
		background-color: #ffc108;
		border-bottom: 1px solid #ffc108 !important;
		border-radius: 10px;
	}
	
	.isrestricted .badge-info {
		color: #FFF;
		background-color: #e04957;
		border-bottom: 1px solid #e04957 !important;
		border-bottom-left-radius: 10px;
		border-top-left-radius: 10px;
	}
	
	.isrestricted strong {
		border-bottom: 1px solid #e04957 !important;
	
	}
	
	#adaptable-page-header-wrapper #main-navbar li .action-menu-trigger a:hover {
		color: #5cc0de;
	
	}
	
	#adaptable-page-header-wrapper #main-navbar li a:hover {
		background-color: #5cc0de;
		text-decoration: none;
	}
	
	h1, h2, h3, h4, h5 {
		text-transform: uppercase;
		color: #0272bc;
	}
	
	.nav-link {
		/* border: 1px solid black;
	    width: 70px;
	    overflow: hidden;*/
		white-space: nowrap;
	}
	
	.activity-wrapper {
		padding-left: 1.5% !important;
	}
	
	.section li.modtype_forum div.activity-wrapper {
		-webkit-box-shadow: -3px 0 0 0 #980399;
		padding: 10px;
		border: 1px solid #980399 !important;
		border-radius: 10px;
	}
	
	.section li.modtype_assign div.activity-wrapper {
		-webkit-box-shadow: -3px 0 0 0 #0272bc;
		padding: 10px;
		border: 1px solid #0272bc !important;
		border-radius: 10px;
	}
	
	@media (max-width: 979px) {
		.page-header-margin.header-style1 #page {
			margin-top: 0;
		}
	}
	
	.instancename {
		border-bottom: 1px solid #acacab !important;
		margin: 1em;
		padding: .1em;
	}
	
	.block {
		border-top: 1px solid #bbb !important;
	}
	
	.sectionname {
		background-color: #0272bc !important;
		border-radius: 0;
	}
	
	
	.section .activity .contentwithoutlink>a, .section .activity .activityinstance>a {
		text-indent: 0px !important;
	}
	
	.section .activity .contentwithoutlink .dimmed .activityicon, .section .activity .activityinstance .dimmed .activityicon {
		opacity: 1 !important;
	}
	
	.btn-info{
		background-color: #0272bc !important;
	}
	
	.linkRecalcRes{
		max-width: 400px;
		height: auto !important;
		width: 100%;
	}
	
	.search-box{
		border-top-right-radius: 10px !important;
	}
	
	
	#dropdownmain-navigation0 {
		max-height: 600px;
	    overflow-x: hidden;
	    overflow-y: auto;
	}
}
