style.css:
@-moz-document domain("pinboard.in")
{
* {
    font-family: Arial, sans-serif;
}
div#logo {
    font-size: 18px !important;
}
div#logo img.pin_logo {
    content: url("https://pinboard.in/blue-pin.png") !important;
    width: 24px;
    height: 24px;
    filter: invert(0.5);
}
body {
    background-color: #202124 !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-size: auto !important;
    color: #aaa !important;
}
p, .blurb {
	color: #bdc1c6 !important;
	}

    h2.settings_heading {
    background: #2c2c2c !important;
}
    .settings_tab_selected {
    border: 1px solid #aaa !important;
    border-bottom: 1px solid #222 !important;
    background: #2c2c2c !important;
    color: #efefef !important;
}
    a, a:hover, a:visited, a:active {
    color: #9e9e9e;
}
    textarea, input, input[type="checkbox"] {
    background-color: #2a2a2a !important;
    border: 1px solid #424242 !important;
    color: #d5d5d5 !important;
    accent-color: #8bc34a !important;
}
.display {
    line-height: 1.5em !important;
}
#banner,
.user_navbar,
#tag_cloud {
    font-size: 115% !important;
}
#banner {
    background: transparent;
}
.bookmark_title {
    font-size: 1.6em !important;
    color: #8ab4f8 !important;
}
div.display a {
    color: #626262;
}
a.banner_username,
div#main_column p b a,
div#main_column p a,
div#top_menu a,
div#footer a,
a.url_display:hover,
a.edit:hover,
a.delete:hover,
div#title a,
div.service_box a {
    color: #4183c4 !important;
}
div#footer hr {
    border-top-color: #484848 !important;
}
div.small_username b a,
div#main_column b a,
a#login {
    color: #8ab4f8 !important;
}
.private {
    margin-top: 15px !important;
    background: #2a2a2a !important;
    border: 1px solid #484848 !important;
}
a.url_display {
    font-size: 1.2em !important;
}
a.edit {
    color: #8cb0cb !important;
}
a.delete {
    color: #8b5858 !important;
}
a.url_link {
    padding: 1px 2px !important;
    font-weight: 300 !important;
}
div.search_button p label {
    color: #888 !important;
}
input[type="submit"] {
    background: linear-gradient(#4183c4, #265aba) !important;
    border-color: #083 !important;
    position: relative !important;
    display: inline-block !important;
    padding: 2px 3px !important;
    font-size: 13px !important;
    font-weight: 600 !important;
    line-height: 20px !important;
    white-space: nowrap !important;
    vertical-align: middle !important;
    cursor: pointer !important;
    user-select: none !important;
    border: 1px solid rgba(27, 31, 35, 0.2) !important;
    border-radius: 0.25em !important;
    -webkit-appearance: none !important;
    color: #e2e2e2 !important;
}
input[type=submit]:hover {
    background: linear-gradient(#265aba, #4183c4) !important;
}
a.tag {
    color: #707070 !important;
}
a.tag.selected {
    background-color: #ffaaaa !important;
}
.description blockquote {
    color: #888 !important;
}
.description,
div#main_column p b,
div#main_column b,
div#profile_main_column,
td {
    margin: 5px 0 !important;
    font-size: 1.2em !important;
    line-height: 1.4em !important;
    color: #888 !important;
    font-weight: 400 !important;
}
.when,
.edit_links {
    margin: 5px 0 !important;
    font-size: 1.1em !important;
    line-height: 1.4em !important;
    color: #888 !important;
    font-weight: 300 !important;
}
.star,
a.cached {
    font-size: 200% !important;
}
.star,
.cached,
.source {
    display: none !important;
}
.url_link {
    background-color: #3b7ac2 !important;
    border: 1px solid #4b93f8 !important;
    font-size: 1.1em !important;
    line-height: 1.4em !important;
    font-weight: 300 !important;
    color: #b4d8f7!important;
}
a.tag {
    font-weight: 0 !important;
    font-size: 1em !important;
}
}
