style.css:
@-moz-document domain("nitrotype.com") {

body {
    background: none;
}
html {
    background: fixed url("https://img4.goodfon.com/wallpaper/nbig/e/aa/paskha-easter-pastel-iaitsa-krashenye-spring-eggs-happy.jpg"), #3e3c40;
    background-size: 100%;
}

.bg--gradient {
    background: fixed;
}

.card--grit {
    background: none;
}
.bg--gradient {
    background: fixed;
}
.hp-heroBG {
    background: url("https://www.itl.cat/pngfile/big/284-2843894_wallpaper-eggs-easter-spring-easter-eggs-decoration-pastel.jpg");
    background-size: 100%;
}
.hp-heroCopy {
    color: #fffb00;
    padding-top: 60px;
    margin-bottom: 60px;
    position: relative;
    z-index: 2;
}
div.flash.flash--beta {
    display: none;
}
.structure.structure--flashed.structure--noAds {
    padding-top: 50px;
}
.structure-ad {
    display: none;
}

.theme--pDefault .profile-bg
  {
    background: url("https://cdn.wallpapersafari.com/25/34/N5AUPx.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
    background-size: 100%
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn.wallpapersafari.com/25/34/N5AUPx.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
   background-size: 100%
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://img4.goodfon.com/wallpaper/nbig/3/f4/decoration-easter-paskha-iaitsa-krashenye-spring-eggs-happy.jpg") left bottom, linear-gradient(0deg, #f3ff86 60%, #c9c637 100%);
   background-size: 100%
  }
 .theme--pDefault .profile-username {
    text-shadow: 0 0 7px #f3ff86;
}
.news--solo .news-body p {
    color: #ffffff;
    line-height: 1.8;
}
.news-teaser {
    color: #ffffff;
    margin-bottom: 40px;
}
.card--a {
    color: #ffffff;
    background: transparent;
   
}
 
.card--shadow-l {
    box-shadow: none;
}
.btn--inky {
    color: #fff;
}
.card--b {
    color: #fff;
}
.theme--pDefault .profile-body {
    color: #ffffff;
}
.card--grit {
    background: none;
}
.dash-copyContainer {
    background: #fdff8633;
    box-shadow: 0 1px 157px 10px #000000c7;
}

.dash-copy {
    color: #fff;
       text-shadow: 0 0 5px #fbff86, 0 0 7px #ffe600;
      font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
      font-size: 19px; 
   
}
.dash-letter.is-waiting {
    background: #fff7eba6;
    color: #fff;}
.dash-letter.is-incorrect {
    background: #e20b0bbd;
    color: #fff;}

.btn--primary {
    color: #605e5ec2;
     text-shadow: 0 0 5px #fff, 0 0 7px #fff;
    background-size: 100%;
    
  
    background-color: #ffe986 !important;
    border-color: white
   
}
.btn--secondary {
    background-color: #0000 !important;
    color: #fff;
    background-size: 100%;
    border-color: white
}

.growl--a {
    background-image: url("https://images.unsplash.com/photo-1488867605300-0380106bc701?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxleHBsb3JlLWZlZWR8NHx8fGVufDB8fHx8&w=1000&q=80");
    border-color: #02020233;
    background-size: 150%;
    color: #fff;
}
.growl-close {
    background: #fffb00;
    border-radius: 50%;
    border: 1px solid #fffb00;
    color: #fff;
    padding: 10px;
    position: absolute;
    right: -20px;
    top: -5px;
    transition: all 0.25s ease-in-out;
}

.raceResults--default 
{background-image: url("https://wallpaperboat.com/wp-content/uploads/2019/03/clear-easter-wallpaper-001.jpg");
          background-size: 100% }
}
