style.css:
#header, .content-bubble-arrow, #currently, #footer, #timeline_heading, #trend_info {display:none !important;}

body {background-image:none; background-color:#fff; overflow:hidden;}

.status-btn input {
width:auto !important;
background-image:none !important;
border:1px solid #ccc !important;
}
.info textarea {width:auto !important;}
.section, .wrapper {padding:0 !important;}
#pagination a { width: 200px; margin:0 auto;}

#container {
margin:0;
width:100%;
}

#content {
border-bottom-left-radius:0px;
border-top-left-radius:0px;
}

.status-body {
width: auto !important;
padding-right:20px;
}

.entry-content {
font-size: 15px !important;
}

.entry-meta span {display:none;}
.entry-meta a span {display:inline;}

#timeline li span a img { margin-left:5px;}

#side_base {
position:absolute;
bottom:-212px;
left:0px;
border-bottom-right-radius:0px;
border-top-right-radius:0px;
}

.promotion, #custom_search, #saved_searches, #side_lists, #trends, #following, #rssfeed { display:none !important; }

ol.statuses li.last-on-page, ol.statuses li.last-on-refresh { border-bottom:1px solid #000 !important; }

.wide {width:350px !important; }
.wide .status-body { margin-right: 5px !important; }
