style.css:
@-moz-document url-prefix("http://www.forest.impress.co.jp/docs/"),
url-prefix("http://www.forest.impress.co.jp/prize/") {
	#extra,
	#content-inner div[style="padding-top: 0px;"],
	#content-inner div[style="padding-top: 10px;"]
	{
		display:none !important;
	}
	#content-inner{
		margin:0 50px 0 150px !important;
	}
}
@-moz-document domain("internet.watch.impress.co.jp") {
	.ipw_right,
	div[style="padding-top: 5px;"],
	div[style="padding-top: 15px;"]
	{
		display:none !important;
	}
	.ipw_main>.contents.news{
		width: 90% !important;
	}
}
