}
.docs-gm .docs-gm-sidebar-icon-path {
    fill: #7557ff
}
.docs-gm .docs-menubar .goog-control, .docs-title-widget {
    filter: hue-rotate(35deg);
}
#titlebar-mode-indicator-container>div {
