style.css:
@-moz-document url-prefix("https://tradersdiaries.com") {
:root .theme-dark {
    --color-head: rgba(0, 0, 0, .8);
    --color-bg: #121212;
    --color-font: #fff;
    --color-font-muted: rgba(255, 255, 255, 0.8);
    --color-font-link: #f0b90b;
    --color-font-placeholder: rgba(255, 255, 255, 0.4);
    --color-error: #f88;
    --color-border: rgba(0, 0, 0, 0.3);
    --color-bg-th: #000;
    --color-bg-row1: rgba(255, 255, 255, 0.1);
    --color-bg-row2: rgba(255, 255, 255, 0.15);
    --color-bg-row-hover: rgba(255, 255, 255, 0.2);
    --color-bg-row1-col2: rgba(255, 255, 255, 0.05);
    --color-bg-row2-col2: rgba(255, 255, 255, 0.1);
    --color-bg-row1-col2-hover: rgba(255, 255, 255, 0.15);
    --color-bg-pos: rgba(64, 225, 109, 0.1);
    --color-bg-neg: rgba(255, 57, 81, 0.1);
    --color-bg-best: rgba(64, 225, 109, 0.3);
    --color-bg-worst: rgba(255, 57, 81, 0.3);
    --color-btn-bg: #3696EF;
    --color-notify-bg: #efe;
    --color-bg-modal: rgba(0, 0, 0, 0.5);
    --color-tooltip-bg: #fff;
    --color-tooltip-font: #000;
    --color-tooltip-font-link: #00e;
    --color-tooltip-border: #dee2e6;
    --tooltip-svg-filter: none;
    --svg-filter: invert(1);
}
.b24-widget-button-shadow, .b24-widget-button-wrapper {
    display: none !important
}
td, th {
    padding: 0.5rem 1rem !important;
    border: none !important;
}
.lk-menu {
    background: rgba(0, 0, 0, .8) !important;
    backdrop-filter: blur(0.5rem);
    padding: 1rem;
    margin: 0 !important;
    position: sticky;
    position: -webkit-sticky;
    top: 0;
}
.head {
    margin: 0;
    padding: 1rem;
    background: var(--color-head);
}
.content-wrap {
    margin: 0 !important;
    margin: auto;
    min-width: 100% !important;
    max-width: 100% !important;
    width: 100% !important
}
#content {
    margin: 0 !important;
    min-width: 100% !important;
    max-width: 100% !important;
    width: 100% !important
}
.form-submit {
    margin: 0 1rem 0 0.5rem !important
}
.btn-share-copy {
    margin-right: 1rem;
}
.btn-toolbar button {
    width: calc(20px + 2rem);
    height: calc(20px + 2rem);
    margin-right: 1rem;
}
.btn-toolbar button img {
    margin: auto;
}
.btn-toolbar {
    margin: 1rem 1rem 0 0;
    width: calc(60px + 8rem);
}
.bg-pos {
    color: #40E16D;
}
.bg-neg {
    color: #FF3951;
}
.right {
    margin-left: 1rem !important;
}
.trade-head {
    margin: 0.5rem 0 1rem 0;
}
button {
    padding: 0.5rem 1rem !important;
    border-radius: 0.5rem;
    border: none;
    max-height: 33px !important;
}
* {
    border: none !important;
    font-size: 14px;
}
.form-control {
    margin: 0 !important;
}
form {
    display: flex;
}
input, textarea {
    padding: 0.5rem 1rem !important;
    border-radius: 0.5rem;
    border: none;
    margin: 0 !important;
}
.btn-share-copy, .btn-share-telegram {
    width: 100% !important
}
.separator td {
    padding: 0 !important;
}
.week {
    padding-top: 1rem;
}
body {
    font-family:"Inter";
    margin: 0;
    line-height: 1 !important;
}
.subscribe {
    display: none;
}
.caption, .tooltip-icon {
    display: none !important;
}
.head:before {
    content:"Fuck Crypto :)";
    font-weight: 700;
    font-size: 21px;
    margin-left: 0.5rem;
}
.lk-nav a {
    border: none !important;
    padding: .5rem !important;
}
thead {
    position: sticky !important;
    position: -webkit-sticky !important;
    top: 61px !important;
}
table {
    border: none !important;
    border-image-width: 0 !important;
    border-spacing: 0 !important;
}
}
