/* ==UserStyle==
@name         Dark Intra
@version      20220207.09.07
@namespace    userstyles.world/user/thibb1
@description  `Save your eyes on Epitech Intra with this dark theme.Also adds some smalls hacks and life improvements for more readability !`
@author       thibb1
@license      No License
==/UserStyle== */
@-moz-document domain("intra.epitech.eu") {
	/* -=- Main theme -=- */
	/* -=- Root Colors -=- */
	:root {
		--background: #181818;
		--background-2: #202020;
		--background-3: #303030;
		--foreground: #ccc;
		--foreground-2: white;
		--foreground-3: #bbb;
		--foreground-black: black;
		--main: #2d90ff;
		--accent: #00ecff;
		--yellow: #efaf16;
		--orange: #b55815;
		--blue: #159cff;
		--green: #1e9563;
		--border-color: #38444d;
		--border: 2px solid var(--border-color);
		--radius: 18px;
	}


	/* -=- Module full display -=- */
	#module div.bloc.main {
		display: block !important;
	}

	span.toshow {
		display: none;
	}

	div.main-wrapper {
		color: white;
	}

	div.expandable div {
		overflow: visible !important;
		;
	}

	.bulle {
		opacity: 1 !important;
	}

	a {
		color: var(--main);
	}

	.item.semestre label,
	.item.credits label {
		color: var(--foreground) !important;
	}


	/* -=- Front page -=- */
	#homeboard,
	body {
		background-color: var(--background);
	}


	/* -=- gdpr-banner -=- */
	#gdpr-banner {
		border-radius: var(--radius);
		background-color: var(--background-3);
		max-width: 50%;
		margin-left: 25%;
		margin-top: 5%;
	}

	#gdpr-banner .button span,
	#gdpr-banner .body .button {
		background: none !important;
		height: 25px !important;
		width: 25px;
		border-radius: var(--radius);
	}

	#gdpr-banner .button.confirm {
		background-color: var(--main) !important;
		margin-left: 5px !important;
	}

	#gdpr-banner .button.deny {
		background-color: var(--yellow) !important;
		margin-left: 5px !important;
	}

	#gdpr-banner .button .label {
		display: none;
	}


	/* -=- Header -=- */
	.intra {
		background: none !important;
		background-color: var(--background) !important;
		border-bottom: solid 2px var(--border-color);
	}

	.layer {
		background: none !important;
		border: none !important;
		color: var(--foreground);
		box-shadow: none !important;
	}

	.layer .item {
		background-color: var(--background-2) !important;
		border: 2px solid var(--border-color) !important;
		margin-bottom: 5px !important;
		padding-left: 10px !important;
		border-radius: var(--radius) !important;
	}

	.layer .item:hover {
		background-color: var(--background-3) !important;
	}

	.layer .item a {
		color: var(--main) !important;
	}

	#notification {
		background: none !important;
	}

	.infos {
		display: none !important;
	}

	#header #language {
		width: initial !important;
	}


	/* -=- Index -=- */
	#breadcrumb {
		background-color: var(--background) !important;
		color: var(--foreground);
	}

	#breadcrumb ul,
	#breadcrumb ul li,
	#breadcrumb ul li a span,
	#breadcrumb ul li:first-child {
		background: none;
	}

	#breadcrumb ul {
		width: fit-content;
		padding-right: 5px;
		background-color: var(--background-2) !important;
		border-radius: var(--radius);
	}

	#header .intra .navigation {
		overflow: inherit !important;
	}

	#header .intra .navigation tr td .button .icon {
		background-position-x: -43px !important;
	}

	#header .intra .navigation tr td .button .label {
		border-radius: var(--radius);
		background-color: var(--background-3);
		position: relative !important;
		padding: 8px !important;
		bottom: -26px;
		max-width: 70px;
	}


	/* -=- Section -=- */
	section,
	.articles article {
		background-color: var(--background-2) !important;
		border-radius: var(--radius)!important;
		padding: 10px !important;
	}

	.view_more {
		border: none !important;
		background-color: var(--background-2) !important;
		border-radius: var(--radius) !important;
		line-height: initial;
	}

	.link_delete {
		display: initial !important;
		background: none !important;
		color: red !important;
	}

	.articles article {
		background-color: var(--background-3) !important;
	}

	.icon.no-picture,
	section h2 span.icon {
		filter: invert(1);
	}

	section h2 a {
		color: var(--main);
	}

	.articles article a {
		color: var(--accent);
	}

	.warning {
		color: var(--yellow) !important;
	}

	#homeboard {
		border-bottom: none
	}


	/* -=- Left pannel -=- */
	#toggle-sidebar,
	#sidebar .trombi {
		display: none !important;
	}

	#sidebar {
		background-color: var(--background);
	}

	.rzone a {
		color: var(--accent) !important;
	}

	.rzone .ui-active a,
	.rzone a:hover {
		color: var(--blue) !important;
	}

	#wall #sidebar a:hover {
		background-color: transparent;
	}

	#sidebar form.filter.course .datatree > dl dd {
		background: none;
	}

	.rzone hr,
	#sidebar form.filter.course .datatree dt a .actions.ui-contextmenu-active,
	#sidebar form.filter.course .datatree dt:hover a .actions,
	#sidebar form.filter.course .datatree dt.module.current.expanded .label .modif.label,
	#sidebar .wrapper .button.toggle.location {
		display: none;
	}

	#sidebar form.filter.course .datatree > dl {
		max-width: 280px
	}

	#sidebar form.filter.course .datatree dt.module > a .icon:first-child {
		background: none;
		border-radius: var(--radius);
		margin-right: 6px;
	}

	#sidebar form.filter.course .datatree .notregistered .icon.registration {
		background-color: var(--border-color) !important;
	}

	#sidebar form.filter.course .datatree .ongoing .icon.registration {
		background-color: var(--blue) !important;
	}

	#sidebar form.filter.course .datatree .valid .icon.registration {
		background-color: var(--green) !important;
	}

	#sidebar form.filter.course .datatree .fail .icon.registration {
		background-color: var(--orange) !important;
	}

	#sidebar a {
		color: var(--accent);
	}

	#sidebar form.filter.course .datatree {
		margin-left: 10px;
	}

	#sidebar form.filter.course .datatree .module {
		background-color: var(--background-2);
		margin-bottom: 5px;
		margin-left: 5px;
		border-radius: var(--radius);
	}

	#sidebar form.filter.course .datatree .expanded,
	#sidebar .planning.miniview table td.weekend {
		box-shadow: none;
		background-color: var(--background-3);
		border-radius: var(--radius) !important;
		border: none;
	}

	#sidebar form.filter.course .datatree dt:hover {
		background-color: var(--background-3);
		border-radius: var(--radius);
	}

	#sidebar form.filter.course .datatree dl dd dl dt .label {
		padding-left: 5px;
	}

	#sidebar form.filter.course .datatree > dl > dd > dl > dt > .button {
		padding: 0;
		height: 30px;
		width: 30px;
		pointer-events: all;
	}

	#sidebar .arrow,
	#sidebar form.filter.course .datatree .expanded .arrow {
		border-radius: var(--radius);
		background: var(--foreground-3);
		display: block;
		position: relative;
		left: 14px;
		top: 15px;
		width: 16px;
		height: 3px;
	}

	#sidebar form.filter.course .datatree dt.module {
		pointer-events: none;
		padding-bottom: 5px;
	}

	#sidebar form.filter.course .datatree > dl > dd > dl > dt > a span.label {
		pointer-events: all;
	}

	#sidebar form.filter.course .datatree > dl > dd > dl > dt > .button span {
		background: var(--foreground-3) !important;
		border-radius: var(--radius);
		margin-left: 15px;
		margin-top: 6px;
		width: 3px;
		height: 16px;
	}

	#sidebar form.filter.course .datatree > dl > dd > dl > dt.expanded > .button span,
	#sidebar .wrapper .button.toggle span {
		background-color: transparent !important;
	}

	#sidebar .planning.miniview table td.today {
		background: var(--blue);
		border-radius: var(--radius);
		border: none;
	}

	#sidebar .planning.miniview table td.selected {
		background-color: var(--orange) !important;
	}

	#sidebar .planning.miniview table tr.selected td:hover,
	#sidebar .planning.miniview table tr.selected td.weekend:hover,
	#sidebar .planning.miniview table td.weekend:hover,
	#sidebar .planning.miniview table td:hover,
	#sidebar .planning.miniview table tr.selected td.ui-selecting,
	#sidebar .planning.miniview table tr.selected td.weekend.ui-selecting,
	#sidebar .planning.miniview table td.weekend.ui-selecting,
	#sidebar .planning.miniview table td.ui-selecting,
	#sidebar .planning.miniview table tr.ui-selecting td,
	#sidebar form.filter.course .datatree .expanded,
	#sidebar form.filter.course .datatree > dl > dt:hover {
		background: var(--background-3) !important;
		border-radius: var(--radius);
		color: var(--foreground-2) !important;
	}

	#sidebar form.filter.course .datatree > dl > dt,
	#sidebar .planning.miniview {
		background: var(--background-2) !important;
		border-radius: var(--radius);
		margin-bottom: 5px;
		color: var(--foreground-2);
		border: none;
	}

	#sidebar .planning.miniview table td {
		color: var(--foreground);
	}

	#sidebar form.filter.course .datatree > dl,
	#sidebar form.filter.course .datatree > dl > dd,
	#sidebar > .wrapper,
	#sidebar > .wrapper > .button.toggle {
		border: none !important;
	}

	#sidebar .wrapper .button.toggle,
	#sidebar .wrapper .bloc.filter,
	#sidebar .wrapper .bloc.planif {
		background: var(--background-2) !important;
		margin-left: 18px;
		margin-top: 10px;
		border-radius: var(--radius);
	}

	#sidebar .wrapper .button.toggle .label {
		color: var(--main);
	}

	#sidebar .wrapper .bloc.filter,
	#sidebar .wrapper .bloc.planifd {
		background: var(--background-3) !important;
		padding: 10px;
	}

	#sidebar .wrapper li:hover {
		background-color: var(--background-2);
	}


	/* -=- notices -=- */
	.notice .data,
	.sub_list {
		border: none !important;
	}

	.identity .arrow,
	.expand,
	.item.hide a label {
		display: none !important;
	}

	.notice .data a {
		color: var(--main);
	}

	.notices .item:hover,
	#wall #content .bloc .item:hover .data .content.html {
		background-color: transparent !important;
	}

	#wall #content .item.collapsed .sub .content.html {
		display: block !important;
	}


	/* -=- footer -=- */
	#footer {
		background: none !important;
		border-top: 2px solid var(--border-color);
		color: var(--foreground);
	}

	#footer a {
		color: var(--blue);
	}


	/* -=- array -=- */
	.overflow {
		background-color: var(--background-2) !important;
		color: var(--foreground) !important;
	}

	.datagrid {
		color: var(--main) !important;
	}

	.datagrid tbody tr:nth-child(2n) {
		background-color: var(--background) !important;
		color: var(--foreground) !important;
	}

	.datagrid tbody tr:hover,
	.datagrid tbody tr.item.ui-selected,
	.datagrid tbody tr.item.ui-selecting,
	.datagrid tbody tr:hover a,
	.datagrid tbody tr.item.ui-selected a,
	.datagrid tbody tr.item.ui-selecting a {
		background-color: var(--border-color) !important;
		color: var(--foreground-2) !important;
	}

	.datagrid table thead th div div,
	.datagrid table tfoot th div,
	.datagrid table tfoot,
	.datagrid table tfoot tr {
		background: var(--background-3) !important;
	}

	.datagrid a {
		color: var(--accent) !important;
	}

	div.datagrid table thead th div div {
		background: none;
	}

	div.datagrid {
		border: var(--border);
		border-radius: var(--radius);
		overflow: hidden
	}

	.tabulation div.tab:not(#netsoul):not(#binome),
	div.datagrid {
		width: fit-content;
	}

	#content.current .datagrid .overflow table .item td.first {
		border-radius: var(--radius);
	}

	#content.current .datagrid .overflow table .item.valid td.first {
		background: var(--green);
	}

	#content.current .datagrid .overflow table .item.ongoing td.first {
		background: var(--blue);
	}

	#content.current .datagrid .overflow table .item.fail td.first {
		background: var(--orange);
	}

	#content.current .datagrid .overflow table .item.notregistered td.first {
		background: var(--border-color);
	}

	#content .datagrid .overflow {
		width: fit-content;
	}


	/* -=- idk -=- */
	h1 {
		color: var(--foreground-2);
	}

	h2 {
		color: var(--blue);
	}


	/* -=- pop-ups -=- */
	.popup.custom,
	.popup.custom .item,
	.popup .help,
	.popup.custom .item.buttons .button > span,
	.popup.choose.columns .zone.data .list.datagrid,
	.cxm ul,
	.cxm ul li.item,
	.cxm ul li.item.over {
		color: var(--foreground);
		background: var(--background-2) !important;
		border: none;
	}

	.popup.choose.columns .zone.data .list.datagrid {
		padding: 5px;
		border: var(--border);
	}

	.progress .message span {
		color: black;
	}

	#auth-login .message {
		background-color: var(--orange);
		border: none;
		border-radius: vaR(--radius);
	}

	/* -=- admin -=- */
	#admin #main #content .bloc.main > .group,
	#content .bloc.main .preference .bloc,
	#file .bloc.main ul li.ui-selected .wrapper,
	#file .bloc.main ul li:hover .wrapper {
		background: var(--background-2);
	}

	#file .bloc.main ul li.ui-selected .wrapper,
	#file .bloc.main ul li:hover .wrapper {
		border-radius: var(--radius);
	}
}

@-moz-document url-prefix("https://intra.epitech.eu/planning") {
	/* -=- planning -=- */
	#planning .planner .top,
	.planner .v-header {
		background: var(--background-2) !important;
	}

	#planning .planner {
		background: var(--background-3);
		border-radius: var(--radius);
	}

	#planning .event.info.box {
		background: var(--background-2);
		border-radius: var(--radius);
		color: var(--foreground);
	}

	#planning .planner .scroll,
	#planning .planner .v-header,
	#planning .planner .v-header div,
	#planning .planner .days .scroll .events .quarter,
	#planning .planner .days .scroll .events td,
	#planning .planner .days .scroll .events {
		border: none;
	}

	#planning .planner .days .scroll .events td {
		padding-right: 3px;
	}

	#planning .planner .v-header {
		color: var(--accent);
		padding-right: 5px;
		padding-bottom: 2px;
	}

	#planning .planner .v-header div {
		height: 79.4px;
		text-align: center;
		line-height: 80px;
	}

	#planning .calendar .appoint .actions .button,
	#planning .list .appoint .actions .button {
		width: 0px;
	}

	#planning .calendar .appoint .actions {
		background: transparent;
	}

	#planning .calendar .top .actions.export,
	#planning .calendar .top .actions .button.contextmenu,
	#planning .planner .top .events tbody tr,
	#planning .appoint .actions .button.contextmenu,
	#planning .appoint .actions .button.registered,
	#planning .event.info.box .actions .button.contextmenu {
		display: none;
	}

	#planning .planner .appoints .appoint {
		overflow: visible;
	}

	#planning .planner .grid {
		pointer-events: none;
	}

	#planning .appoint .actions .button .icon {
		border-radius: var(--radius);
		border: 1px solid black;
	}

	#planning .appoint .actions .button.unregister .icon {
		background: var(--orange);
	}

	#planning .appoint .actions .button.register .icon {
		background: var(--main);
	}

	#planning .appoint .actions .button.register.disabled .icon {
		background: var(--background-3);
	}

	#planning .appoint .actions .button.token.present.disabled .icon {
		background: var(--accent);
	}

	#planning .appoint .actions .button.token .icon {
		background: var(--yellow);
	}

	#planning .list .appoint .actions {
		margin-right: 15px;
	}

	#planning .navigation .switch .button span {
		background: none;
	}

	#planning .navigation .switch.with_planning {
		width: 520px;
	}

	#planning .navigation .switch .button {
		background: none !important;
	}

	#planning .navigation .switch .button > span {
		background: var(--background-2) !important;
		border-radius: var(--radius);
		color: var(--main);
	}

	#planning .navigation .switch .button.next span.label,
	#planning .navigation .switch .button.prev span.label {
		display: block;
		text-align: center;
		padding: 7px 5px 0;
	}

	#planning .navigation .switch .button.next,
	#planning .navigation .switch .button.prev {
		width: auto;
		margin-top: 2px;
	}

	#planning .navigation .switch .button span.icon {
		display: none;
	}

	#planning .navigation .switch .button.ui-active > span {
		border: var(--border);
	}

	#planning #content #planning_list .events .row.even {
		background-color: var(--background-2);
	}

	#planning #content #planning_list .events .row:hover {
		background-color: var(--background-3);
	}
}

@-moz-document url-prefix("https://intra.epitech.eu/user") {
	/* -=- profile -=- */
	#profil .tabulation ul.tabs,
	#profil .tabulation ul.tabs li,
	#profil .tabulation ul.tabs li a,
	#profil .tabulation ul.tabs li a span {
		background: none;
		text-shadow: none;
		color: var(--main);
		margin: 0;
		padding: 0;
	}

	#profil .tabulation ul.tabs li.ui-active a span {
		color: var(--blue);
	}

	#profil .tabulation ul.tabs li.ui-active {
		border: var(--border);
	}

	#profil .tabulation ul.tabs {
		display: flex;
		flex-direction: row-reverse;
		align-items: center;
		justify-content: center;
	}

	#profil .tabulation ul.tabs li {
		border-radius: var(--radius);
		background-color: var(--background-2);
		margin-right: 5px;
		padding: 8px;
	}

	#graph_container {
		border-radius: var(--radius) !important;
	}

	#profil #highcharts-0 rect {
		fill: transparent;
	}

	#profil #highcharts-0 text {
		fill: var(--foreground-2) !important;
	}

	#profil .bloc.main .data .picture img {
		clip-path: circle(70px at 75px 95px);
	}

	#profil #binome image.circle {
		scale: 2;
		clip-path: circle(21px at 39px 30px);
	}

	#profil #highcharts-3 tspan {
		fill: var(--foreground);
	}

	#profil #highcharts-3 svg > rect:not(.highcharts-series) {
		fill: var(--background-2);
	}

	#profil .highcharts-tooltip rect:nth-child(4) {
		fill: var(--background-2)
	}

	#highcharts-3 stop {
		stop-color: var(--background-2);
	}

	#highcharts-12 stop,
	#highcharts-17 stop,
	#highcharts-19 stop,
	#highcharts-21 stop,
	#highcharts-24 stop,
	#highcharts-25 stop,
	#highcharts-27 stop,
	#highcharts-28 stop,
	#highcharts-29 stop,
	#highcharts-31 stop,
	#highcharts-32 stop,
	#highcharts-33 stop {
		stop-color: var(--background-3);
	}


	#profil g.highcharts-series rect {
		fill: var(--main) !important;
		clip-path: inset(0% 10% 0% 10% round 10px);
	}

	#user > div:not(#footer) {
		border-radius: var(--radius);
	}

	#profil #flags .item {
		background-color: var(--background-2);
		box-shadow: none;
		border-radius: var(--radius);
	}

	#profil #flags .item > div {
		border: none !important;
	}
}

@-moz-document url-prefix("https://intra.epitech.eu/module") {
	/* -=- mod-ules -=- */
	#module .tab > ul li.activite .slide .access > .item .links {
		background-color: var(--background-2);
		border-radius: var(--radius);
	}

	#module .tab > ul li.activite .data .item {
		border: none;
		box-shadow: none !important;
	}

	#module .tab > ul li.activite > .title {
		background: var(--background-2);
		border-radius: var(--radius);
		border: none;
		margin-top: 5px;
	}

	#module .tab > ul li.activite > .title:hover,
	#module .tab > ul li.activite.active > .title {
		background-color: var(--background-3);
	}

	#module .tab > ul li.activite.is-rdv .item .info.appointment,
	#module .tab > ul li.activite.is-proj .item .info.project,
	#module .tab > ul li.activite.is-note .item .info.notation {
		filter: invert(1);
	}

	#module .tabs,
	#module .tabs li a,
	#module .tabs li a span {
		background: none;
		text-shadow: none;
		display: flex;
		flex-direction: row-reverse;
		justify-content: left;
		color: var(--foreground);
	}

	#module .tabs .ui-active a span,
	#module .tabs li a span:hover,
	#module .tab > ul li.activite .eventzone .event:hover {
		background: var(--background-3);
		color: var(--foreground-2);
	}

	#module .tab > ul li.activite .item.title .categ {
		display: block;
	}

