style.css:
@-moz-document domain("intra.epitech.eu") {
/* -=- main -=- */
:root {
    --background: #181818;
    --background-2: #202020;
    --background-3: #303030;
    --foreground: #ccc;
    --foreground-2: white;
    --foreground-3: #bbb;
    --foreground-black: black;
    --main: #2d90ff;
    --accent: #00ecff;
    --yellow: #efaf16;
    --orange: #b55815;
    --blue: #159cff;
    --green: #1e9563;
    --border-color: #38444d;
    --border: 2px solid var(--border-color);
    --radius: 5px;
}



/* -=- Front page -=- */
#homeboard,
body {
    background-color: var(--background);
}

a {
    color: var(--main);
}

h1,
h2 {
    color: var(--foreground-2);
}

/* -=- gdpr-banner -=- */
#gdpr-banner {
    background-color: var(--background-3);
}

/* -=- Header -=- */
.intra {
    background-color: var(--background) !important;
    border-bottom: solid 2px var(--border-color);
}

#header #identification .infos,
#header #notification,
#header .intra .navigation tr td .button .label,
#header #notification .dropdown .layer,
#header #identification.expanded .menu {
    color: var(--foreground-2);
    background-color: var(--background-2);
}

#header #identification .infos:hover,
#header #identification.expanded a.infos,
#header #notification .dropdown:hover a,
#header #notification .dropdown.ui-expanded a,
#header .intra .navigation tr td.ui-active .label,
#header .intra .navigation tr td .button:hover .label,
#header #notification .dropdown .layer .item:hover {
    color: var(--foreground-2);
    background-color: var(--background-3);
}

#header .intra .navigation tr td .button .label {
    border-right: none;
}

#header .intra .navigation {
    height: 60px;
    border-left: var(--border);
}

#header #notification .dropdown .layer {
    border: var(--border);
    border-radius: var(--radius);
    right: 10%;
}

#header #identification.expanded a {
    text-transform: none;
}


/* -=- Breadcrum -=- */
#breadcrumb {
    color: var(--foreground-3);
    background-color: var(--background-2);
    border-radius: var(--radius);
    width: fit-content;
}

#breadcrumb ul li,
#breadcrumb ul li:first-child {
    background: none;
}

#wall #breadcrumb {
    padding: 0;
    margin-left: 20px;
}

/* -=- Section -=- */
#homeboard {
    border-bottom: none;
}

#homeboard section {
    color: var(--foreground-3);
    background-color: var(--background-2);
    border: var(--border);
}

#homeboard section > h2 > span.icon {
    filter: invert(1);
}

#homeboard section div.view_more {
    background-color: var(--background-2);
    border-radius: 0 0 var(--radius) var(--radius);
}

#homeboard section div.view_more:hover,
#homeboard section article {
    background-color: var(--background-3);
}

#homeboard section article,
#homeboard section article:hover,
#homeboard section div.view_more, 
#wall #content .item .first .data,
#wall #content .item:hover .first .data,
#wall #content .item .sub_list,
#wall #content .item .sub_list:hover {
    border: none;
}

#wall #content .item:hover .sub_list {
    border-left: none;
    border-right: none;
    border-bottom: none;
}

#homeboard section article a.link_delete {
    display: block;
    background: none;
    color: red;
}

#wall #content .bloc .item:hover,
#wall #content .bloc .item:hover .data .content.html {
    background-color: var(--background-3);
    border-radius: var(--radius);
}

.arrow.picture {
    display: none;
}

.cxm ul,
.cxm ul li.item,
.cxm ul li.item:hover {
    background-color: var(--background-3);
    border: none;
}

/* -=- Left Panel -=- */
#sidebar {
    background-color: var(--background);
}

#sidebar #toggle-sidebar,
#sidebar .trombi {
    display: none !important;
}

.rzone hr {
    display: none;
}



.main-wrapper {
    color: var(--foreground-2);
}


/* -=- footer -=- */
#footer {
    background: none;
    border-top: 2px solid var(--border-color);
    color: var(--foreground);
}

#footer a {
    color: var(--blue);
}


/* -=- datagrids -=- */
div.datagrid {
    border: none;
    border-radius: var(--radius);
    background: var(--background-2);
}

div.datagrid,
div.datagrid tbody tr.separator > td,
div.datagrid table thead th div div {
    border: none;
}

.datagrid table th > div,
div.datagrid .overflow,
div.datagrid table thead th div div,
div.datagrid div:last-child table tfoot tr,
div.datagrid table tfoot,
div.datagrid tbody tr:nth-child(even) {
    background: none;
}

div.datagrid tbody tr.item.ui-selected,
div.datagrid tbody tr.item.ui-selecting,
div.datagrid tbody tr.item.ui-selected:hover,
div.datagrid tbody tr.item:hover {
    background-color: var(--background-3);
}

div.datagrid {
    width: fit-content;
    color: var(--foreground)
}

div.datagrid tbody tr.item:hover {
    color: var(--foreground-2);
}

.datagrid a,
div.datagrid tbody tr.item.ui-selected a,
div.datagrid tbody tr.item.ui-selecting a,
div.datagrid tbody tr.item:hover a,
div.datagrid tbody tr.item.ui-selected:hover a,
div.datagrid tbody tr.item.ui-selecting:hover a {
    color: var(--main);
}

div.datagrid table thead th > div {
    border-left: var(--border);
}


/* -=- tabs -=- */
.tabulation ul.tabs li {
    border: none;
    border-radius: var(--radius);
    background: var(--background-2);
}


.tabulation ul.tabs {
    border: none;
    margin-bottom: 5px;
}


ul.tabs li.ui-active,
ul.tabs li:hover {
    background: var(--background-3) !important;
}
}

@-moz-document url-prefix("https://intra.epitech.eu/module") {
/* -=- module -=- */
#module .bloc.main {
    display: block !important;
}

#module .button.hide,
#module .button.show {
    display: none;
}

#module .tab > ul li.activite .slide .access > .item .links {
    overflow: visible !important;
}

.bulle {
    opacity: 1 !important;
}

#module .bloc.main .data .item.desc.credits label,
#module .bloc.main .data .item.desc.semestre label {
    color: var(--foreground);
}

#module .tab > ul li.activite > .title,
#planify .bloc.tabulation #list h3 {
    border: none;
    border-radius: var(--radius);
    background: var(--background-2);
}

#module .tab > ul li.activite.active > .title,
#module .tab > ul li.activite:hover > .title {
    background: var(--background-3) !important;
}

#module .tab > ul li.activite,
#planify .bloc.tabulation #list h3 {
    margin-bottom: 5px;
}

#sidebar form.filter.course .datatree > dl {
    border: none;
    margin-bottom: 5px;
}

#module .tab > ul li.activite .data .item,
#module .tab > ul li.activite .eventzone .event .data,
#module .tab > ul,
#module .tab > ul li.activite .eventzone .event .item:first-child,
#module .tab > ul li.activite .eventzone .event .item:first-child,
#content .rzone,
#sidebar form.filter.course .datatree > dl > dd,
#sidebar form.filter.course .button.expand-filters {
    border: none;
}

#module .tab > ul li.activite.is-rdv .item .info.appointment,
#module .tab > ul li.activite.is-proj .item .info.project,
#module .tab > ul li.activite.is-note .item .info.notation,
#project .bloc.main .data .message a .icon,
#module .bloc.main .item.resp .nopicture {
    filter: invert(1);
}

#module .tab > ul li.activite .slide .access > .item,
#sidebar form.filter.course .datatree dt a .actions.ui-contextmenu-active,
#sidebar form.filter.course .datatree dt:hover a .actions,
#sidebar form.filter.course .datatree dt.module.current.expanded .label .modif.label {
    display: none;
}

#module .tab > ul li.activite.is-proj .slide .access > .item.project,
#module .tab > ul li.activite.is-rdv .slide .access > .item.rdv,
#module .tab > ul li.activite.is-note .slide .access > .item.note {
    display: block;
}

#module .tab > ul li.activite.is-proj .slide .access > .item.project h3,
#module .tab > ul li.activite.is-rdv .slide .access > .item.rdv h3,
#module .tab > ul li.activite.is-note .slide .access > .item.note h3,
#module .tab > ul li.activite .slide .access > .item .links,
#planify .bloc .arrow {
    background: none;
}

#module .tab > ul li.activite .slide .access .item,
#sidebar form.filter.course .datatree > dl > dt,
#sidebar form.filter.course .button.expand-filters,
#sidebar form.filter.course .datatree dt.module {
    background-color: var(--background-2);
    border-radius: var(--radius);
}

#module .tab > ul li.activite .slide .access .item:hover,
#module .tab > ul li.activite .eventzone .event:hover,
#sidebar form.filter.course .button.expand-filters:hover,
#sidebar form.filter.course .datatree dt.module.expanded {
    background-color: var(--background-3);
}

#module .tab > ul li.activite .slide .access > .item .links {
    position: static;
    box-shadow: none;
    height: auto !important;
}

#sidebar form.filter.course .datatree .notregistered .icon.registration,
#sidebar form.filter.course .filters .role-unregistered.ui-active .icon {
    background: var(--border-color);
    border-radius: var(--radius);
}

#sidebar form.filter.course .datatree .ongoing .icon.registration,
#sidebar form.filter.course .datatree .register .icon.registration,
#sidebar form.filter.course .filters .role-registered.ui-active .icon {
    background: var(--main);
    border-radius: var(--radius);
}

#sidebar form.filter.course .datatree .fail .icon.registration {
    background: var(--orange);
    border-radius: var(--radius);
}

#sidebar form.filter.course .datatree .valid .icon.registration {
    background: var(--green);
    border-radius: var(--radius);
}

#sidebar form.filter.course .datatree dt.module > a .icon:first-child {
    margin: 3px 4px 0 4px;
}

#sidebar form.filter.course .datatree .module {
    margin-bottom: 5px;
    margin-left: 10px;
    padding-bottom: 5px;
}

input:disabled {
    color: var(--foreground);
}

#sidebar form.filter.course .datatree > dl > dd > dl > dt > .button {
    padding: 0;
    height: 30px;
    width: 30px;
    pointer-events: all;
}

#sidebar .arrow,
#sidebar form.filter.course .datatree .expanded .arrow {
    border-radius: var(--radius);
    background: var(--foreground-3);
    display: block;
    position: relative;
    left: 14px;
    top: 15px;
    width: 16px;
    height: 3px;
}

#sidebar form.filter.course .datatree > dl > dd > dl > dt > .button span {
    background: var(--foreground-3);
    border-radius: var(--radius);
    margin-left: 15px;
    margin-top: 6px;
    width: 3px;
    height: 16px;
}

#sidebar form.filter.course .datatree > dl > dd > dl > dt.expanded > .button span {
    background: transparent;
}

#project .bloc.notallowed {
    border: none;
    background-color: var(--orange);
    border-radius: var(--radius);
}
#planify .bloc.notregistered {
    border: none;
    background-color: var(--yellow);
    border-radius: var(--radius);
}
}

@-moz-document url-prefix("https://intra.epitech.eu/planning") {
/* -=- planning -=- */
#sidebar > .wrapper > .button.toggle,
#planning #content > .bloc.main {
    border: none;
    border-radius: var(--radius);
    background: var(--background-2);
}

#planning #content #planning_list .events .row.even,
.planner .top,
.planner.weeksview > .weeks .events th,
#sidebar > .wrapper > .button.toggle > span,
#planning .calendar .days .scroll .events .noevent .layer {
    background: none;
}

#sidebar > .wrapper > .button.toggle:hover,
#sidebar > .wrapper > .button.toggle.open,
#planning #content #planning_list .events .row:hover {
    background: var(--background-3);
}

#sidebar > .wrapper,
.planner .scroll,
.planner .v-header,
.planner .v-header div,
.planner.daysview > .days .scroll .events,
.planner.daysview > .days .scroll .events .quarter,
.planner.daysview > .days .scroll .events td,
.planner.daysview > .days .scroll .events .half,
.planner.daysview > .days .scroll .events .hour,
.planner.weeksview > .weeks,
.planner.weeksview > .weeks .events td,
.planner.weeksview > .weeks .events .week .appoints th,
.planner.weeksview > .weeks .events .week .appoints td {
    border: none;
}

#sidebar > .wrapper > .button.toggle {
    margin-bottom: 5px;
}

#planning #content #planning_list .day,
#planning #content #planning_list .empty {
    border-bottom: var(--border);
}

#planning .calendar .days .top .actions,
.planner .v-header,
.planner .weeks .top {
    display: none;
}

.planner .v-header {
    color: var(--accent);
}

#planning .planner .v-header div {
    height: 80px;
    text-align: center;
    line-height: 80px;
}

.planner .top div.events {
    margin: 0;
}
}

@-moz-document url-prefix("https://intra.epitech.eu/user") {
/* -=- user -=- */
#profil #highcharts-0 rect {
    fill: var(--background-3);
}

#profil #highcharts-0 text {
    fill: var(--foreground-2) !important;
}

#profil .bloc.main .data > .infos {
    background: var(--background-2);
    color: var(--foreground-2);
    border: none;
    width: fit-content;
    min-width: 30%;
}

#profil .bloc.main .data .infos > .item,
#profil .bloc.main .data .infos .editable.infos > .list .info.item,
#profil #flags .item .success,
#profil #flags .item .header {
    border: none;
}

#profil #notes #user-note {
    width: auto
}

#profil #flags .item {
    background-color: var(--background-2);
    box-shadow: none;
    border-radius: var(--radius);
}

#profil .bloc.main .data .picture img {
    border-radius: var(--radius);
}
}

@-moz-document url-prefix("https://intra.epitech.eu/e-learning") {
/* -=- elearning -=- */
#elearning #content .stepview .toolbar,
#elearning.ui-fullscreen #content .bloc.main .rzone,
#elearning #content .bloc .data .mainview ul li .wrapper.expand ul,
#elearning #content .homeview ul li .maxi-wrapper .wrapper.expand .separator,
#elearning #content .bloc .data .mainview ul li .title,
#elearning #content .homeview ul li .wrapper ul,
#elearning #content .homeview ul li .color-wrapper,
#elearning #content .homeview ul li.is-class .onglet .filter li.cours,
#elearning #content .homeview ul li.has-class .onglet .filter li.cours,
#elearning #content .homeview ul li.is-tp .onglet .filter li.tp,
#elearning #content .homeview ul li.has-tp .onglet .filter li.tp,
#elearning #content .homeview ul li.type-pdf .band,
#elearning #content .homeview ul li.type-video .band,
#elearning #content .homeview ul li.type-iframe .band,
#elearning #content .homeview ul li.type-pwpoint .band,
#elearning #content .homeview ul li.type-doc .band {
    background: none;
}

#elearning #content .homeview ul li.is-class .onglet .filter li.cours,
#elearning #content .homeview ul li.has-class .onglet .filter li.cours,
#elearning #content .bloc .data .mainview ul li .wrapper.expand ul,
#elearning #content .homeview ul li.is-tp .onglet .filter li.tp,
#elearning #content .homeview ul li.has-tp .onglet .filter li.tp {
    border: none;
}

#elearning #content .bloc .data .mainview ul li a.arrow,
#elearning #content .homeview ul li .maxi-wrapper .separator,
#elearning #content .homeview ul li .onglet .filter .arrow {
    display: none;
}

#elearning #content .bloc .data .mainview ul li .maxi-wrapper {
    height: auto;
    background: var(--background-2);
    border-radius: var(--radius);
}

#elearning #content .homeview ul li.has-tp .onglet .filter li.cours.active,
#elearning #content .homeview ul li.has-class .onglet .filter li.tp.active {
    background: var(--background-3);
    border-radius: var(--radius);
}

#elearning #content .bloc .data .mainview ul li .wrapper ul {
    height: inherit;
    overflow: inherit;
}

#elearning #content .homeview ul li ul li a {
    color: var(--main);
}

#elearning #content .homeview ul li a {
    color: var(--accent);
}

#elearning #content .homeview ul li.is-class .onglet .filter li.cours a,
#elearning #content .homeview ul li.is-tp .onglet .filter li.tp a,
#elearning #content .homeview ul li.has-class .onglet .filter li.cours a,
#elearning #content .homeview ul li.has-tp .onglet .filter li.tp a {
    color: var(--foreground-2);
}
}

@-moz-document url-prefix("https://intra.epitech.eu/admin") {
/* -=- admin -=- */
#admin #main #content .bloc.main > .group,
#content .bloc.main .preference .bloc {
    background: var(--background-3);
    border-radius: var(--radius);
    width: fit-content;
    border: none;
}

#content .bloc.main .preference .bloc > h2 {
    background: none;
    border: none;
}
}

@-moz-document url-prefix("https://intra.epitech.eu/file") {
/* -=- file -=- */
#file .bloc.main ul li.ui-selected .wrapper,
#file .bloc.main ul li:hover .wrapper {
    background: var(--background-2);
    border-radius: var(--radius);
}
}

@-moz-document url-prefix("https://web.microsoftstream.com/embed/video") {
/* -=- embed stream -=- */
body {
    background: var(--background-2);
}


.stream-loading-container {
    background: none;
}
}
