style.css:
@-moz-document regexp('https://cp134.webserver.pt:2083/.*')
/* i really want this to be global */
{

  #maintbl > tbody > tr.header > td > div > div.col-xs-12.col-md-8
  {
    display: grid;
  }

  #btnReOpen
  {
    height: 90%;
  }

    @media (min-width: 960px) {
	#maintbl > tbody > tr.header > td > div > div.col-xs-12.col-md-8 {
		gap: 0.5em;
		grid-template-columns: 4fr 1.5fr 0.4fr;
		padding-right: 6px;
	}

		#path { width: calc(100% - 4em); }
		
                #path div { font-size: 11px!important; }

		#charset { width: calc(100% - 6em); }

		#btnReOpen {
	            font-size: 12px;
	            padding: 6px;
	        }

	.col-md-4
	{
            width: 22.5%;
            float: unset;
            float: right;
	}

	.col-md-8
	{
		width: 77.5%;
	}

	col-xs-12 col-md-4 action-buttons,
		#maintbl > tbody > tr.header > td > div.row,
		#maintbl > tbody > tr.header > td > div.row label,
		#maintbl > tbody > tr.header > td > div.row button
	{
	font-size: 12px!important;
	}

	#switch_editor_control,
		#sform_submit,
		#btnClose
	{
		padding: 6px 6px;
	}
}

	@media (min-width: 1600px) {
		col-xs-12 col-md-4 action-buttons,
		#maintbl > tbody > tr.header > td > div.row,
		#maintbl > tbody > tr.header > td > div.row label,
		#maintbl > tbody > tr.header > td > div.row button {
			font-size: 14px!important;
		}

                #charset { width: calc(100% - 8em); }
                #btnReOpen { height: 90%; }

		#switch_editor_control,
		#sform_submit,
		#btnClose
		{
			padding: 6px 12px;
		}

		#maintbl > tbody > tr.header > td > div > div.col-xs-12.col-md-8
		{
			display: grid;
			gap: 1em;
			grid-template-columns: 4fr 1.5fr 0.5fr;
		}

		#path { width: calc(100% - 5em); }
		
                #path div.form-control { font-size: 13px!important; }

		#charset { width: calc(100% - 8em); }

		.col-md-4
		{
			width: 22.5%;
			float: unset;
			float: right;
		}

		.col-md-8
		{
			width: 77.5%;
		}
	}
}
.ace-twilight .ace_marker-layer .ace_selection {
    background: #ddf0ff33
}

.ace-twilight .ace_marker-layer .ace_bracket {
    background: #090;
}
