style.css:
@-moz-document domain(extreme-down.io) {

/* Body */
body {
    background: #1f1d2b;
    color: #ccc
}


/* Topbar */
.topbar {
    background: #14131c url(https://www.extreme-down.io/templates/ExtDown6_old/images/title_bg.png) 0 0 repeat-x;
}

.top_nav a:hover {
    background-color: #5865f24d;
}

/* RSS and Twitter icons in topbar */
.social_icons {
    float: right;
    margin: 9px 10px 0 0;
}

.social_icons img[alt=rss] {
    background: url(https://dearvoodoo.com/library/fontawesome/svgs/light/rss.svg) no-repeat;
    filter: invert(0.7);
    box-sizing: border-box;
    width: 20px;
    height: 20px;
    padding-left: 20px;
}

.social_icons img[alt=twitter] {
    background: url(https://dearvoodoo.com/library/fontawesome/svgs/brands/twitter.svg) no-repeat;
    filter: invert(0.7);
    box-sizing: border-box;
    width: 20px;
    height: 20px;
    padding-left: 20px;
}

/* Searchbar in topbar */
.search_box {
    float: right;
    background: rgba(17,17,17,.35);
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    border-top: none;
    border-left: none;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
    margin: 5px 2px 0 0;
    padding: 2px 3px 3px 2px;
}

/* Topbar search icon */
.search_box input[type=submit] {
    width: 20px;
    height: 17px;
    background: transparent url(https://dearvoodoo.com/library/fontawesome/svgs/light/magnifying-glass.svg) no-repeat!important;
    filter: invert(0.7);
    border-top: none!important;
    border-left: none!important;
    border-bottom: none!important;
    border-right: none!important;
    box-shadow: none!important;
    vertical-align: -webkit-baseline-middle;
}

/* Topbar Cog icon */
.search_box a.advsrc {
    width: 20px;
    height: 17px;
    background: transparent url(https://dearvoodoo.com/library/fontawesome/svgs/light/gear.svg) no-repeat!important;
    filter: invert(0.7);
    border-top: none!important;
    border-left: none!important;
    border-bottom: none!important;
    border-right: none!important;
    box-shadow: none!important;
    vertical-align: -webkit-baseline-middle;
}

/* Searchbar itself */
.search_box input[type=text] {
    background: #14131c!important;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    border-left: 1px solid rgba(255, 255, 255, 0.1);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    border-right: 1px solid rgba(255, 255, 255, 0.1);
}

/* Site header */
#header {
    background: #161426;
    border-bottom: 1px solid #37363e;
}

#header h1 a {
    filter: invert(1);
}

a.avabutton {
    border-color: none;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
    padding: none;
    display: block;
    width: 38px;
    height: 38px;
    float: left;
    margin-right: 10px;
}

/* Profil with all icons */
.logedblock {
    float: right;
    background-color: #252936;
    border-radius: 50px;
    padding: 6px 6px 6px 6px;
    margin: 7px 0 0;
    position: relative;
    display: block;
}

.logedblock > a > img {
    border-radius: 50%;
}

.loginblock {
    float: right;
    background: #252936;
    margin: 5px 0 0;
    border-radius: 50px;
    padding: 6px;
    position: relative;
    display: block;
    overflow: hidden;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
}

input#login_name, input#login_password {
    padding: 4px;
    width: 120px;
    display: inline-block;
    background: #14131c!important;
    border-color: rgba(255, 255, 255, 0.1);
}

input#login_name:-webkit-autofill,
input#login_name:-webkit-autofill:hover, 
input#login_name:-webkit-autofill:focus, 
input#login_name:-webkit-autofill:active,
input#login_password:-webkit-autofill,
input#login_password:-webkit-autofill:hover, 
input#login_password:-webkit-autofill:focus, 
input#login_password:-webkit-autofill:active{
    -webkit-box-shadow: 0 0 0 30px #14131c inset !important;
    -webkit-text-fill-color: #ccc !important;
    border-color: #5865f2 !important;
}


input#login_not_save {
    filter: invert(100%) brightness(0.7);
}

img[alt="Forgot password?"] {
    background: url(https://dearvoodoo.com/library/fontawesome/svgs/light/key.svg) no-repeat;
    filter: invert(0.7);
    box-sizing: border-box;
    width: 15px;
    height: 15px;
    margin: 0 5px 5px 5px;
    padding-left: 15px;
}

img[alt="Register!"] {
