/*scrollbar*/
#contentWrapper.iron-dropdown > * {
    overflow: hidden;
}

#contentWrapper.iron-dropdown > *:hover {
    overflow: auto;
}

::-webkit-scrollbar {
    width: 10px!important;
}

/* Track */
::-webkit-scrollbar-track {
    border-radius: 8px;
    background-color: #59A8CC47!important;
}

/* Handle */
::-webkit-scrollbar-thumb {
    background: #11F19FC2;
    border-radius: 10px;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
    background-image: linear-gradient(200deg, #DA1756 0%, #1F70B2 100%)!important;
}


