style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("jappy.com")
{

/* i really want this to be global */

body{
 background: #444;
 color: #49ccff;
  }
 
html {
   background: #444 !important;
} 


/**********************/
.site .siteRight {
 background: #444;
}

/**********************/
.site .siteLeft {  
    display: none;
}

/**********************/
 .site .siteRight {
      padding-left: 500px;
}

/**********************/
.breakGame #breakGameFrame {
    max-width: 843px;
}

/**********************/
.breakGame #breakGameFrame .ingame .gameHeader {
    width: 70%;
    margin: 0px 0px -50px 0px;
}

/**********************/
.breakGame #breakGameFrame .ingame .gameHeader .gameMetricContainer {
    line-height: 1.2;
}

/**********************/
#breakGameFrame > div {
    background: #ffedb000 !important;
}

/**********************/
.breakGame .bubble {
    padding: 0px 9px 0px 9px;
    margin: 0px 9px 32px 9px;
    border: 1px solid #dfdfdf;
    background: rgba(30, 30, 30, 1.0);
    font-weight: bold;
    color: #49ccff;

}

/**********************/
.breakGame .dialog .headline {
    font-size: 180%;
    text-align: center;
    margin-bottom: -0.75rem;
    padding: 6px 0px 0px 0px;
}

/**********************/
.breakGame #breakGameFrame .overlay.loading {
    border-radius: 13px;
    border: 1px solid #dfdfdf;
    background: rgb(30, 30, 30, 1.0);
    transition: 0s;
    transition-delay: 0s;
    animation: 0.0s;
    text-align: center;
    color: #49ccff;
    backdrop-filter: blur(0px) !important;
    width: 70%;
    height: 22%;
    align-self: start;
    justify-self: center;
    grid-template-rows: min-content min-content auto;
}

/**********************/
.breakGame #breakGameFrame .overlay.loading .loadingProgress .barContainer {
    position: relative;
    background: rgba(240, 240, 240, 0.0);
    height: 3rem;
    width: 100%;
    display: inline-block;
    border-radius: 7px;
    overflow: hidden;
    border: 3px solid #49ccff;
}

/**********************/
.breakGame #breakGameFrame .overlay {
    position: absolute;
    top: 125px;
    left: 117px;
    right: 0px; 
}

/**********************/
.breakGame #breakGameFrame .overlay.loading .loadingProgress .bar {
    position: absolute;
    top: 0px;
    left: 0px;
    background: #49ccff;
    display: inline-block;
    height: 100%;
}

/**********************/
.breakGame #breakGameFrame .overlay.loading .loadingProgress {
    margin: -50px 33px 0px 40px;
}

/**********************/
.breakGame #breakGameFrame .overlay.loading .loadingProgress .text {
    position: absolute;
    top: 0rem;
    left: 0px;
    right: 0px;
    text-align: center;
    color: #1e1e1e;
    font-size: 2rem;
    padding: 0;
    text-shadow: none;
    font-weight: bold;
}

/**********************/
.breakGame #breakGameFrame .overlay.loading .text {
    font-size: 2rem;
    padding: 3rem;
    line-height: 1.5;
    text-align: center;
    color: #49ccff;
    text-shadow: 1px 1px 0px #333;
    font-weight: bold;
}

/**********************/
#loading > div > br {
display: contents;
}

/**********************/
#breakConfirm > div > div > div.message {
display: none;
}

/**********************/
#breakConfirm > div > div > div.bubble
{
display: none !important;
}

/**********************/
.content.menu > div:nth-of-type(2)
{
display: none !important;
}

/**********************/
.breakGame #breakGameFrame .menu .navigation {
    display: flex;
    flex-direction: column;
    align-content: center;
    align-items: stretch;
    padding: 0px 20px;
    gap: 0px;
    width: 600px;
    justify-content: flex-start;
    max-width: 100%;
}

/**********************/
.breakGame table {
    line-height: 100%;
}

/**********************/
.breakGame #breakGameFrame .ingame .gameHeader .gameMetricContainer .addon {
    opacity: 1.0 !important;
    display: block !important;
    color: #49ccff;
    position: absolute;
    top: 716px;
    left: -62px;
    right: 170px;
    border: 1px solid #dfdfdf;
    background: rgb(30, 30, 30, 1.0);
    padding: 5px 0px 5px 0px;  
    height: 50px;
    border-radius: 13px;
    text-shadow: 1px 1px #000;
    font-weight: bold;
    font-size: 170%;
    z-index: 2000;
    text-align: center;
}

/**********************/
.breakGame table th .breakGame table td {
    padding: 3px 18px;
}

/**********************/
.taR {
    text-align: center;
}

/**********************/
.breakGame #breakGameFrame .achievementGrid .achievementEntry .label {
    max-width: 240%;
    font-size: 100%;
    white-space: normal;
    overflow: visible;
}

/**********************/
.breakGame #breakGameFrame .achievementGrid .achievementEntry .icon {
    width: 120%;
    max-height: 80px;
    max-width: 80px;
    aspect-ratio: auto;
}

/**********************/
.breakGame #breakGameFrame .achievementGrid {
    grid-gap: 11px;
}

/**********************/
.breakGame #breakGameFrame .achievementGrid .achievementEntry {
    width: 90px;
    height: 90px;
    gap: 0px;
    padding: 0px 0px 2px 0px;
}

/**********************/
.breakGame #breakGameFrame .ingame .ingameMenu {
    display: flex;
    flex-direction: column;
    gap: 0px;
    font-size: unset;
    justify-content: center;
    align-items: center;
    padding: 0px 9px 9px 9px;
    margin: 5px 0px 0px 0px;
}
   }
