style.css:
@-moz-document url-prefix("https://www.jappy.com/game/jbreak") {
body
{
	background: #444;
	color: #49ccff;
}

html
{
	background: #444 !important;
}

/**********************/
.site .siteRight
{
	background: #444;
	margin-top: 5px;
	margin-left: 555px;
}

/**********************/
.site .siteLeft
{
	display: none;
}

/**********************/
.breakGame #breakGameFrame .ingame .gameHeader
{
	width: 70%;
	margin-top: 0px;
	padding-top: 0px;
	padding-left: 0px;
	margin-bottom: -65px;
}

/**********************/
.breakGame #breakGameFrame .ingame .gameHeader .gameMetricContainer
{
	line-height: 1.2;
}

/**********************/
#breakGameFrame > div
{
	background: #ffedb000 !important;
}

/**********************/
.breakGame .bubble
{
	margin-left: -177px;
	padding-left: 9px;
	margin-right: 0px;
	margin-bottom: 106px;
	background: rgba(30, 30, 30, 1.0) !important;
	border: 3px solid #dfdfdf;
	font-weight: bold;
	color: #49ccff;
	box-shadow: 0px 0px 8px 1px #49ccff;
}

.breakGame #breakGameFrame .gameSummary
{
	gap: 0px;
	padding: 10px;
	border: 2px solid #dfdfdf;
	border-radius: 12px;
	background-color: #444444;
	box-shadow: 0px 0px 8px 1px #49ccff;
}

/**********************/
.breakGame .dialog
{
	margin-left: -21px !important;
}

.breakGame .dialog .headline
{
	font-size: 180%;
	text-align: center;
	margin-bottom: -7px;
	margin-top: 0px;
	padding: 5px 0px 0px 0px;
}

/**********************/
.breakGame #breakGameFrame .overlay.loading
{
	border-radius: 13px;
	border: 2px solid #dfdfdf;
	background: rgba(30, 30, 30, 1.0) !important;
	transition: 0.1s;
	transition-delay: 0.1s;
	animation: 0.1s;
	text-align: center;
	color: #49ccff;
	backdrop-filter: blur(0px) !important;
	width: 79%;
	height: 20%;
	align-self: start;
	justify-self: center;
	grid-template-rows: min-content min-content auto;
}

/**********************/
.breakGame #breakGameFrame .overlay.loading .loadingProgress .barContainer
{
	position: relative;
	background: rgba(240, 240, 240, 0.0);
	height: 3rem;
	width: 100%;
	display: inline-block;
	border-radius: 7px;
	overflow: hidden;
	border: 3px solid #49ccff;
}

/**********************/
.breakGame #breakGameFrame .overlay
{
	position: absolute;
	top: 125px;
	left: 71px;
	right: 0px;
}

/**********************/
.breakGame #breakGameFrame .overlay.loading .loadingProgress .bar
{
	position: absolute;
	top: 0px;
	left: 0px;
	background: #49ccff;
	display: inline-block;
	height: 100%;
}

/**********************/
.breakGame #breakGameFrame .overlay.loading .loadingProgress
{
	margin: -50px 33px 0px 40px;
}

/**********************/
.breakGame #breakGameFrame .overlay.loading .loadingProgress .text
{
	position: absolute;
	top: 0rem;
	left: 0px;
	right: 0px;
	text-align: center;
	color: #1e1e1e;
	font-size: 2rem;
	padding: 0;
	text-shadow: none;
	font-weight: bold;
}

/**********************/
.breakGame #breakGameFrame .overlay.loading .text
{
	font-size: 2rem;
	padding: 3rem;
	line-height: 1.5;
	text-align: center;
	color: #49ccff;
	text-shadow: 1px 1px 0px #333;
	font-weight: bold;
}

/**********************/
#loading > div > br
{
	display: contents;
}

/**********************/
#breakConfirm > div > div > div.message
{
	display: none;
}

/**********************/
#breakConfirm > div > div > div.bubble
{
	display: none !important;
}

.breakGame #breakGameFrame .menu .navigation
{
	margin-left: 0px;
	display: flex;
	flex-direction: column;
	align-content: center;
	align-items: stretch;
	padding: 0px 20px;
	gap: 0px;
	width: 600px;
	justify-content: flex-start;
	max-width: 100%;
}

.breakGame #breakGameFrame .tableOverflow
{
	overflow-x: auto;
	max-width: 90%;
	border: 2px solid #dfdfdf;
	border-radius: 12px;
	background-color: #444444;
	margin-left: 5%;
	margin-bottom: 15px;
	box-shadow: 0px 0px 8px 1px #49ccff;
}

/**********************/
.breakGame table
{
	line-height: 100%;
}

/*********Combo/Punkte/Box*************/
.breakGame #breakGameFrame .ingame .gameHeader .gameMetricContainer .addon
{
	opacity: 1.0 !important;
	display: block !important;
	color: #49ccff;
	position: absolute;
	top: 716px;
	left: -90px;
	right: 170px;
	border: 3px solid #dfdfdf;
	background: rgba(30, 30, 30, 1.0);
	height: 50px;
	border-radius: 13px;
	text-shadow: 0px 0px 10px #e70000;
	font-weight: bold;
	font-size: 150%;
	z-index: 2000;
	text-align: center;
	width: 96px;
	padding-top: 12px;
	margin-top: 4px;
	margin-left: -196px;
	margin-right: 0px;
	padding-right: 24px;
	padding-bottom: 5px;
	box-shadow: 0px 0px 4px 1px #49ccff;
}

/**********************/
.breakGame table th .breakGame table td
{
	padding: 3px 18px;
}

/**********************/
.taR
{
	text-align: center;
}

/**********************/
.breakGame #breakGameFrame .achievementGrid .achievementEntry .label
{
	max-width: 240%;
	font-size: 90%;
	white-space: normal;
	overflow: visible;
	padding-top: 2px;
}

/**********************/
.breakGame #breakGameFrame .achievementGrid .achievementEntry .icon
{
	width: 120%;
	max-height: 80px;
	max-width: 80px;
	aspect-ratio: auto;
	padding-top: 5px;
}

/**********************/
.breakGame #breakGameFrame .achievementGrid
{
	grid-gap: 11px;
}

/**********************/
.breakGame #breakGameFrame .achievementGrid .achievementEntry
{
	width: 90px;
	height: 90px;
	gap: 0px;
	padding: 3px 0px 10px 0px;
	border: 1px solid #dfdfdf;
	border-radius: 12px;
	background-color: #444444;
	box-shadow: 0px 0px 4px 1px #49ccff;
}

/**********************/
.breakGame #breakGameFrame .ingame .ingameMenu
{
	display: flex;
	flex-direction: column;
	gap: 0px;
	font-size: unset;
	justify-content: center;
	align-items: center;
	padding: 0px 9px 9px 9px;
	margin: 15px 0px 0px 0px;
}

.breakGame #breakGameFrame .overlay.gameover
{
	position: absolute;
	top: 0px;
	left: 756px;
	right: -270px;
	grid-column: unset;
	grid-row: unset;
	background-color: rgba(68, 68, 68, 1);
	text-align: center;
	transition: 0.1s;
	height: 100%;
	padding: 50px;
	z-index: 5000001;
}

.breakGame #breakGameFrame .overlay.gameover .bubble
{
	margin-left: -100px !important;
	width: 343px;
}

div.breakGame > #breakGameFrame > div.menu.content > div:nth-of-type(3) > div.tableOverflow > div.personalHighscore > table > thead > tr > th:nth-of-type(3)
{
	text-align: center;
}

div.breakGame > #breakGameFrame > div.menu.content > div:nth-of-type(3) > div.tableOverflow > div.personalHighscore > table > thead > tr > th:nth-of-type(4)
{
	text-align: center;
}

div.breakGame > #breakGameFrame > div.menu.content > div:nth-of-type(3) > div.tableOverflow > div.personalHighscore > table > thead > tr > th:nth-of-type(5)
{
	text-align: center;
}

div.breakGame > #breakGameFrame > div.menu.content > div:nth-of-type(2) > div:nth-of-type(3)
{
	margin-top: 15px;
	margin-left: 15px;
	margin-right: 10px;
	background: #444444 !important;
}

div.breakGame > #breakGameFrame > div.menu.content > div:nth-of-type(3) > div:nth-of-type(3)
{
	width: 589px;
	max-width: 589px;
	margin-left: 0px;
	margin-top: 13px;
	margin-right: 0px;
	padding-top: 0px;
	padding-left: 9px;
	padding-bottom: 9px;
	border: 2px solid #dfdfdf;
	border-radius: 12px;
	background-color: #444444;
	box-shadow: 0px 0px 4px 1px #49ccff;
}

.breakGame #breakGameFrame .gameSummary .label
{
	font-weight: 900;
	line-height: 1.0;
	text-align: center;
	font-size: 17px;
}

div.breakGame > #breakGameFrame > div.content > div.ingame > div.gameHeader > div.gameMetricContainer.score
{
	margin-top: 40px;
	margin-left: 191px;
	padding-left: 25px;
	padding-top: 0px;
	padding-bottom: 0px;
	margin-bottom: 0px;
}

div.breakGame > #breakGameFrame > div.content > div.ingame > div.gameHeader > div:nth-of-type(2)
{
	margin-top: 40px;
	margin-left: 191px;
	margin-right: 20px;
	padding-bottom: 0px;
}

#sf
{
	display: none !important;
}

div.breakGame > #breakGameFrame > div.menu.content > div:nth-of-type(4)
{
	width: 600px !important;
	margin-top: -84px !important;
	margin-left: 0px !important;
	text-align: left !important;
	max-width: 600px !important;
	padding-left: 20px !important;
	padding-right: 20px !important;
	padding-top: 11px !important;
	padding-bottom: 2px !important;
}

.breakGame #breakGameFrame .gameSummary .row .column .caption
{
	font-size: unset;
	text-align: center;
	color: #49ccff !important;
	font-weight: bold !important;
}

.breakGame p
{
	padding: 3px 3px 7px 3px;
	font-weight: bold;
	line-height: 1.3!important;
	text-align: center;
	font-size: 120%;
}

.breakGame #breakGameFrame .gameSummary .row .column .value
{
	font-size: 1.5em;
	text-align: center;
	color: #49ccff !important;
	font-weight: bold !important;
}

.breakGame #breakGameFrame div.gameSummary > div:nth-of-type(2) > div.column > div:nth-of-type(2)
{
	color: rgba(255, 0, 0, 0.49) !important;
	font-weight: 900;
	text-shadow: 1px 2px 5px black;
	text-decoration-line: underline;
}

.breakGame .btn.secondary
{
	font-weight: 500;
	background: linear-gradient(0deg, #49cbff 0%, #3394bb 100%);
	border: 2px solid #49cbff;
	box-shadow: 0 0 10px #3699c1;
	border-color: #49cbff;
	color: #ffffff;
	text-shadow: 0 0 10px black;
}

.wordGuessDetailLayer table td, .personalHighscore table td
{
	color: #fff;
}

.notificationTop
{
	z-index: 9999999;
	animation: fadeNotificationOut 2.0s forwards ease-in-out;
	position: fixed;
	bottom: 150px;
	right: 785px;
	width: 280px;
	text-align: center;
	font-size: 120%;
	padding: 10px 15px;
	font-weight: 800;
	border-radius: 15px;
}

div.notificationTop.notificationError
{
	color: #f00;
	line-height: 150%;
	font-weight: 800;
	background: #717171;
	border: 3px solid #dfdfdf;
	border-radius: 12px !important;
}

.breakGame #breakGameFrame .ingame .ingameMenu .gameMode
{
	padding: 5px 0;
	font-size: 140%;
	font-weight: 900;
	color: #f00 !important;
	text-shadow: 0 0 3px black;
	opacity: 0.9;
}
}
