style.css:
@document domain(y.hentaichan.live),
          domain(exhentai-dono.me),
          domain(hchan.live)
{
img {
	max-width: auto !important;
	max-height: 100vh !important;
}
#image {
	scroll-snap-align: start;
}
html {
	scroll-snap-type: y proximity;
}
body {
	background-color: none !important;
	/*Для смены фона скопируйте ссылку на нужное изображение и замените её в url([здесь]) в строке ниже ↓ */
	background: fixed url(https://sun9-18.userapi.com/s/v1/ig2/RYvh7_ZjmxlYr-WwBkxS2iVJ2NVAThSuHF6O2lTxuaLM2GZ0Av5GPz3htPlRhKTPyuBTCbblipUVHq3FKK6MQ5OH.jpg?size=1920x1080&quality=95&type=album) no-repeat #000 !important;
	background-size: cover !important;
}
#content {
	border: hidden;
}
.thumb {
	border: 1px solid #cfcfcf !important;
	margin: 7px;
	opacity: 0.69 !important;
}
.a-series, .a-series-title {
	color: #b6b6b6 !important;
	text-decoration: none !important;
}
.a-series:hover, .a-series-title:hover {
	color: rgb(0, 125, 10) !important;
}
.drop {
	margin-right: 5px;
	margin-left: 5px !important;
	height: 24px;
}
.styleswitch {
	visibility: hidden;
	height: 0px !important;
	width: 0px !important;
}
#header {
	background-color: #222222 !important;
	border-bottom: 2px solid #333333;
}
.siteLogo {
	color: #CFCFCF !important;
	text-shadow: 2px 2px 0 #727272, 2px -2px 0 #727272, -2px 2px 0 #727272, -2px -2px 0 #727272, 2px 0px 0 #727272, 0px 2px 0 #727272, -2px 0px 0 #727272, 0px -2px 0 #727272 !important;
}
.siteLogo:hover {
	color: #727272 !important;
	text-shadow: 2px 2px 0 #CFCFCF, 2px -2px 0 #CFCFCF, -2px 2px 0 #CFCFCF, -2px -2px 0 #CFCFCF, 2px 0px 0 #CFCFCF, 0px 2px 0 #CFCFCF, -2px 0px 0 #CFCFCF, 0px -2px 0 #CFCFCF !important;
}
a:link, a:visited, a:active {
    color: #b6b6b6 !important;
}
a:hover {
	color: rgb(0, 125, 10) !important;
}
.drop, .btn, #related {
	background-color: #222222;
	color: #b6b6b6 !important;
	text-shadow: none !important;
}
.dash {
	background: none !important;
}
.thumb, div[style="width:78px;height:110px;float:left;border:0px solid black; margin:10px;"], img[style="max-width:1000px;background-color:white;"] {
	opacity: 0.69;
}
.main_fon {
	background-color: #222222 !important;
	border-color: #333333 !important;
	color: #b6b6b6 !important;
}
.extaraNavi {
	background-color: #333333 !important;
	border: 1px solid #333333 !important;
	border-radius: 3px;
}
.extra_on {
	border: none !important;
	background-color: #333333 !important;
}
div[style="background-color:#f2f4f6;padding-bottom:10px;margin-top:-6px;padding-left:13px;margin-left:-13px;margin-right:-16px;padding-top:5px;"] {
	background-color: #333333 !important;
}
.comment_left, .comment_right {
	background-color: #222222 !important;
}
.manga_comment_row {
	background-color: #333333 !important;
}
#post-comment {
	background-color: #333333 !important;
	color: #b6b6b6;
	border: none !important;
	border-top-right-radius: 0px !important;
	border-top-left-radius: 5px !important;
	border-bottom-left-radius: 0px !important;
}
a[onclick="megaAjax('subscrabe_button_', '42326', '0','subscrabe');return false;"] {
	background-color: #333333 !important;
	color: #b6b6b6;
	border: none !important;
	border-top-right-radius: 5px !important;
	border-top-left-radius: 0px !important;
	border-bottom-left-radius: 5px !important;
	border-bottom-right-radius: 0px !important;
	font-weight: bold;
	padding: 4px !important;
}
#navigator {
	background-color: #222222 !important;
	border-bottom: solid 1px #333333 !important;
	border-left: solid 1px #333333;
	border-right: solid 1px #333333 !important;
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;
}
#top {
	background: none !important;
	background-color: #222222 !important;
	border-bottom: solid 1px #333333 !important;
	border-left: solid 1px #333333;
	border-right: solid 1px #333333 !important;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
}
#top_rss {
	background: none !important;
	width: 20px !important;
}
#top_search, #top_user, #top_site {
	background: none !important;
}
#search_button {
	border: 1px solid rgb(210, 210, 210) !important;
	border-left: none !important;
	height: 22px;
	margin-top: -1px;
}
#story {
	background-color: #222222;
	border: 1px solid rgb(210, 210, 210) !important;
	color: rgb(210, 210, 210);
}
#top a, a[href="/adminsamma.php?mod=addnews&action=addnews"], a[title="Связатся с администрацией Хентай-тян!"] {
	color: rgb(210, 210, 210) !important;
}
.name_row a, .related_info a {
	text-shadow: none !important;
	color: #4d8504;
	font-weight: bolder;
}
.sidetag a {
	color: #006ffa !important;
}
.gasdsp {
	margin-top: 3px;
	margin-left: 1px;
}
a[href="https://y.hentaichan.live/manga/"], a[href*="/series/"], a[href*="/mangaka/"], a[href*="/translation/"] {
	color: #0054bb !important;
	text-decoration: none !important;
}
a[href*="/user/"] {
	color: #ad6a6a !important;
	text-decoration: none !important;
}
a[style="color:#333333;"] {
	color: #b6b6b6 !important;
	font-weight: normal !important;
}
#pagination {
	background-color: #222222 !important;
}
div[style="float:left;background-color:#FFF;border-top-left-radius:5px;border-top-right-radius:5px;margin-bottom:-20px;padding-bottom:20px;border:1px solid #d6dee5;"] {
	background-color: #222222 !important;
	border: 1px solid #333333 !important;
}
div[style="border:1px solid #BBB; float:left;"] {
	border: none !important;
}
#add_comment {
	border-bottom-right-radius: 7px;
	border-bottom-left-radius: 7px;
}
label[for="allow_subscribe"] {
	color: rgb(39, 39, 39);
}
.baseTitle {
	background-color: #333333 !important;
}
.area_right {
	background-color: #222222 !important;
	border: 1px solid #333333 !important;
}
.area_rightSpace {
	background-color: #222222 !important;
}
.area_right a[href*="/manga/new"], a[href*="/games/new"], a[href*="/video/new"], a[href*="/lastcomment/"], a[href*="/tags/"], #top_left li a, .recent-row a {
	color: #0054bb !important;
	text-decoration: none !important;
}
div[style="margin-bottom:4px;font-size:14px;font-weight:bold;"] {
	visibility: hidden !important;
}
img[height="140"], .entry a img {
	border: 1px solid #cfcfcf !important;
	margin: 4px;
}
.recent-row, .info-left {
	color: #b6b6b6 !important;
}
#right div[style="background-color:#f2f4f6;padding:10px;margin-bottom:10px;width:555px;"] {
	background-color: #333333 !important;
}
.related {
	background: none !important;
	background-color: #333333 !important;
	border: none !important;
	overflow: hidden;
	border-radius: 5px;
}
.content_row {
	background-color: #333333 !important;
	border: none !important;
	overflow: hidden;
	border-radius: 5px;
}
#pagination_related {
	background-color: #222222 !important;
}
.profile-box {
	border: none !important;
}
.head-category {
	background-color: #222222 !important;
	border: none !important;
	color: #cfcfcf;
}
.additional-box a {
	background-color: #222222 !important;
	border: none !important;
	color: #0054bb !important;
}
/*******************************************************/
}
