body {
  background-image: url(https://roschmann.group/files/2018/06/cf021373.jpg);
  background-size: cover;
}

h3 a {
  font-size: 16px;
  font-family: Rubik;
}

.il-mainbar {
  width: 80px;
  background-color: rgb(34,34,34);
}

.il-mainbar-triggers {
  background-color:transparent;
}

.il-mainbar-triggers .btn-bulky .icon, .il-mainbar-triggers .il-link.link-bulky .icon {
  filter: invert(50%);
}

.il-maincontrols-mainbar, .il-mainbar-slates, .il-maincontrols-slate .btn-bulky, .il-maincontrols-slate .link-bulky, div.il-maincontrols, .il-layout-page {
  background-color: transparent;
  transition: 0.1s;
}

.il-mainbar-triggers .btn-bulky .bulky-label, .il-mainbar-triggers .il-link.link-bulky .bulky-label {
  	color: rgba(255,255,255,0.5);
  font-size: 10px;
  width: 50px;
}

.il-mainbar-triggers .btn-bulky, .il-mainbar-triggers .il-link.link-bulky {
  border-radius: 50%;
  background-color: rgb(34,34,34);
  border-bottom: none;
  margin: 8px;
  width: 60px;
  height: 60px;
}

.il-mainbar-triggers .btn-bulky.engaged {
  border-radius: 10%;
}

.il-maincontrols-slate.engaged {
  background-color: rgba(255,255,255,0.2);
  backdrop-filter: blur(20px);
  margin-left: -10px;
}

.il-link.link-bulky {
  background-color: rgba(255,255,255,0.5);
  padding: 20px;
  border-radius: 0 50px 50px 0%;
}

.il-mainbar-close-slates {
  background-color: rgba(34,34,34,0.9);
}

.il-link.link-bulky .bulky-label {
  color: white;
  margin-top: auto;
  margin-bottom: auto;
}

.il-maincontrols-slate .link-bulky .icon {
  filter: invert(90%);
  margin: 5px;
  margin-right: 20px;
}

.ilContainerBlock {
  margin-top: 25px;
}

#mainscrolldiv {
  background-color: rgba(255,255,255,0.3);
  width: 75%;
  margin: auto;
  backdrop-filter: blur(5px);
  padding: 30px;
  text-align: left;
  box-shadow: 0 0 100px gray;
}

.ilc_Paragraph {
  text-align: justify;
  text-shadow: 0 0 20px white;
}

.ilHeader .ilAccAnchor {
    font-family: 'Pacifico', cursive;
   font-weight: regular;
    color: #21ff85!important;
    font-size: 60px!important;
    padding-left: 40px;
   text-shadow:
    -1px -1px 0 black,  
     1px -1px 0 black,
    -1px  1px 0 black,
     1px  1px 0 black,
     0px 0px 10px #21ff85;
}

div.ilHeaderDesc {
  font-family: Trispace;
  width: 40%;
  font-size: 14px;
  margin: auto;
  margin-top: 40px;
  text-align: center;
  font-weight: regular;
  color: #2c2c2c;
  text-shadow:
     0px 0px 50px #21ff85;
}

img.ilListItemIcon {
  width: 20px;
  height: 20px;
  opacity: 50%;
}

