.btn-default {
  background-color: #527dff;
  border: none;
  border-radius: 20px;
}

.btn-default:hover {
  background-color: darken(#527dff, 20%);
}

.btn-default.focus, .btn-default:focus, .btn-default:active {
  background-color: darken(#527dff, 50%)!important;
}

table.calmini td.calminitoday {
  background-color: #59ba73;
  border-radius: 20px;
}

.panel, .panel-heading {
  border-radius: 20px 20px 20px 20px!important;
}

body {
  font-family: Rubik;
}

.ilHeader {
  text-align: center;
}

.media.il_HeaderInner {
  border-radius: 50%;
    background-repeat: no-repeat;
    background-size: contain;
    background-position: top;
}

.ilContainerBlockHeader, h2 {
  font-family: Lora;
}

#headerimage {
    visibility: hidden!important;
}

.media-left {    
   background-image: url(https://p.turbosquid.com/ts-thumb/hR/T4EjOD/uS/cool_face_android_emoji_360/jpg/1638703587/600x600/turn_fit_q87/4e995b2e9910e140c72ddda8be4abc92477ae16b/cool_face_android_emoji_360-1.jpg);
    border-radius: 50%;
    background-repeat: no-repeat;
    background-size: contain;
    background-position: top;
    padding-right: 50px;
    margin-right: 20px;
}

.media-left .icon.medium {
    visibility: hidden;
}

.media a {
    color: #619eff;
    font-size: 20px!important;
   font-weight: bold;
  transition: 0.5s
}

.media a:hover {
    text-decoration: none;
    padding-left:5px;
}
