style.css:
/* i really want this to be global */
* {
    font-family: Helvetica, sans-serif!important;
}
body {
    color: #d2d2d2;
    background-color: #131313;
}
h5 {
    color: #d2d2d2;
}
input::selection {
    background-color: #6a00c0;
}
#stags {
    width: 180px;
    border-radius: 10px;
    color: #bfbfbf;
    height: 30px;
    margin-top: 7px;
    background-color: #2a2a2a;
    border-style: none;
}
#tags {
    width: 180px;
    border-radius: 10px;
    color: #bfbfbf;
    height: 30px;
    margin-top: 7px;
    background-color: #2a2a2a;
    border-style: none;
}
input[name="search"] {
    width: 100px;
    border-radius: 10px;
    color: #bfbfbf;
    height: 30px;
    margin-top: 7px;
    margin-bottom: 15px;
    background-color: #2a2a2a;
    border-style: none;
}
input {
    outline: none;
    width: 100px;
    border-radius: 10px;
    color: #bfbfbf;
    height: 30px;
    background-color: #2a2a2a;
    border-style: none;
}
textarea {
    padding: 7px;
    outline: none;
    border-radius: 10px;
    color: #bfbfbf;
    background-color: #2a2a2a;
    border-style: none;
}
input[name="searchDefault"] {
    margin-top: 10px!important;
    border: none;
    border-radius: 20px;
    color: #e6e6e6;
    background-color: #6d00b4;
    width: 120px;
    height: 30px;
}
input[name="commit"] {
    margin-top: 10px!important;
    margin-bottom: 7px!important;
    display: block;
    border: none!important;
    border-radius: 20px;
    color: #e6e6e6;
    background-color: #6d00b4!important;
    width: 100px!important;
    height: 30px;
}
input[name="submit"] {
    margin-top: 10px!important;
    margin-bottom: 7px!important;
    border: none!important;
    border-radius: 20px;
    color: #e6e6e6;
    background-color: #6d00b4!important;
    width: 100px!important;
    height: 30px;
}
div#paginator a {
    margin: 0 3px;
    padding: 4px 9px;
    font-weight: normal;
    border: 2px solid #6100b2;
    border-radius: 16px;
}
div#paginator a:hover {
    background: #6d00b4;
    color: white;
}
preview hoverZoomLink {
    margin-right: 70px!important;
}
div#comment-list2 img.preview {
    display: inherit!important;
    margin-left: auto;
    /* margin-right: auto; */
}
img {
    margin-right: 0!important;
    width: auto;
    border-radius: 4px;
    vertical-align: middle;
}
a:visited {
    color: #9b3bd8;
    text-decoration: none;
}
a:link {
    color: #9b3bd8;
    text-decoration: none;
}
div.sidebar {
    width: 15%;
    margin-right: 1em;
    float: left;
    overflow: hidden;
}
span.thumb {
    display: inline-block;
    text-align: center;
    vertical-align: middle;
}
#note-container {
    width: 79%;
    height: 100%;
    margin: 0;
}
#image {
    width: 100%;
    height: 100%;
    object-fit: contain;
}

/*********************
     *SCROLLBAR*
 ********************/
::-webkit-scrollbar {
    width: 10px;
    height: 10px;
}
::-webkit-scrollbar-track-piece {
    background-color: gray;
}
::-webkit-scrollbar-thumb {
    background-color: black;
}

table {
    border-collapse: collapse;
    /* Убираем двойные рамки */
}
th,
td {
    border: none;
    /* Параметры рамки */
    padding: 4px;
    /* Поля в ячейках */
}
tr:hover {
    background: #370057!important;
    /* Цвет фона */
}
.toptencont {
    border: none;
}
