style.css:
@-moz-document url-prefix("https://www.jappy.com/"), url-prefix("https://www.jappy.com/dashboard") {
	html
	{
	    background: #444 !important;
	}

	body
	{
	    background: #444;
	    color: #49ccff;
	}

	/***Benachrichtigungen***/
	.dashboard .widget > .head .counter, .dashboard .widget > .widgetWrapper > .head .counter
	{
	    margin-right: 10px;
	    margin-left: -30px;
	    line-height: 2em;
	    color: #fff;
	    text-align: center;
	    font-weight: 900;
	    border: 2px solid #dfdfdf;
	    background: #444444;
	    padding: 1px;
	    box-shadow: 0px 0px 4px 1px #49ccff;
	    animation: blink 2s linear infinite !important;
	}

	.userSidebarEntry .counter
	{
	    border-radius: 68%;
	    text-align: center;
	    width: 26px;
	    height: 14px;
	    margin-top: -28px;
	    margin-left: 86px;
	    color: #ffffff;
	    font-weight: 900;
	    padding-top: 6px;
	    padding-bottom: 4px;
	    padding-right: 0px;
	    padding-left: 0px;
	    border: 2px solid #dfdfdf;
	    background: #444444;
	    box-shadow: 0px 0px 4px 1px #49ccff;
	    animation: blink 2s linear infinite !important;
	}

	a.userSidebarEntry.cf > div.icon > div.counter.userNotificationNewMails
	{
	    margin-left: 116px;
	    animation: blink 2s linear infinite !important;
	}

	.newEntries
	{
	    display: block;
	    text-align: center;
	    border-radius: 50%;
	    height: 12px;
	    width: 12px;
	    font-size: 12px;
	    color: #ffffff !important;
	    padding: 4px;
	    font-weight: 900;
	    border: 2px solid #dfdfdf;
	    background: #444444;
	    box-shadow: 0px 0px 4px 1px #49ccff;
	}

	/***Benachrichtigungen***/
	/***Kachel Spalten***/
	.dashboard .grid
	{
	    background: #444;
	    grid-template-columns: 28% 28% 28% 28% 28% 28%;
	    max-width: 100%;
	}

	.siteSectionHeader
	{
	    font-weight: 600;
	    display: flex;
	    width: 14%;
	    box-sizing: border-box;
	    padding: 6px;
	    gap: 0px;
	    align-content: center;
	    align-items: center;
	    justify-content: flex-start;
	    border: 2px solid #dfdfdf !important;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	    background: #717171;
	    color: #49ccff;
	    border-radius: 10px !important;
	}

	.siteSectionHeader > .caption
	{
	    flex-grow: 2;
	    padding: 0 0 0 8px;
	    font-weight: 700;
	    font-size: 1.3em;
	    text-shadow: 0 0 3px black;
	}

	.dashboard .widget
	{
	    border: 2px solid #dfdfdf !important;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	    background: #717171;
	    color: #49ccff;
	    margin-top: 5px;
	    margin-right: 5px;
	    border-radius: 10px !important;
	}

	.dashboard .widget > .head .caption, .dashboard .widget > .widgetWrapper > .head .caption
	{
	    text-shadow: 0 0 3px black;
	    color: #49cbff !important;
	}

	.dashboard .small:hover
	{
	    transition: 0.1s;
	    opacity: 1.0;
	    box-shadow: 0px 0px 8px 1px #fff;
	    animation: blink 2s linear infinite !important;
	}

	@keyframes blink
	{
	    0%
	    {
	        box-shadow: rgb(255, 255, 255) 0px 0px 8px 1px !important;
	    }

	    50%
	    {
	        box-shadow: none;
	    }

	    100%
	    {
	        box-shadow: rgb(255, 255, 255) 0px 0px 8px 1px !important;
	    }
	}

	.dashboard .small:hover .head
	{
	    background: none;
	    transition: none;
	}

	div.site.cf
	{
	    margin-top: 10px;
	    margin-left: 20px;
	}

	.site .siteRight
	{
	    background: #4440;
	}

	.site .siteLeft
	{
	    background: #717171;
	    border: 2px solid #dfdfdf !important;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	    margin-right: 8px;
	    border-radius: 20px;
	}

	.userSidebarEntry .sectionToggle span
	{
	    border: 1px solid #dfdfdf;
	    background-color: #ffffff;
	    border-radius: 20px;
	    opacity: 1.0;
	}

	.siteDouble .left
	{
	    margin-left: 20px;
	    border-radius: 25px;
	    background: #717171;
	    border: 2px solid #dfdfdf;
	    padding: 10px;
	    width: fit-content;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	}

	#dw_newsChangelog, .dashboard .newsChangelogDetail
	{
	    height: 250px !important;
	    background: #717171;
	}

	#dw_newsChangelog .content .text
	{
	    margin-left: 10px;
	    margin-right: 10px;
	    margin-bottom: 10px;
	    padding: 0px;
	    padding-bottom: 0px;
	    border: 2px solid #dfdfdf;
	    background-color: #444444;
	    border-radius: 20px;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	}

	#dw_newsChangelog .content .subcaption, .newsChangelogDetail .changeLogEntry .subcaption
	{
	    font-weight: 900;
	    font-size: 120%;
	    color: #fff;
	    margin-left: 55px;
	}

	#dw_newsChangelog .content .detailChanges li, .newsChangelogDetail .changeLogEntry .detailChanges li
	{
	    line-height: 1.3;
	    font-weight: 900;
	    margin-bottom: 5px;
	    color: #fff;
	}

	/***Neueemos***/
	#dw_emotionLegacyPropagation .emotionLegacyEntry .caption, .emotionLegacyPropagationDetail .emotionLegacyEntry .caption
	{
	    font-weight: 700;
	    color: #fff;
	    font-size: 120%;
	}

	#dw_emotionLegacyPropagation, .dashboard .emotionLegacyPropagationDetail
	{
	    height: 250px !important;
	    background: #717171;
	}

	#dw_emotionLegacyPropagation .content.entryCount3, #dw_emotionLegacyPropagation .content.entryCount4
	{
	    grid-template-columns: repeat(3, 1fr);
	    grid-template-rows: repeat(1, 1fr);
	    margin-left: 10px;
	    margin-right: 10px;
	    margin-bottom: 10px;
	    padding: 0px;
	    padding-bottom: 0px;
	    border: 2px solid #dfdfdf;
	    background-color: #444444;
	    border-radius: 20px;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	}

	#dw_emotionLegacyPropagation .emotionLegacyEntry .price, .emotionLegacyPropagationDetail .emotionLegacyEntry .price
	{
	    font-size: 100%;
	    font-weight: 700;
	    color: #ffffff;
	}

	/***Neueemos***/
	/***giftSet***/
	#dw_giftPropagation, .dashboard .giftPropagationDetail
	{
	    height: 250px !important;
	    background: #717171;
	}

	#dw_giftPropagation .content, .giftPropagationDetail .body
	{
	    margin-left: 10px;
	    margin-right: 10px;
	    margin-bottom: 10px;
	    padding: 0px;
	    padding-bottom: 0px;
	    border: 2px solid #dfdfdf;
	    background-color: #444444;
	    border-radius: 20px;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	}

	#dw_giftPropagation .giftVariantEntry .caption, .giftPropagationDetail .giftVariantEntry .caption
	{
	    font-weight: 700;
	    color: #fff;
	    font-size: 120%;
	}

	#dw_giftPropagation .giftVariantEntry .price, .giftPropagationDetail .giftVariantEntry .price
	{
	    display: flex;
	    font-size: 100%;
	    font-weight: 700;
	    color: #fff;
	}

	#dw_giftSetPropagation, .dashboard .giftSetPropagationDetail
	{
	    height: 250px !important;
	    background: #717171;
	}

	#dw_giftSetPropagation .content, .giftSetPropagationDetail .body
	{
	    margin-left: 10px;
	    margin-right: 10px;
	    margin-bottom: 10px;
	    padding: 0px;
	    padding-bottom: 0px;
	    border: 2px solid #dfdfdf;
	    background-color: #444444;
	    border-radius: 20px;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	}

	#dw_giftSetPropagation .giftSetEntry .caption, .giftSetPropagationDetail .giftSetEntry .caption
	{
	    font-weight: 700;
	    color: #fff;
	    font-size: 120%;
	}

	/***giftSet***/
	/***Sammelbild-Vermittlung***/
	#dw_collectableFinder > div.content > div > img
	{
	    height: 14em !important;
	    margin-top: 20px;
	    margin-left: 10px;
	    margin-right: 10px;
	    padding: 10px;
	    border: 2px solid #dfdfdf;
	    background-color: #444444;
	    border-radius: 20px;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	}

	#dw_collectableFinder
	{
	    color: #fff;
	    background: #717171;
	    width: 400px;
	    grid-column: 5/ span 2 !important;
	    grid-row: 3 / span 2 !important;
	}

	#dw_collectableFinderResult
	{
	    color: #fff;
	    background: #717171;
	    width: 400px;
	    grid-column: 5/ span 2 !important;
	    grid-row: 3 / span 2 !important;
	}

	#dw_collectableFinderResult .list
	{
	    position: relative;
	    min-height: 150px;
	    margin-top: 20px;
	    margin-left: 10px;
	    margin-right: 10px;
	    padding: 10px;
	    border: 2px solid #dfdfdf;
	    background-color: #444444;
	    border-radius: 20px;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	}

	#dw_collectableFinderResult .list img:nth-child(1)
	{
	    position: absolute;
	    top: 40px;
	    left: 30px;
	    z-index: 4;
	}

	#dw_collectableFinderResult .list img:nth-child(2)
	{
	    position: absolute;
	    top: 20px;
	    left: 90px;
	    z-index: 3;
	}

	#dw_collectableFinderResult .list img:nth-child(3)
	{
	    position: absolute;
	    top: 40px;
	    left: 170px;
	    z-index: 2;
	}

	#dw_collectableFinderResult .list img:nth-child(4)
	{
	    position: absolute;
	    top: 20px;
	    left: 250px;
	    z-index: 1;
	}

	/***Sammelbild-Vermittlung***/
	.listOfEchos
	{
	    padding: 5px;
	    border: 1px solid #dfdfdf;
	    background-color: #444444;
	    border-radius: 10px;
	    margin-left: 5px;
	    margin-right: 0px;
	    margin-top: 5px;
	}

	.echoInfos .numberOfComments
	{
	    margin: 5px 5px 0px 0px;
	    padding: 10px 5px 10px 7px;
	    background: #444444;
	    border: 1px solid #dfdfdf;
	    border-radius: 10px;
	}

	.getMoreTickerEntries
	{
	    padding: 10px 15px;
	    font-weight: 500;
	    background: linear-gradient(0deg, #49cbff 0%, #3394bb 100%);
	    border: 2px solid #49cbff;
	    box-shadow: 0 0 10px #3699c1;
	    border-color: #49cbff;
	    color: #ffffff;
	    text-shadow: 0 0 10px black;
	}

	.listOfEchos .echo.addEcho
	{
	    background: #444444;
	    border: 1px solid #dfdfdf;
	}

	.listOfEchos .echo
	{
	    background: #444444;
	    border: 1px solid #dfdfdf;
	    border-radius: 10px;
	    margin-right: 5px;
	}

	.listOfEchos .more
	{
	    margin: 0px 0px 0px 5px;
	    padding: 7px 7px 7px 7px;
	    background: #444444;
	    border: 1px solid #dfdfdf;
	    border-radius: 10px;
	}

	.listOfEchos .echo.own
	{
	    background: #717171;
	}

	.feed .fragment
	{
	    border: 2px solid #dfdfdf;
	    background: #717171;
	    border-radius: 20px 20px 11px 11px;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	}

	.feed .fragment:hover .entry
	{
	    animation: none;
	    border: 0px solid #dfdfdf !important;
	    border-top: none;
	}

	.feed .fragment .actions
	{
	    animation: none;
	    background: #444444 !important;
	    border: 0px solid #dfdfdf !important;
	    border-radius: 20px 20px 0px 0px;
	    border-top: 0px solid #dfdfdf !important;
	    border-bottom: 2px solid #dfdfdf !important;
	}

	.feed .fragment:hover .actions
	{
	    animation: none;
	    background: #444444 !important;
	    border: 0px solid #dfdfdf !important;
	    border-radius: 20px 20px 0px 0px;
	    border-top: 0px solid #dfdfdf !important;
	    border-bottom: 3px solid #dfdfdf !important;
	}

	.feed .attachedImage img
	{
	    border-top: 0px solid #dfdfdf !important;
	    border-bottom: 4px solid #dfdfdf !important;
	}

	.feed .profileImageEntry img
	{
	    border-bottom: 3px solid #dfdfdf !important;
	}

	a.streamVideoPreview img
	{
	    border-top: 3px solid #dfdfdf !important;
	    border-bottom: 2px solid #dfdfdf !important;
	}

	.feed .entry
	{
	    border-top: 1px solid #dfdfdf;
	    background: #444444;
	    border-left: none;
	    border-right: none;
	    border-bottom: none;
	}

	.feed .entryHeader
	{
	    background: #444444;
	    border: 1px solid #dfdfdf;
	    border-radius: 20px;
	    margin: 0px 90px 5px 90px;
	    padding: 0;
	}

	.tickerUserHead
	{
	    padding: 0px 0 10px 0;
	}

	.feed .ticker .text
	{
	    color: #49ccff;
	}

	.feed .ticker
	{
	    margin-bottom: 5px;
	}

	.feed .actions .topicName
	{
	    color: #49ccff !important;
	    font-weight: 900;
	}

	.feed .actions .list img
	{
	    width: 28px;
	    opacity: 1.0;
	    padding: 3px;
	    border: 1px solid #dfdfdf;
	    background-color: #444444;
	    border-radius: 20px;
	    box-shadow: 0px 0px 4px 1px #49ccff;
	}

	.feed .actions .list .action
	{
	    float: right;
	    border-radius: 50%;
	    padding: 3px;
	    margin-top: 5px;
	    cursor: pointer;
	    margin-left: 15px;
	}

	.feed .actions .topicIcon img
	{
	    width: 30px;
	    height: 30px;
	    margin: 0px;
	    border: 1px solid #dfdfdf;
	    background-color: #444444;
	    padding: 3px;
	    border-radius: 20px;
	    box-shadow: 0px 0px 4px 1px #000000;
	}

	.feed .actionText
	{
	    color: #49ccff !important;
	    font-weight: 900;
	}

	.rubyBar .title
	{
	    font-weight: 800;
	    font-size: 228%;
	    color: #49ccff !important;
	}

	.rubyBar .rubyState
	{
	    color: #ffffff;
	    border: 2px solid #dfdfdf;
	    border-radius: 12px;
	    background-color: #444444;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	    margin-top: 10px;
	    margin-right: 5px;
	}

	.rubyBar > a:hover, .rubyBar > .rubyState:hover
	{
	    animation: none;
	}

	.rubyBar .rubyState a
	{
	    color: #49ccff !important;
	}

	.visitorShop
	{
	    background: #717171;
	    border: 1px solid #dfdfdf;
	    border-radius: 20px;
	}

	.visitorShop .offer
	{
	    background: #717171;
	    border: 1px solid #dfdfdf;
	}

	.visitorShop .explanation
	{
	    font-weight: 600;
	}

	.visitorShop .offer .description
	{
	    font-weight: 600;
	}

	.visitorShop .offer .title
	{
	    font-weight: 900;
	    font-size: 17px;
	    color: #000;
	}

	.visitorShop .offer .notAvailable
	{
	    font-size: 90%;
	    font-weight: 900;
	    color: #f00 !important;
	    text-shadow: 0 0 3px black;
	    opacity: 0.9;
	    grid-column: 1 / span 2;
	}

	.streamLoading
	{
	    display: none !important;
	}

	.streamLoading .text
	{
	    display: none !important;
	}

	.reactionMatch
	{
	    background: #717171;
	    border: 1px solid #dfdfdf;
	    border-radius: 20px;
	}

	.reactionMatch hr
	{
	    width: auto;
	    background: #717171;
	    border: 1px solid #dfdfdf;
	}

	.userSidebarEntry.active.cf
	{
	    background: #717171;
	    border: 3px solid #dfdfdf;
	    border-radius: 14px;
	}

	.pinBox.styled
	{
	    background: #717171;
	    border-radius: 20px;
	    border: 2px solid #dfdfdf;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	}

	.userSidebarEntry:hover
	{
	    background: #ffedb000;
	}

	.userSidebarContent .navigationSectionPlainLink:hover
	{
	    background: #ffedb000;
	}

	.userSidebarContent .navigationSectionPlainLink.active
	{
	    background: #ffedb000;
	}

	.navigationSectionBrick:hover
	{
	    background: #ffedb000;
	}

	a.navigationSectionPlainLink.navigationSectionFilter.navigationSectionFilter_emotion.cf
	{
	    display: none;
	}

	.userSidebarContent .navigationSectionPlainLink:last-child
	{
	    margin-bottom: 0px;
	}

	.navigationSectionBrick .text
	{
	    padding-top: 17px !important;
	    padding-bottom: 0px !important;
	    font-weight: 900;
	    color: #ffffff;
	    text-shadow: 0 0 4px black;
	}

	.userSidebarContent .navigationSectionPlainLink .text
	{
	    font-weight: 600;
	}

	.userSidebarContent .navigationSectionPlainLink .icon img
	{
	    width: 30px;
	    height: 30px;
	    padding: 2px;
	    border: 1px solid #dfdfdf;
	    background-color: #444444;
	    border-radius: 20px;
	    box-shadow: 0px 0px 8px 1px #49ccff;
	}

	.mailboxPreview .conversation a
	{
	    background: #717171;
	    border-left: 5px solid #717171;
	}

	.userSidebarEntry
	{
	    border-left: 5px solid #ff9e3300;
	}

	.expanded.navigationSection6.navigationSection > .userSidebarContent .cf.navigationSectionPlainLink:nth-of-type(2)
	{
	    display: none;
	}

	.expanded.navigationSection6.navigationSection > .userSidebarContent .cf.navigationSectionPlainLink:nth-of-type(7)
	{
	    display: none;
	}

	.userSidebarEntry .text
	{
	    color: #49ccff;
	}

	.header .headSearch input
	{
	    color: #49ccff !important;
	}

	.header .headSearch input:focus
	{
	    background: #484848;
	}

	.header .headSearch input:hover
	{
	    background: #484848;
	}

	.header .headNotifications .headNotificationSection a:hover
	{
	    background: #717171;
	}

	.header .userAccount.active, .header .userAccount:hover
	{
	    background: #717171;
	}

	.topicSelector .topic .description
	{
	    color: #ffffff;
	    font-weight: 900;
	}

	.tickerLayer .twoPane .meta .description div
	{
	    font-weight: 900 !important;
	    color: #49cbff !important;
	}

	.tickerLayer .zoom
	{
	    color: #ffffff;
	    border-radius: 20px;
	    opacity: 0.8;
	    border: 1px solid #dfdfdf;
	    background-color: #444444;
	    box-shadow: 0px 0px 4px 1px #000000;
	}

	.tickerLayer .twoPane .meta .headline
	{
	    font-weight: 900 !important;
	    color: #49cbff !important;
	}

	.topicSelector .topic:hover
	{
	    background: #717171 !important;
	    border: 1px solid #dfdfdf;
	}

	.topicSelector .topic .icon img
	{
	    width: 40px;
	    height: 40px;
	    margin: 0px;
	    border: 1px solid #dfdfdf;
	    background-color: #444444;
	    padding: 3px;
	    border-radius: 20px;
	    box-shadow: 0px 0px 4px 1px #000000;
	}

	.layerTickerDetails .tickerLayer .frame.editing
	{
	    background: #717171 !important;
	    border: 1px solid #dfdfdf;
	    margin-top: 10px;
	}

	.tickerLayer.edit .statusSelector
	{
	    background: #717171 !important;
	    border: 1px solid #dfdfdf;
	    margin-top: 10px;
	}

	#tickerEditorLayer > div > div.head
	{
	    font-weight: 900;
	    color: #49cbff !important;
	}

	#tickerEditorLayer > div > div.listing.cf > div:nth-child(1)
	{
	    font-weight: 900 !important;
	    color: #49cbff !important;
	}

	#tickerEditorLeft > div > div.attachments.cf > div
	{
	    background: #717171 !important;
	}

	a.metaRecord .metaBox, .metaRecordCompact .metaBox
	{
	    border-left: 0px;
	    margin-top: 10px;
	}

	a.metaRecord .metaTitle, .metaRecordCompact .metaTitle
	{
	    font-weight: 900;
	    color: #49cbff !important;
	}

	a.metaRecord .metaText
	{
	    font-weight: 900;
	    color: #49cbff !important;
	}

	.tickerLayer .imageUploadStatus .info
	{
	    font-weight: 900;
	    color: #49cbff !important;
	}

	.tickerLayer .info
	{
	    background: #717171;
	    font-weight: 900;
	    color: #49cbff !important;
	}

	.tickerLayer .imageUploadStatus .headline
	{
	    font-weight: 900;
	    color: #49cbff !important;
	}

	.tickerLayer .saveTicker button[disabled]
	{
	    font-weight: 900;
	    color: #ffffff;
	    border: 3px solid #29fd00;
	    border-color: #81E500;
	    box-shadow: 0 0 2px #5de500;
	    background: linear-gradient(0deg, #9CE500 0, #A7FF33 100%);
	    text-shadow: 0 0 10px black;
	}

	.tickerLayer .explanation
	{
	    font-weight: 900;
	    color: #49cbff !important;
	}

