style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://eljur.gospmr.org") {
    
    *{
        text-shadow: 0 0 black!important;
    }

 
    
        .submit, .button.disabled, .button[disabled], .button.disabled, .button[disabled] {
        background: #1D1D1D!important;
        cursor: pointer;
        border: 1px solid #840505!important;
} 

    .submit:hover, .messages-item:hover, .button[disabled]:hover, .button.disabled:hover, .button[disabled]:hover, .button.disabled:hover {

        border: 1px solid #fff0!important;
        color: #1D1D1D!important;
        background: /*[[color_detals]]*/!important;
}



    
    
    /*background-color #2A2A2A*/
    textarea, .footer-menu-icon, .ezhXia3085KYemTFB_N4, .yXIY3sqXtSRbgR6N_5MD, .ej-panel, .ej-table tbody tr td, tfoot, .xfmmft_rUe6MHwduow2f, .calendar-day, .calendar-dayweek, .UeM_u8vJ8IwmZff4lQSg, .context__block, .trLes, .trTotal, .ej-event__room, .ej-select__header, .ej-event, .ej-accordion, .messages-item:hover, .board-item{
        background-color: #2A2A2A!important;
    }
    
    /*background-color #1D1D1D*/

    .main, .schedule-week, .misssum, .m-scroll, .misssum tr td, .login-form, .layout-base, #dnevnik-header, .layout-main .layout-content .grid .grid-group .grid-header .inside, .messages-item__author, .cells, .cells_header, .grid__scrollbar, .ej-select__header {
        background-color: #1D1D1D!important;
    }
    
    
    
    .field.disabled, .field.disabled:focus, .field.disabled:hover, .field[disabled], .field[disabled]:focus, .field[disabled]:hover {
        background-color: #1D1D1D!important;
    }
    
    /*color white*/
    
    .ej-form-label span, .navigation-tabs-label, .ej-form__header, .typography h5, .widget__header, .ej-accordion__title, .dnevnik-day__title, .field, .yXIY3sqXtSRbgR6N_5MD, .eR069Hg7azuJYELDr4Fb, .sbS7qnikFTmjnDJ2bL6T, .ej-panel, #table-miss-sum, tr, .strong, .UeM_u8vJ8IwmZff4lQSg, .calendar div, .context__block, .board-item, .board-item__title, .schedule__day__content__lesson, .columns, .ej-form__title, .ej-form-label, .control-label, input{
        color: white!important;
    }
    
    .control-checkbox__label, .page-empty, .board-item__dates, .messages-item__link{
        color: wheat;
    }
    
    
    .cell, .messages-item, .messages-header, .ej-select__header, .ej-select-popup__inner, #footer, .layout, #contextSettings .context__block, .login-form__body, .container-header-page-tab--1jYmW, .page-tab--1rYXl, .fulldate, .null, .cells_header{
        background-color: #1D1D1D!important;
        color: white!important;
    }
    
    
    
    /*webkit-scrollbar*/
    ::-webkit-scrollbar {
        background-color: #1D1D1D!important;
    }
    
    ::-webkit-scrollbar-thumb{
        background-color: #42413F!important;
    }
    
    
    .layout-main {
        background-color: #1D1D1D;
        border-top: 1px solid /*[[color_detals]]*/;
        border-bottom: 1px solid /*[[color_detals]]*/;
    }
    
    .logo {
        box-shadow: 0 0 100px 10px /*[[color_detals]]*/;
        width: auto;
        height: 62px;
        overflow: hidden;
        transition: 0.4s;
    }
    
    .navigation-tabs__items .active{
        border-color: /*[[color_detals]]*/!important;
    }
    
    
    input.field, .ej-select__input{
        background-color: #2A2A2A!important;
        border-color:/*[[color_detals]]*/;
    } 
    
        .trselect {
        background-color: /*[[color_detals]]*/!important;
    }
    
    .layout-main {
        box-shadow: 0 0 20px 0 /*[[color_detals]]*/;
        
    }
    
        .dnevnik-day--holiday .dnevnik-day__title{
        color: /*[[color_detals]]*/!important;
    }
    
    
    
    
    .header .logo .logo__title{
        color: black;
    }
    .body--ny .footer, .body--ny .layout-base {
        background-image: none;
    }
    
    .navigation-header, .navigation-tabs__inner{
        background-image: url(https://i.postimg.cc/RhKR81RQ/1619556535-10-phonoteka-org-p-effekt-hbez-fona-11.png);
    }
    
    
    .logo__left, .logo__right, .logo__center {
        background-image: url(https://www.publicdomainpictures.net/pictures/270000/nahled/bright-red-background-1536690867Ypt.jpg);
        background-repeat: no-repeat;
        height: 66px;
    }
    
    
    /*Спрайты*/
    
    .menu0-item__icon {
        background: transparent url(https://i.postimg.cc/63SgdVd2/menu-icons-sprite-fca3b753.png) no-repeat;
    }
    
    .menu0-item__icon--tests{
        background-position: 0 -160px;
    }
    
    .menu0-item__icon--portfolio{
        background-position: -32px 0;
    }
    
    .menu0-item__icon--schedule{
        background-position: -32px -32px;
    }
    
    .menu0-item__icon--profile{
        background-position: 0 -192px;
    }
    
    /*--------------*/

    .logo:hover {
        border-radius: 0 30px 0 30px;
        transition: 0.4s;
    }
    
    .logo__right{
        display: none;
    }
    
    .logo__center {
        width: auto;
    }
    
    .header .logo .logo__subtitle{
        color: #2C0F0C;
    }
    .ej-accordion--expanded{
        background-color:#2A2A2A!important;
        color: white;
        border: 1px solid #381022;
        border-radius: 10px;
    }
/*     .ej-accordion--expanded:hover{
        transform: scale(1.1);
        transition: 0.4s;
    } */

    .page-title{
        font-size: 20px;   
        color:#20B2AA!important; 
    }

    .dnevnik-day--holiday .dnevnik-day__header{
        background-color: #1E0505!important;
        box-shadow: none;
    }
    .navigation-tabs__items .active{
        background-color: #2A2A2A!important;
        color: #F4A016;
    }
    .menu0 .menu0-item--active .menu0-item__title{
        color: #F4A016!important;
    }
    
    
    
    .navigation-tabs__period span.navigation-tabs-label{
        color: #F0EAD6;
    }
    
    
    
    
    /*Успеваемость*/
    .messages-item__author {
        color: white;
        background-color: inherit;
    }

    .ej-slider__wrap{
        background-color: inherit;
    }
    
    .navigation-tabs__period{
        cursor: pointer;
        margin-bottom: 1px;
        display: flex;
        width: auto;
        justify-content: center;
        padding: 0 16px 0 16px;
    }
    
    
    
    
    .login-form__body{
        background-color: #2A2A2A!important;
        border-radius: 10px;
    }
    
    #inputrFCiwqz_wxh, #inputDSH1QMiOw4x{
        background-color: #2A2A2A!important;
        color: #F4A016;
        border: 1px solid #CC390B!important;
    }
    
    
    
    .widget {
        background-color: #2A2A2A!important;
        border: 0;
        color: wheat;
    }   
    
    .e9RoOsVun9vmXykpRAVW{
        color: #3EA1DF;
    }
    
    .widget__more, .filter-panel{
        background-color: #2A2A2A!important;
        border: 0;
    }
    
    .cells .cell.trselect {
        background-color: #68c88887!important;
    }
    
    .cell--hover {
        background-color: #c4b2646e!important;
    }

    
    .fulldate {
        padding: 30px 0!important;
        height: 100%;
    }

    
    .ej-event {
        border: none;
        color: #94A9FF;
    }
    
    .schedule__day__content, .ej-events--mini .ej-events__timeline:after, .ej-events--mini .ej-events__timeline:before {
        border: 1px solid #625F57;
    }
    
    .ej-event:after {
        background: none;
    }
    
    .menu2, tr .left, span.selection-title, .selection a, .schedule__day__content, .notice, table.ej-table thead tr{
        background-color: #1D1D1D!important;
        color: wheat!important;
    }
    
    
    .calendar-days, .calendar-dayweeks{
        background-color: inherit;
    }  
    
    .calendar-day{
        border: 1px solid #f5deb375;
    }

    
    .trhover {
        background-color: #878f5794!important;
    } 
    


    
    .trLes:hover *{
            background-color: #2d006b!important;
    }
    
    .calendar-event{
        background-color: #715E0A;
    }
    

    
    .dnevnik-day{
        background-color: #2A2A2A!important;
        color: white;
        box-shadow: none;
        border-radius: 4px 4px 7px 7px;
        overflow: hidden;
    }

    
    .dnevnik-day__lessons .dnevnik-lesson, .dnevnik-day__header{
        background-color: #2A2A2A!important;
        border-bottom: 0.5px solid #1D1D1D;
    }


    .ej-event.ej-event--active:after{
        background-image: none;
    }
    

    

    @media screen and (min-width: 897px){
        .navigation-tabs__period:hover{
            border: 1px solid rgba(12,119,195,.25);
            border-radius: 5px 5px 0 0;
        }
        
        .navigation-tabs__period:active{
            border-color: rgba(12,119,195,.5);
        }
    }


    
    @media screen and (max-width: 897px) {
        .navigation-tabs__period{
            background-color: #1D1D1D!important;
            border-top: 1px solid red!important;
        }
    }
}
