style.css:
@-moz-document domain("myanimelist.net") {
/* MyAnimeList Old Navigation (Sidebar) by WOOLAN */

.page-common #myanimelist {
    width: 1210px;
}

#myanimelist .wrapper {
    display: block !important;
    width: 1210px !important;
}

#ad-skin-left, #ad-skin-right {
    width: auto !important;
}

#headerSmall {
    padding: 0 !important;
    height: 0 !important;
}

#headerSmall > a {
    display: none !important;
}

/* Place user menu inside the header and to the far right */
#header-menu {
    float: right;
    position: static !important;
    height: 34px;
}

/* Fix user menu height and colors */
#header-menu.pulldown .header-menu-unit {
    background-color: #2E51A2 !important;
    padding-left: 5px;
}

#header-menu.pulldown .header-menu-unit ul {
    line-height: 1 !important;
}

#header-menu.pulldown .header-menu-unit, #header-menu.pulldown .header-menu-unit > a {
    height: 34px;
    line-height: 34px;
}

#header-menu.pulldown .header-menu-unit > a, #header-menu.pulldown .header-notification > div > a {
    color: #FFFFFF !important;
}

#header-menu.pulldown .header-menu-unit > a:hover, #header-menu.pulldown .header-notification > div > a:hover {
    color: #BBC8E8 !important;
}

#header-menu.pulldown .header-menu-unit > a > i, #header-menu.pulldown .header-notification > div > a > i {
    font-size: 18px !important;
}

#header-menu.pulldown .header-menu-unit > a > i.fa-list, #header-menu.pulldown .header-notification > div > a > i {
    line-height: 36px !important;
}

#header-menu.pulldown .header-menu-unit > a > i.fa-envelope {
    line-height: 34px;
}

#header-menu.pulldown .header-menu-unit .header-list-dropdown {
    left: -80%;
}

#header-menu.pulldown .header-menu-unit.header-profile .header-profile-button {
    display: none;
}

#header-menu.pulldown .border {
    display: none;
}

/* Unread messages/notifications bubbles */
#header-menu.pulldown .header-menu-unit.header-message > .has-unread:after {
    left: auto !important;
    right: 0;
    top: 3px;
    padding: 1px 3px;
    border: 0 !important;
}

#header-menu.pulldown .header-menu-unit.header-notification > .has-unread:after {
    left: auto !important;
    right: 0;
    top: 3px;
    padding: 1px 3px;
    border: 0 !important;
}

/* Move login/signup buttons inside the header */
#header-menu .header-menu-login {
    height: 34px;
    margin-right: 10px;
    line-height: 34px;
}

/* Re-color login and signup buttons */
.header-menu-login .btn-login {
    background-color: #e1e7f5 !important;
    border: #e1e7f5 2px solid !important;
    margin-top: 3px;
}

.header-menu-login .btn-signup {
    background-color: #5a79c1 !important;
    border: #5a79c1 2px solid !important;
    margin-top: 3px;
}

#menu_right {
    margin-right: 50px;
    background-color: #2e51a2 !important;
}

/* Add old logo to header */
.page-common #menu {
    width: 100%;
    background: #2E51A2 url("http://i.imgur.com/gUI8QEw.png") no-repeat scroll left top !important;
}

#contentWrapper {
    float: left;
    width: 1060px;
}

/* Fixed annoying padding in tab navigation */
#horiznav_nav {
    padding-bottom: 2px !important;
}

/* Menu placement and look and feel */
#menu_left {
    margin-top: 34px;
    margin-bottom: 25px;
    width: 150px !important;
    background-color: #FFFFFF !important;
}

#menu_left ul#nav {
    width: 150px;
}

#menu_left ul#nav li {
    float: none;
    width: 100%;
    background-color: transparent;
}

#menu_left ul#nav li a {
    background-color: #FFFFFF !important;
    color: #444444 !important;
}

#menu_left ul#nav > li > a {
    border-bottom: 1px solid #999999 !important;
    font-size: 11px;
    height: 30px !important;
    line-height: 40px;
    padding: 0 !important;
}

#menu_left ul#nav > li > a:only-child {
    border-bottom: 0;
    margin-top: 10px;
    height: 25px !important;
    line-height: 25px;
    background-color: #F6F6F6 !important;
    color: #2E51A2 !important;
}

#menu_left ul#nav li ul {
    position: static !important;
}

#menu_left ul#nav li ul li {
    border-bottom: 1px solid #FFFFFF !important;
}

#menu_left ul#nav li ul li a {
    padding: 3px 0px 1px 0px !important;
    background-color: #F6F6F6 !important;
    color: #2E51A2 !important;
}

#menu_left ul#nav li ul li > a:hover, #menu_left ul#nav li .edit a:hover, #menu_left ul#nav > li.hover > a:only-child {
    background-color: #2E51A2 !important;
    color: #F6F6F6 !important;
}

#menu_left ul#nav li .edit a {
    padding-right: 5px !important;
}

#menu_left ul#nav li > ul {
    display: block !important;
}
}
