style.css:
@namespace url(http://www.w3.org/1999/xhtml);

/* border color/size already looks good on superuser */
@-moz-document domain("stackoverflow.com"), domain("serverfault.com") {
    .post-tag { border: 1px solid #37607D !important; }
}

/* make lots of things curvy (and adjust spacing a little) */
@-moz-document domain("stackoverflow.com"),
               domain("superuser.com"),
               domain("serverfault.com") {

    .votes { -moz-border-radius: 7px; }
    .status { -moz-border-radius: 7px; }
    .post-tag {
      /* remove the 0px if you don't like the tag shape */
      -moz-border-radius: 7px 0px !important;
      text-align: center;
      line-height: 1.5 !important;
      display: inline-block;
      padding-top: 1px !important;
      padding-bottom: 1px !important;
      min-width: 30px;
    }
    #tabs a { -moz-border-radius: 7px 7px 0 0; }
    .nav li { -moz-border-radius: 5px; }
    .stats { -moz-border-radius: 7px; }
    .tabs-question-user a {
      -moz-border-radius: 5px;
      padding-top: 3px !important;
    }
    .date-span { -moz-border-radius: 7px; }
    .welovestackoverflow { -moz-border-radius: 7px; }
}
