style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.burbuja.info") {

/*    bloque thanks   */ 
div[id^="post_thanks_box_"]
{
color:green!important;
font-family: serif ! important; 
font-weight: normal !important;
font-size: 10pt !important ;
}
div[id^="post_thanks_box_"] td.alt2
{
display:none!important;
}

div[id^="post_thanks_box_"] td.alt1
{
background:#efe !important;
}

div[id^="post_thanks_box_"] td.alt1>div
{
display:none!important;
}

div[id^="post_thanks_box_"] tr td.alt1:active>div
{
display:table-cell !important;
color:green!important;
font-family: serif ! important; 
font-weight: normal !important;
font-size: 10pt !important ;
}
div[id^="post_thanks_box_"] tr td.alt1:active>div>a
{
color:green!important;
font-family: serif ! important; 
font-weight: normal !important;
font-size: 10pt !important ;
}

/*    bloque groan */ 
div[id^="post_groan_box_"]
{
color:red!important;
font-family: serif ! important; 
font-weight: normal !important;
font-size: 10pt !important ;
}
div[id^="post_groan_box_"] td.alt2
{
display:none!important;
}
div[id^="post_groan_box_"] td.alt1
{
background:#fee !important;
}

div[id^="post_groan_box_"] td.alt1>div
{display:none!important}

div[id^="post_groan_box_"] tr td.alt1:active>div
{
display:table-cell !important;
}

div[id^="post_groan_box_"] tr td.alt1:active>div>a
{
color:red!important;
font-family: serif ! important; 
font-weight: normal !important;
font-size: 10pt !important ;
}

}
