@-moz-document domain("electronics-tutorials.ws") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.footer-wrap #footer-menu>li>a
{
	text-transform: capitalize!important;
}

#main-menu>li,

.body-cont .content-wrap .tiles-list ul li .hovered-box,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.display-off {
    display: block!important;
}

.body-cont .content-wrap .post-wrap.box {
    display: inline-block;
}

/*___ Fonts ________________________________________________________________________*/

.comments-wrap .comment-list-cont .comment-list li .comment-author cite,
.mtxt,

body h3
{
	font-size: 3rem!important;
}

.comments-wrap .comment-list-cont .comment-list li .comment-text
{
	font-size: 2.5rem!important;
}

.body-cont .content-wrap .desc p,
.ntxt,
.single-page .single-wrap .content-cont p,
.single-page .single-wrap .content-cont table tr td,
.single-page-content .widgets .category ul li
{
	font-size: 2.2rem!important;
}

.body-cont .content-wrap .tiles-list ul li a p
{
	font-size: 1.8rem!important;
}

selector
{
	line-height: 2.5rem!important;
}

selector
{
	line-height: 2.1rem!important;
}

/*___ Remove Items _________________________________________________________________*/

.body-cont .content-wrap .sidebar-home,
.double-ads,
.footer-ad-container,
.home-sticky-ad-wrapper,
.home-top-ads,
.mobile-latest-tutorials-wrapper,

[id*='div-gpt-ad-'],
[id*='gpt_unit_/']
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

selector
{
    top: 10px!important;
}

/*___ Margins ______________________________________________________________________*/

.body-cont .content-wrap .tiles-list,
.body-cont .content-wrap .post-wrap.box,
.comments-wrap .comment-list-cont .comment-list>li
{
	margin: 1.2rem!important;
}

selector
{
	margin: 3px 6px!important;
}

/*___ Paddings _____________________________________________________________________*/

xxx
{
	padding: 12rem!important;
}

selector
{
	padding: 3px 26px!important;
}

/*___ Heights ______________________________________________________________________*/

selector
{
	max-height: none!important;
}

selector
{
	height: auto!important;
}

.body-cont .content-wrap .tiles-list ul li {
    height: 11rem;
}

/*___ Widths _______________________________________________________________________*/

#main,

.container
{
	max-width: none!important;
}

.col-md-6
{
	width: auto!important;
}

.col-md-8,
.container
{
	width: 98%!important;
}

.body-cont .content-wrap .post-wrap.box {
    width: 47%;
}

.col-md-3 {
    width: 18%;
}

}

