style.css:
@-moz-document url("https://dl.psim.us/teambuilder"), url("https://play.pokemonshowdown.com/teambuilder") {
.teamlist .team {
    	width: 470px;
	}
	.teamlist .team small {
    	padding: 0 10px 0 10px;
	}
}

@-moz-document url-prefix("https://calc.pokemonshowdown.com") {
body {
    background-color: black;
    color: cyan;
	}
  fieldset {
    background-color: black;
    border: 1px solid red;
	}
  .btn {
    border: solid 1px #00ff00;
    color: orange;
    background: #36393f;
	}
  textarea.import-team-text {
    background: #36393f;
    color: orange;
	}
  .visually-hidden:checked+.btn {
    color: magenta;
    background: black;
	}
  .locked-move:checked+.btn {
    background: black;
    color: red;
	}
  input.base.calc-trigger {
    background: #36393f;
    color: orange;
    border: 1px solid #00ff00;
	}
  input.ivs.calc-trigger {
    background: #36393f;
    color: orange;
    border: 1px solid #00ff00;
	}
  input.evs.calc-trigger {
    background: #36393f;
    color: orange;
    border: 1px solid #00ff00;
	}
}

