[class^="ElementDialog__ICTHeader-"], [class*=" ElementDialog__ICTHeader-"]
{background:transparent;color:white}

[class^="ElementDialog__StatHeading-"], [class*=" ElementDialog__StatHeading-"]
{color:white;}

[class^="ElementDialog__ElementTypeLabel-"], [class*=" ElementDialog__ElementTypeLabel-"]
{background:transparent;padding:0;margin-bottom:1rem;color:#FFFFFF;font-size:1.3rem}


/* Transfers page */

[class^="Scoreboard__ScoreboardWrap-"], [class*=" Scoreboard__ScoreboardWrap-"]
{background:transparent;box-shadow:none}

[class^="Scoreboard__Heading-"], [class*=" Scoreboard__Heading-"]
{color:#d1d1d1}

[class^="Scoreboard__Value-"], [class*=" Scoreboard__Value-"]
{background-image: linear-gradient(to right, rgb(0, 255, 135), rgb(2, 239, 255));
    background-clip: text;-webkit-text-fill-color: transparent;   -webkit-background-clip: text;
    text-shadow: 0px 1px 60px rgba(1, 247, 185, 0.8);}

[class^="PageHeader__StyledPageHeader-"], [class*=" PageHeader__StyledPageHeader-"]
{display:none}

[class^="FieldRenderers__SearchInput-"], [class*=" FieldRenderers__SearchInput-"]
{background:#303030}

[class^="FieldRenderers__SearchIconWrap-"], [class*=" FieldRenderers__SearchIconWrap-"]
{background:#191919}

[class^="ElementList__ElementsShown-"], [class*=" ElementList__ElementsShown-"]
{background:none;color:#d1d1d1}

[class^="ElementList__ElementsShown-"] strong, [class*=" ElementList__ElementsShown-"] strong
{background:none;color:#FFFFFF}

[class^="FieldRenderers__Select-"], [class*=" FieldRenderers__Select-"]
{background: rgba(0, 0, 0, 0.01) none repeat scroll 0% 0%;}
    
    
  /* Status page */  
    
    [class^="EventInfo__Heading-"], [class*=" EventInfo__Heading-"]
    {color:#d1d1d1}
    
    [class^="EventInfo__InfoItem-"], [class*=" EventInfo__InfoItem-"]
    {border-color:rgba(255,255,255,0.2)}
    
    [class^="EventInfo__StyledEventInfo-"], [class*=" EventInfo__StyledEventInfo-"]
    {background:transparent;color:#d1d1d1;border: 1px solid #303030;}
    
    [class^="EventInfo__InfoList-"], [class*=" EventInfo__InfoList-"]
    {background:#131313}
    
    [class^="EventStatus__EventStatusInfo-"], [class*=" EventStatus__EventStatusInfo-"]
    {background:transparent}
    
    [class^="EventStatus__EventStatusLeagues-"], [class*=" EventStatus__EventStatusLeagues-"]
    {background:transparent}
    
    [class^="EventTopElement__TopElementValue-"], [class*=" EventTopElement__TopElementValue-"]
    {background:transparent}
    
    [class^="EventTopElement__TopElementName-"], [class*=" EventTopElement__TopElementName-"]
    {background:transparent}
    
 
    /* Home page*/
    [class^="ScoutLeader__ScoutContent-"], [class*=" ScoutLeader__ScoutContent-"]
    {background:#191919}
    
    [class^="ScoutLeader__ScoutTitle-"], [class*=" ScoutLeader__ScoutTitle-"]
    {color:#FFFFFF}
    
    [class^="ScoutLeader__ScoutDate-"], [class*=" ScoutLeader__ScoutDate-"]
    {color:#d1d1d1}
    
    [class^="ScoutLeader__ScoutImgWrap-"], [class*="ScoutLeader__ScoutImgWrap-"]
    {filter:grayscale(100)}
    
    [class^="styles__ScoutTitle-"], [class*="styles__ScoutTitle-"]
    {color:#d1d1d1}
    
    [class^="styles__StyledRelatedArticles-"], [class*="styles__StyledRelatedArticles-"]
    {border-top:1px solid rgba(255,255,255,0.1)}
    
    [class^="Scout__Social-"], [class*="Scout__Social-"]
    {filter:grayscale(100);background:none}
    
    [class^="Scout__SocialLinks-"], [class*="Scout__SocialLinks-"]
    {background:none}
    
    [class^="Collapsible__Button-"], [class*="Collapsible__Button-"]
    {filter: invert(100) grayscale(100);color: rgba(0, 0, 0, .9);}
    
    
}
