style.css:
@-moz-document url-prefix(https://doc.rust-lang.org/stable/rust-by-example),
  url-prefix(https://doc.rust-lang.org/rust-by-example),
  url-prefix(https://doc.rust-lang.org/rustc/),
  url-prefix(https://doc.rust-lang.org/book/),
  url-prefix(https://doc.rust-lang.org/1.30.0/book/),
  regexp("https?:\\/{2}doc.rust-lang.org\\/(\\d+.?)+\\/book\\/"),
  url-prefix(https://doc.rust-lang.org/reference/),
  url-prefix(https://doc.rust-lang.org/cargo/),
  url-prefix(https://rust-lang.github.io/rustup/),  
  url-prefix(https://rust-unofficial.github.io/patterns/),
  url-prefix(https://rust-lang.github.io/async-book/),
  url-prefix(https://rust-lang.github.io/api-guidelines/),
  url-prefix(https://rustwasm.github.io/docs/) {
    #page-wrapper .page #content main {
      font-size: 1.4em;
      max-width: 75%;
      position: relative;
      left: -1em;
    } 
    #page-wrapper .page  #content main pre code {
      white-space: break-spaces;
    }
}
@-moz-document url-prefix(https://docs.rs/toolshed/) {
  .rustdoc .sidebar {
    right: 0;
    left: auto;
    font-size: 1.2em;
    min-width: 10em;
    width: 12.5em;
    flex-shrink: 0;
  }
  .rustdoc #main {
    font-size: 1.4em;
    padding-left: 0;
    margin-left: 3%;
    max-width: 79%;
  }
  .rustdoc #main .docblock .example-wrap pre.rust .comment {
    white-space: normal;
    word-break: break-all;
  }
  .rustdoc .docs-rs-footer {
    right: 0 !important;
    bottom: 0 !important;
  }
}
@-moz-document url-prefix(https://doc.rust-lang.org/),
	url-prefix(https://docs.rs/derive_builder/),
	url-prefix(https://docs.rs/delegate/),
	url-prefix(https://docs.rs/ambassador/),
	url-prefix(https://docs.rs/async-std/),
  url-prefix(https://docs.rs/toml/),
	url-prefix(https://docs.rs/syn/),
  url-prefix(https://docs.rs/tracing/),
  url-prefix(https://docs.rs/tracing-subscriber/),
  url-prefix(https://docs.rs/tracing-appender/)  
 {
    .rustdoc {
      flex-direction: row-reverse;
    }
    .rustdoc .sidebar {
      right: 0;
      left: auto;
      font-size: 1.2em;
      min-width: 10em;
      width: 12.5em;
      flex-shrink: 0;
    }
    .rustdoc main {
      font-size: 1.4em;
      padding-left: 0;
      display: flex;
      flex-flow: row nowrap;
      justify-content: flex-end;
    }
    .rustdoc main .width-limiter {
      margin-right: initial;
      width: 93%;
      max-width: initial;
    }
    .rustdoc main .docblock .example-wrap pre code {
        white-space: pre-wrap;
    }
}
@-moz-document url-prefix(https://www.ralfj.de/blog/2018/08/22/two-kinds-of-invariants.html) {
  body {
  	background-color: hsl(60, 9%, 87%);
  }
  #-frame {
    margin: 0 0 0 5em;
  }
  #-navi {
    width: 10em;
    margin-left: .5em;
  }
  #-navi ul > li > a {
    width: 100%;
  }
  #-content p {
    font-size: 1.5em;
    font-weight: normal;
    font-family: "Open Sans", sans-serif;
  }
  #-content figure pre {
    font-size: 1.3em;
  }
}
@-moz-document url-prefix(https://github.com/) {
  section[aria-labelledby="file-name-id"],
  #readme {
    background-color: hsl(60, 9%, 87%);
  }
  .markdown-body.entry-content {
  	margin: 0 0;
    max-width: initial;
    font-size: 1.5em;
  }
  .js-code-nav-container .blob-code-inner {
  	font-size: 17px;
    white-space: pre-wrap;
  }
}
@-moz-document regexp("^https?:\\/{2}\\w+\\.github\\.io\\/posts\\/.*$") {
  #content {
    width: 90%;
    font-size: 1.5em;
  }
}
@-moz-document regexp("^https?:\\/{2}web\\.archive\\.org\\/web\\/\\d+\\/https?:\\/{2}xion\\.io\\/post\\/code\\/.*$") {
  #wm-ipp-base {
  	display: none !important;
  }
  body {
    background-color: hsl(60, 9%, 87%);
  }
  #disqus_thread,
  body > aside,
  body > main > nav,
  body > main > article .tag-cloud,
  body > main > footer {
    display: none;
  }  
  body > main {
    width: 92%;
  	left: 50%;
    transform: translateX(-50%);
  }
  body > main > article {
  	margin: 0;
  }
  body > main > article > div  p {
  	font-size: 1.4em;
    line-height: 1.4;
  }
  body > main > article > div .highlight  pre {
    font-size: 1.2em;
    line-height: 1.5;
  }    
}
@-moz-document url-prefix(https://www.novatec-gmbh.de/en/blog/) {
  #site #site-header {
  	height: 2.5em !important;
  }
  #site-main .page-content {
    background-color: #e1e1db !important;
  }
  #site-main .article {
    max-width: 100% !important;
  }
  #site-main .article .copy-text p,
  #site-main .article .copy-text ul {
    font-size: 20px;
  }
  #site-main .article .copy-text  .crayon-syntax .crayon-pre {
    font-size: 16px;
  }
  #site-main .sidebar {
    display: none;
  }
}
@-moz-document url-prefix(http://cliffle.com/blog/) {
  .theme-base-0e {
    background-color: #e1e1db !important;
  }
  .theme-base-0e .sidebar {
    width: auto;
  }
  .theme-base-0e .sidebar,
  .theme-base-0e .sidebar .container.sidebar-sticky {
    position: static;
  }
  .theme-base-0e .sidebar .container.sidebar-sticky .sidebar-nav {
    flex-direction: initial;
  }
  .theme-base-0e .content.container {
    margin-left: 5%;
    margin-right: 5%;
    max-width: initial;
    font-size: 1.5em;
  }
}
@-moz-document url-prefix(https://veykril.github.io/tlborm/) {
  #page-wrapper #content > main {
    max-width: 85%;
    font-size: 1.3em;
  }
  #page-wrapper #content > main .parse-table {
    width: auto;
    margin-left: -1%;
    margin-right: -1%;
  }
  #page-wrapper #content > main pre > .result {
    white-space: pre-wrap;
  }
}
@-moz-document url-prefix(https://play.rust-lang.org/) {
	#playground [data-test-id="output"] [data-test-id="output-execute"] [data-test-id="output-stderr"] pre code,
    #playground [data-test-id="output"] [data-test-id="output-execute"] [data-test-id="output-stdout"] pre code {
      tab-size: 25px;
      white-space: pre-wrap;
    }
}
