style.css:
@-moz-document domain("typeracer.com") {
  table.inputPanel span {
    letter-spacing: 0.25px;
    word-spacing: 2px;
    line-height: 28px;
    color: /*[[new]]*/ !important;
    font-size: /*[[size]]*/px !important;   
    text-decoration: none;
    font-family: '/*[[font]]*/' !important;
  }
  table.inputPanel span:nth-child(2) {
    color: /*[[active]]*/ !important;
    border-bottom: 1px solid /*[[active]]*/ !important;
  } 

  table.inputPanel span:nth-child(1) {
    color: /*[[typed]]*/ !important;
  }

  .txtInput {
    border: none !important;
    background: #333333 !important;
    border-bottom: 2px solid /*[[new]]*/ !important;
    font-family: '/*[[font]]*/' !important;
    font-size: 22px !important;
  }
  .txtInput:focus {
    border-bottom: 2px solid /*[[active]]*/ !important;
    outline: none !important;
  }
}
