==UserStyle== */

/* i really want this to be global */
:root, html, body, blockquote {
	font-family: "Computer Modern", "Work Sans", "Quay Sans ITC Std", "Foundry Sans", ibm-plex-sans, -apple-system, system-ui, BlinkMacSystemFont, HelveticaNeue, Helvetica, Roboto, sans-serif !important;
  font-style: normal !important;
  /*font-size: xx-small !important;*/
  font-size-adjust: none !important;
  text-size-adjust: none !important;
	/* color: rgba(0, 13, 29, 0.6) !important; */
	/* line-height: 2.53333rem !important; */
	/* margin-top: 0.266667rem !important; */
	-webkit-font-smoothing: none !important;
	-moz-osx-font-smoothing: grayscale !important;
	-font-smooth: never !important;
	-text-rendering: optimizeSpeed !important;
	/*text-align: justify !important;*/
	text-decoration: none !important;
	/*text-shadow:0 1px 0 rgba(102, 102, 102, .58)!important;*/
	/*-webkit-tap-highlight-color: transparent !important;
	-webkit-box-sizing: border-box !important;
	-webkit-box-flex: 1 !important;
	-moz-box-sizing: border-box !important;
	box-sizing: border-box !important;*/
}
blockquote, input, input[type=button], input[type=number], input[type=password], input[type=reset], input[type=submit], input[type=text], p, td, th, textarea, li, ul, button {
	font-family: "Work Sans", "Quay Sans ITC Std", "Foundry Sans", ibm-plex-sans, -apple-system, system-ui, BlinkMacSystemFont, HelveticaNeue, Helvetica, Roboto, sans-serif !important;
}
h1, h1>div, h1>span, .h1, #h1, h2, h2>a, h3, h4, caption, th, label {
	font-family: "Nekst-Regular", "Work Sans", "Quay Sans ITC Std", "Foundry Sans", ibm-plex-sans, -apple-system, system-ui, BlinkMacSystemFont, HelveticaNeue, Helvetica, Roboto, sans-serif !important;
}

#main-window-body, .body-156, .fullSpace-274, .linkStyles-296, .spanWrapper-280, .labelRootStyles-285, .truncatableText-284,
.content, .content-html {
	font-family: "Work Sans", "Quay Sans ITC Std", "Foundry Sans", ibm-plex-sans, -apple-system, system-ui, BlinkMacSystemFont, HelveticaNeue, Helvetica, Roboto, sans-serif !important;
}

a, a:link, a:visited {
	/* font-family: inherit !important; */
	text-decoration: none !important;
}
a:hover {
	/* font-family: inherit !important; */
	text-decoration: underline !important;
}
pre, div>pre, pre#banner, x-screen, div#PlainText, .PlainText, .priceFinal, .code, #code, .pf-c-log-viewer__text {
	font-family: "Intel One Mono", "Roboto Mono", "SpaceMono NFM", "Cascadia Mono", "Courier New", source-code-pro, Consolas, ui-monospace, Menlo, Monaco, monospace !important;
	text-decoration: none !important;
}
