style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.asahi.com") {

  #TopBnr, div.BnrLst, div.BnrLnkSbs, div.BnrLnkSbs,
  div.BnrPr, dl.PrInfo, #impAswfid,
  div.BnrLnkLct, div.BoxAdGeneral, div.Box1, div.Box2, div.AdsBox,
  #flashContent, ul.BnrLnk, #Sub {
    display: none !important;
  }
  #Header { height:auto !important; }

  div#Contents {
    background-image: none !important;
  }
  ul#PageTop li {
    background-color: white !important;
  }

  #Logo, div#MainWrap, div#MainWrapInner, div#ScrlTarget {
    margin-right: 0 !important;
    margin-bottom: 0 !important;
  }
  div#MainInner {
    margin-right: 15px !important;
  }
  #Logo > a {
    padding-top: 0px !important;
  }
}
