style.css:
@-moz-document domain("eljur.ru") {
* {
        text-shadow: 0 0 black!important;
    }
    
    footer{
        display: none;
    }
    .logotype__image{
        transition: 3s;
    }
    
    .logotype__image:hover{
        transition: 3s;
        transform: rotate(360deg);
    }
    .body--ny .footer, .body--ny .layout-base, .ej-event:after {
        background-image: none;
        height: 0px;
        
    }

    .submit, .button.disabled, .button[disabled], .button.disabled, .button[disabled] {
        background: #1D1D1D!important;
        cursor: pointer;
        border: 1px solid #840505!important;
    }

    .submit:hover, .messages-item:hover, .button[disabled]:hover, .button.disabled:hover, .button[disabled]:hover, .button.disabled:hover {
        border: 1px solid #fff0!important;
        color: #1D1D1D!important;
        background: #D52728!important;
    }

    textarea, .footer-menu-icon, .ezhXia3085KYemTFB_N4, .yXIY3sqXtSRbgR6N_5MD, .ej-panel, .ej-table tbody tr td, tfoot, .xfmmft_rUe6MHwduow2f, .calendar-day, .calendar-dayweek, .UeM_u8vJ8IwmZff4lQSg, .context__block, .trLes, .trTotal, .ej-event__room, .ej-select__header, .ej-event, .ej-accordion, .messages-item:hover, .board-item {
        background-color: #2A2A2A!important;
    }

    .main, .schedule-week, .misssum, .m-scroll, .misssum tr td, .login-form, .layout-base, #dnevnik-header, .layout-main .layout-content .grid .grid-group .grid-header .inside, .messages-item__author, .cells, .cells_header, .grid__scrollbar, .ej-select__header, header, main {
        background-color: #1D1D1D!important;
    }

    .field.disabled, .field.disabled:focus, .field.disabled:hover, .field[disabled], .field[disabled]:focus, .field[disabled]:hover {
        background-color: #1D1D1D!important;
    }

    .ej-form-label span, .navigation-tabs-label, .ej-form__header, .typography h5, .widget__header, .ej-accordion__title, .dnevnik-day__title, .field, .yXIY3sqXtSRbgR6N_5MD, .eR069Hg7azuJYELDr4Fb, .sbS7qnikFTmjnDJ2bL6T, .ej-panel, #table-miss-sum, tr, .strong, .UeM_u8vJ8IwmZff4lQSg, .calendar div, .context__block, .board-item, .board-item__title, .schedule__day__content__lesson, .columns, .ej-form__title, .ej-form-label, .control-label, input {
        color: white!important;
    }

    .control-checkbox__label, .page-empty, .board-item__dates, .messages-item__link {
        color: wheat;
    }

    .cell, .messages-item, .messages-header, .ej-select__header, .ej-select-popup__inner, #footer, .layout, #contextSettings .context__block, .login-form__body, .container-header-page-tab--1jYmW, .page-tab--1rYXl, .fulldate, .null, .cells_header {
        background-color: #1D1D1D!important;
        color: white!important;
    }

    ::-webkit-scrollbar {
        background-color: #1D1D1D!important;
    }

    ::-webkit-scrollbar-thumb {
        background-color: #42413F!important;
    }

    .layout-main {
        background-color: #1D1D1D;
        border-top: 1px solid #D52728;
/*         border-bottom: 1px solid #D52728; */
        box-shadow: 0 0 20px 0 #D52728;
    }

    .logo {
        box-shadow: 0 0 100px 10px #D52728;
        width: auto;
        height: 62px;
        overflow: hidden;
        transition: 0.4s;
    }

    .logotype__title, .logotype__subtitle {
        color: white!important;
    }

    .navigation-tabs__items .active {
        border-color: #D52728!important;
        background-color: #2A2A2A!important;
        color: #F4A016;
    }

    input.field, .ej-select__input {
        background-color: #2A2A2A!important;
        border-color: #D52728;
    }

    .trselect {
        background-color: #D52728!important;
    }

    .ej-accordion--expanded {
        background-color: #2A2A2A!important;
        color: white;
        border: 1px solid #381022;
        border-radius: 10px;
    }

    .page-title {
        font-size: 20px;
        color: #20B2AA!important;
    }

    .dnevnik-day--holiday .dnevnik-day__header {
        background-color: #1E0505!important;
        box-shadow: none;
    }

    .menu0 .menu0-item--active .menu0-item__title {
        color: #F4A016!important;
    }

    .navigation-tabs__period span.navigation-tabs-label {
        color: #F0EAD6;
    }

    .messages-item__author {
        color: white;
        background-color: inherit;
    }

/*     .ej-slider__wrap, ej-slider, .grid__scrollbar {
        height: 0px!important;
        background-color: black!important;
        background-image: none;
        padding: 0;
    }
    
    #shadow-root, style{
        display: none;
    } */

    .navigation-tabs__period {
        margin-bottom: 1px;
        width: auto;
        padding: 0 16px 0 16px;
    }

    .login-form__body {
        background-color: #2A2A2A!important;
        border-radius: 10px;
    }

    #inputrFCiwqz_wxh, #inputDSH1QMiOw4x {
        background-color: #2A2A2A!important;
        color: #F4A016;
        border: 1px solid #CC390B!important;
    }

    .widget, .widget__more, .filter-panel {
        background-color: #2A2A2A!important;
        border: 0;
    }

    .cells .cell.trselect {
        background-color: #68c88887!important;
    }

    .cell--hover {
        background-color: #c4b2646e!important;
    }

    .fulldate {
        padding: 30px 0!important;
        height: 100%;
    }

    .ej-event, .schedule__day__content, .ej-events--mini .ej-events__timeline:after, .ej-events--mini .ej-events__timeline:before, .ej-event:after, .menu2, tr .left, span.selection-title, .selection a, .notice, table.ej-table thead tr {
        background-color: #1D1D1D!important;
        color: wheat!important;
    }

    .calendar-days, .calendar-dayweeks {
        background-color: inherit;
    }

    .calendar-day {
        border: 1px solid #f5deb375;
    }

    .trhover {
        background-color: #878f5794!important;
    }

    .trLes:hover * {
        background-color: #2d006b!important;
    }

    .calendar-event {
        background-color: #715E0A;
    }

    .dnevnik-day {
        background-color: #2A2A2A!important;
        color: white;
        box-shadow: none;
        border-radius: 4px 4px 7px 7px;
        overflow: hidden;
    }

    .dnevnik-day__header {
        background-color: #2A2A2A!important;
    }
    
    .dnevnik-lesson:nth-child(odd){
        background-color: #2A2A2A!important;
    }
    
    .dnevnik-lesson:nth-child(2n){
        background-color: #222!important;
    }
    
    .colors4 {
        color: #24fbee !important;
    }

    .ej-event.ej-event--active:after {
        background-image: none;
    }


    @media screen and (max-width: 897px) {
        .navigation-tabs__period {
            background-color: #1D1D1D!important;
            border-top: 1px solid red!important;
        }
    }
}
