style.css:
@-moz-document domain("www.mangaworld.ac") {
#reader img {    
    width: auto;
    height: 95vh;
    max-height: 95vh;
    border-radius: 0.75rem;
    box-shadow: 1rem 1rem rgba(0,0,0,0.1);
    border: 2px solid black;
    margin-bottom: 2rem;
}

.w-100#reader img {
    width: 100%;
    height: fit-content;
    max-height: fit-content;
}

.btn, .custom-select {
    border-radius: 2rem;
    height: 2.5rem;
    font-size: 0.9rem;
    padding: 0 1.5rem;
}

.btn.btn-primary {
    background-color: white;
    border: 1px solid black;
    color: black;
    transition: all 250ms ease;
}

.btn.btn-primary:hover {
    background-color: black;
    color: white;
}

.prev, .next {
    padding-right: 4rem !important;
}

.top-wrapper { 
	display: none;
}

.alert {
    display: none;
}

.fa-caret-left:before {
    content: "\f053";
    font-size: 0.75rem;
}

.fa-caret-right:before {
    content: "\f054";
    font-size: 0.75rem;
}

#bookmark-dropdown-button:after {
    display: none;
}

#site-footer {
    display: none;
}
}
