style.css:
@-moz-document url(https://wordcounter.net/) {
	body {
    	color: #f5f5f5;
    	background: #000000;
	}/* This (body) only controls the very background of the page.*/
	.navbar-default {
 	   background-color: #000000;
	    border-color: #3c3c3c;
	}/* .navbar-default is the top bar/header thing.*/
  #top_counter {
    background-color: #262626;
	}/* #top_counter is the panel containing the word count at the top of the page; also contains the "go basic" and "options" buttons.*/
  .panel-default {
    border-color: #3c3c3c;
	}/* .panel-default only controls the outline of the button bar.*/
	.panel-default>.panel-heading {
    color: #f5f5f5;
    background-color: #000000;
    border-color: #3c3c3c;
	}/* .panel-default>.panel-heading only controls the very background of the button bar.*/
  .btn-default {
    color: #f5f5f5;
    background-color: #262626;
    border-color: #3c3c3c;
	} /* .btn-default controls the button colors. */
  .form-control {
    color: #f5f5f5;
    background-color: #000;
    background-image: none;
    border: 1px solid #3c3c3c;
	} /* .form-control controls the entire user-input text section. */
  div.grippie {
    background: #545454 no-repeat scroll center 2px;
    border: 1px solid #3c3c3c;
  } /* div.grippie is just the thing at the bottom that lets you resize the user-input text section.*/
  .panel-footer {
    background-color: #000000;
  } /* .panel-footer controls the white bar at the bottom of the user-input text section under the grippie */
  .well {
    background-color: #000000;
    border: 1px solid #3c3c3c;
  } /* .well controls the text box at the bottom of the page telling you about wordcounter.net */
  .list-group-item {
    background-color: #000;
    border: 1px solid #3c3c3c;
  } /* .list-group-item controls for the body sections of the column bars at the side. */
  /* COLORS: 
  f5f5f5 is just shy of white;
  3c3c3c is the lightest gray;
  262626 is a darker gray;
  000 or 000000 (same thing) is plain black
  */
}
