style.css:
/* i really want this to be global */
@-moz-document domain('stockbit.com'), url-prefix('https://stockbit.com/')
{
  body {
    color: #ebebeb !important;
    background: #333 !important;
  }

  .general-content {
    border: 1px solid #333333 !important;
  }

  #main-wrapper * {
    background: transparent;
  }

  .ant-table-tbody > tr > td,
  header {
    border-bottom: 1px solid #333333;
  }
  .searchbox {
    border: 1px solid #333 !important;
    background: #4e4e4e !important;
    border-radius: 5px !important;
  }

  .header-inside {
    background: #333 !important;
  }

  .nav-inside-child {
    background: #333 !important;
  }

  .trading-login {
    border-left: 1px solid #333333 !important;
  }
  .trading-login:after,
  .nav-inside {
    background: #333333 !important;
  }

  #rightmenu {
    border-left: 1px solid #333333 !important;
    background: #333 !important;
  }

  #rightmenu a i {
    background: #333 !important;
  }

  .wl-trade-buy-button {
    border: 1px solid #333333 !important;
  }

  .watchlist-wrap .ant-table-scroll td,
  .watchlist-wrap .ant-table-scroll th {
    border-left: 1px solid #333333 !important;
  }

  #genhead {
    border: 1px solid #333333 !important;
  }

  .general-content-head {
    border-bottom: 1px solid #333333;
  }

  .trading-menu-box {
    border-left: 1px solid #333333;
    border-bottom: 1px solid #333333;
  }
  .trading-menu-content:before {
    background: #333333;
  }

  .menugroup-title i {
    color: #00ab6b;
  }

  .stream-nav-left a.active {
    color: #00ab6b;
  }

  .menugroup-title span {
    display: inline-block;
    color: #00ab6b;
  }

  .ant-popover-inner {
    background-color: #222222 !important;
    border: solid 1px #333333;
  }

  .popoverpanel .panel-box a {
    border-bottom: solid 1px #333333;
  }

  .popoverpanel .ant-popover-title {
    border-bottom: 1px solid #333333;
  }

  .watchlist-trading a.wl-trade-symbol-name {
    color: #ebebeb;
  }

  .wl-trade-symbol-desc {
    color: #e1e1e1;
  }

  .wl-trade-buy-button {
    border: 1px solid #333333;
  }

  .ant-popover-title {
    color: #ebebeb;
  }

  .ant-menu-item > a {
    color: #ebebeb;
  }

  .company-header {
    border: 1px solid #333333;
  }

  .company-top {
    border-bottom: 1px solid #333333;
  }

  .stream-compose {
    border: 1px solid #333333;
  }

  .stream-timeline {
    border: 1px solid #333333;
  }

  .streamwithreply {
    border-bottom: 1px solid #404040;
  }

  .swipertrending {
    border: 1px solid #333333 !important;
  }

  .trending-box:before {
    background: #333333;
  }

  div#banner-right {
    display: none;
  }

  .widget-ihsg {
    border: 1px solid #333333;
  }

  .card-calendar-title {
    color: rgb(0 171 107);
  }
  .trending-user-title {
    color: #00ab6b;
  }

  .ant-btn {
    color: #aaa;
  }

  .trending-users .trending-user-title em {
    color: #ebebeb;
  }

  .stream-text {
    color: #ebebeb;
  }

  .popoverpanel .panel-box a.unread {
    background: #954949;
  }

  pan.notification-title {
    color: #954949;
  }

  input {
    color: #00ab6b;
  }

  [aria-expanded="true"] #react-autowhatever-1,
  [aria-expanded="true"] #react-autowhatever-autosuggest-metric,
  [aria-expanded="true"] #react-autowhatever-autosuggest-stocks,
  [aria-expanded="true"] #react-autowhatever-basic_ratio,
  [aria-expanded="true"] #react-autowhatever-chartbit_search_page,
  [aria-expanded="true"] #react-autowhatever-metric_maps,
  [aria-expanded="true"] #react-autowhatever-preset_screener_search,
  [aria-expanded="true"] #react-autowhatever-search_global,
  [aria-expanded="true"] #react-autowhatever-search_glossary,
  [aria-expanded="true"] #react-autowhatever-symbol_earnings,
  [aria-expanded="true"] #react-autowhatever-valuation_search_page,
  [aria-expanded="true"] #react-autowhatever-valuation_search_widget,
  [aria-expanded="true"] #react-autowhatever-versus_ratio1,
  [aria-expanded="true"] #react-autowhatever-versus_ratio2,
  [aria-expanded="true"] #react-autowhatever-wladdsymbol,
  [aria-expanded="true"] .react-autosuggest__suggestions-container,
  [aria-expanded="true"] + #react-autowhatever-1,
  [aria-expanded="true"] + #react-autowhatever-autosuggest-metric,
  [aria-expanded="true"] + #react-autowhatever-autosuggest-stocks,
  [aria-expanded="true"] + #react-autowhatever-basic_ratio,
  [aria-expanded="true"] + #react-autowhatever-chartbit_search_page,
  [aria-expanded="true"] + #react-autowhatever-metric_maps,
  [aria-expanded="true"] + #react-autowhatever-preset_screener_search,
  [aria-expanded="true"] + #react-autowhatever-search_global,
  [aria-expanded="true"] + #react-autowhatever-search_glossary,
  [aria-expanded="true"] + #react-autowhatever-symbol_earnings,
  [aria-expanded="true"] + #react-autowhatever-valuation_search_page,
  [aria-expanded="true"] + #react-autowhatever-valuation_search_widget,
  [aria-expanded="true"] + #react-autowhatever-versus_ratio1,
  [aria-expanded="true"] + #react-autowhatever-versus_ratio2,
  [aria-expanded="true"] + #react-autowhatever-wladdsymbol {
    background: #333333 !important;
    border: 1px solid #333333 !important;
  }

  .autosuggest-general li i {
    color: #00ab6b;
  }

  .autosuggest-general li b {
    overflow: inherit;
    color: #ffffff;
  }

  #react-autowhatever-symbol_earnings,
  #react-autowhatever-wladdsymbol {
    width: 832px;
    max-width: 320px;
    max-height: 600px;
  }

  .panel-more-wrap {
    background: #333333;
    border: 1px solid #333333;
    border-top: 0 solid #333333;
  }

  .loaderbar {
    background-color: #333333;
  }

  .ant-card-bordered,
  .card-calendar-header,
  .ant-card-see-all,
  .stream-nav .nav-header,
  .stream-nav-revamp .stream-nav-left-revamp a.active span {
    border: 1px solid #333333;
  }

  .STPnumtarget h2,
  .STPstockstarget h3,
  .STPstockstarget span,
  .STPtarget p,
  .STPnumtarget p,
  .STPresult span,
  .stream-new-action .stream-icon,
  .stream-share-amp,
  .stream-icon .icon-stream-post-tip {
    color: #ffffff;
  }
  .STPleft p,
  .STPright p {
    color: #fff;
  }
  .STPdetail span {
    border-top: 0px solid #6b6b6b;
  }
  .STPafter {
    border: 1px solid #333333;
  }

  .panel-item-wrap {
    background: #333333b5 !important;
  }
  .visible-scrollbar,
  .invisible-scrollbar,
  .mostly-customized-scrollbar {
    display: block;
    width: 10em;
    overflow: auto;
    height: 2em;
  }

  .invisible-scrollbar::-webkit-scrollbar {
    display: none;
  }

  /* Demonstrate a "mostly customized" scrollbar
   * (won't be visible otherwise if width/height is specified) */
  .mostly-customized-scrollbar::-webkit-scrollbar {
    width: 5px;
    height: 8px;
    background-color: #333; /* or add it to the track */
  }

  /* Add a thumb */
  .mostly-customized-scrollbar::-webkit-scrollbar-thumb {
    background: #333;
  }

  span.dragtable-drag-handle {
    font-size: smaller;
  }

  img {
    filter: brightness(50%) !important;
  }

  /* table */
  tr:nth-of-type(2n) {
    background: #0000000f !important;
  }
  tr:nth-of-type(2n + 1) {
    background: #333 !important;
  }

  th {
    background: #0000000f !important;
    mix-blend-mode: difference;
  }

  .ant-table {
    border-radius: 0px;
  }

  #ordrbook-wrapper,
  .ordrbook-item-list:before,
  .sellbuycontent,
  .ordrbook-item-list:after,
  .sellstate .sellbuyhead {
    background: #333;
    border: 1px solid #333;
  }
  .running-trade-table > tbody td {
    color: #fff;
  }

  .trading-menu-a-wrap {
    background: #333333 !important;
  }

  .popoverpanel .panel-box a.unread {
    background: #e94948;
    color: #2e2e2e;
  }

  .panel-time {
    color: #ffffff;
  }

  #trading-nav a {
    background: #0b8759;
  }

  .trading-table th > span {
    color: #333 !important;
    display: inline-block !important;
  }

  /* Add Buttons */
  .ant-btn:hover,
  .ant-btn:focus,
  .ant-btn:active,
  .ant-btn.active {
    background: #333 !important;
  }

  button.ant-btn.trending-user-filter-date {
    background: #333333 !important;
    border: none !important;
  }

  .trading-wrap {
    border: 1px solid #333333;
  }

  .trading-table .ant-table-thead > tr > th {
    border-bottom: 1px solid #333333;
  }
  #trading-box {
    border: 1px solid #333333 !important;
  }
  .equity {
    border-right: 1px solid #333333;
  }

  .tradingnav {
    border: 1px solid #333333;
  }

  .tradingnav + .tradingnav {
    border-left: 1px solid #333333;
  }

  .tradingnav:last-child {
    border-right: 1px solid #333333;
  }
}
