/* ==UserStyle==
@name         Let me fix that for you, Youtube
@namespace    USO Archive
@version      20220905.20.12
@description  "Places the comment section between the video and the video suggestion column, so you scroll and read comments while looking at the video. Also removes promos, teasers, posts from creators, and recommended filter.\r\n\r\nThere is a slight bug with the width of the video timestamp slider on certain resolutions. Sorry about that."
@author       Rickard Elim채채 (This is Pulp)
@license      NO-REDISTRIBUTION==/UserStyle== */

@-moz-document domain("www.youtube.com") {
:root {
  --comment-column-width: 380px;
  --right-column-width: 350px;
  --column-spacing: 24px;
}

#columns #below {
  position: static !important;
}

#columns .watch-active-metadata {
  position: relative;
}

@media (min-width: 830px) {
  #columns #primary {
    display: block;
    padding-left: 24px;
  }
  #columns #primary>#primary-inner,
  #columns #primary #player,
  #columns #primary #info,
  #columns #primary #meta {
    max-width: calc(100vw - var(--right-column-width) - 42px);
  }
  #columns #primary video {
    max-width: 100% !important;
  }
  #columns #primary .ytp-chrome-bottom {
    width: calc(100% - 12px) !important;
  }
  #columns #primary>div {
    display: block;
    position: relative;
  }
  #columns #primary video {
    position: static;
    height: auto !important;
  }
  #columns #primary>div>* {
    width: var(--ytd-watch-flexy-player-width);
  }
  #columns #primary ytd-comments {
    position: absolute;
    top: -12px;
    left: calc(100% + var(--column-spacing));
    max-width: var(--comment-column-width);
    width: 100%;
    max-height: 100vh;
    overflow-y: auto;
  }
  #columns #secondary {
    margin-left: var(--comment-column-width);
    width: var(--right-column-width);
    /* 400px in the max-width above */
  }
}

@media (min-width: 1000px) and (max-width: 1040px) {
    #columns ytd-comments {
        left: calc(100% + 42px) !important;
    }
}

@media (min-width: 1000px) and (max-width: 1550px) {
    #columns ytd-thumbnail {
        width: 130px !important;
    }
}

ytd-ad-slot-renderer {
  position: relative;
}

#content ytd-ad-slot-renderer::before {    /* Hiding ad stuff*/
  content: 'blocked ad space';
  color: white;
  position: absolute;
  right: 0px; left: 0px; top: 0px; bottom: 0px;
  background-color: var(--yt-spec-base-background);
  z-index: 10;
    
}
    
    
#chips-below,               /* recommended filters */
ytd-mealbar-promo-renderer, /* popups in the down left */
ytd-rich-section-renderer,  /* posts from channels in home view */
#cinematics,                /* background blur behind the player */
div#masthead.ytd-rich-grid-renderer,/* top promo banner */
ytd-rich-section-renderer,          /* Hiding ad spots */
ytd-ad-hover-text-button-renderer,  /* Hiding ad spots */
ytd-action-companion-ad-renderer,   /* Hiding ad spots */
ytd-promoted-sparkles-web-renderer, /* Hiding ad spot */
ytd-in-feed-ad-layout-renderer,     /* Hiding ad spot in search response */
.ytp-cards-teaser {
    display: none !important;
}
}
