style.css:
@-moz-document domain("start.me") {
.app__body {
		background: url(https://www.toptal.com/designers/subtlepatterns/uploads/furley_bg.png);
	}

	.header {
		background-color: rgba(0, 0, 0, .05);
	}

	.widget-page__searchbar.widget-page__searchbar_allowed {
		visibility: hidden;
		height: 0px;
		margin-top: 0px;
		padding-top: 0px
	}

	.widget-page__section {
		margin-top: 0px;
		padding-top: 15px
	}

	.page-footer {
		visibility: hidden;
		height: 0px;
		margin-top: 0px;
		padding-top: 0px
	}

	.widget-page__ads {
		visibility: hidden;
		height: 0px;
		margin-top: 0px;
		padding-top: 0px
	}

	.header-right__button.header-right__button_type_custom.header-right__button_green {
		visibility: hidden;
		width: 0px;
		margin-top: 0px;
		padding-top: 0px
	}

	.widget__container {
		position: relative;
		display: block;
		min-height: 62px;
		-webkit-user-select: none;
		-moz-user-select: none;
		-ms-user-select: none;
		user-select: none;
		background: url("https://www.toptal.com/designers/subtlepatterns/uploads/furley_bg.png");
		border-radius: 0px 12px 0px 12px;
		padding: 4px;
		color: rgba(0, 0, 0, 1);
		box-shadow: 0 0px 4px 0 rgba(0, 0, 0, 0.2), 0 4px 4px 0 rgba(0, 0, 0, 0.2);
	}

 .widget.widget_editable {
		border-radius: 0px 12px 0px 12px;
		padding: 4px;
		color: rgba(0, 0, 0, 0.5);
	}

	.widget-header {
		border-radius: 0px 12px 0px 12px;
		height: 35px;
		background-color: rgba(0, 0, 0, 0.05);
	}

	.app__color-layer {
		background: url("https://www.toptal.com/designers/subtlepatterns/uploads/white_wall_hash.png");
	}

	.widget__body_lifted {
		margin-top: 10px;
	}
}
