style.css:
@-moz-document
/* http://parsish.com/ */
/* Hamed Malek (info@parsish.com) */
/* Persian Style include Tahoma Font & RTL Direction for Bizzly */

 domain("brizzly.com") 
{
.tweet-form-input {
font-family:tahoma!important;direction:rtl!important;
}
.logo{
display:none;
}
.tweet .author, #sidebar .trends .content, .timeline-protected-user img, .message-conversation .minimize {
float:right!important;margin-left:5px!important;margin-left:0px!important;
}
.notification-bar-contents {
text-align:right!important;font-family:tahoma!important;direction:rtl!important;
}

.meta {
padding-right:150px;
}
.tt-facebook .tweet, .wide .tweet {
font-size:12px!important;
}
.tweet, .fb-status {
font-size:12px!important;
}
}
