style.css:
@-moz-document url("https://www.youtube.com/") {
#content-text.ytd-comment-renderer {
    --yt-endpoint-color: var(--yt-spec-call-to-action);
    --yt-endpoint-hover-color: var(--yt-spec-call-to-action);
    --yt-endpoint-visited-color: var(--yt-spec-call-to-action);
    color: var(--yt-spec-text-primary);
    font-family: "Roboto","Arial",sans-serif;
    font-size: 2rem;
    line-height: 2rem;
    font-weight: 400;
    line-height: 2rem;
    overflow-wrap: anywhere;
}
}

@-moz-document domain("youtube.com"), url-prefix("https://www.youtube.com/") {
#content-text.ytd-comment-renderer {
    --yt-endpoint-color: var(--yt-spec-call-to-action);
    --yt-endpoint-hover-color: var(--yt-spec-call-to-action);
    --yt-endpoint-visited-color: var(--yt-spec-call-to-action);
    color: var(--yt-spec-text-primary);
    font-family: "Roboto","Arial",sans-serif;
    font-size: 2rem;
    line-height: 2rem;
    font-weight: 400;
    line-height: 2rem;
    overflow-wrap: anywhere;
}
}
