style.css:
@-moz-document domain("elchapuzasinformatico.com") {
/*
     * Creado por Amir Torrez
     */


    /* Anuncios */
    .ads,
    #text-3,
    .ad-area,
    #ad-container,
    .single-ad,
    .__lxG__double,
    .widget_block,
    .custom_html-2,
    .actirise-brand,
    #hbdbrk-slideup,
    .nomargin,
    .brand-actirise,
    .actirise-brand iframe
    {
        height: 0 !important;
        width: 0 !important;
        min-height: 0 !important;
        min-width: 0 !important;
        display: none !important;
    }

    /* Contenedor principal */
    #primary
    {
        position: relative !important;
        width: auto !important;
        top: 50px !important;
    }

    /* Pie selector de navegacion (paginas) del sitio principal */
    .navigation
    {
        margin-bottom: 35px !important;
    }

    /* Pie caja de comentarios */
    #disqus_thread
    {
        margin-bottom: 35px !important;
    }

    /* Separación pie de página */
    #colophon
    {
        margin-top: 60px !important;
    }

    /* Barra de búsqueda */

    #secondary
    {
        position: absolute !important;
        width: 96% !important;
    }

    #search-2 input
    {
        background: #333 !important;
    }
}
