style.css:
@-moz-document domain("play.pokemonshowdown.com") {
@font-face {
		font-family: 'Encode Sans Condensed';
		src: url(https://fonts.gstatic.com/s/encodesanscondensed/v10/j8_16_LD37rqfuwxyIuaZhE6cRXOLtm2gfT2hq-M.woff2);
	}
	@font-face {
		font-family: 'IBM Plex Sans';
		src: url(https://fonts.gstatic.com/s/ibmplexsans/v19/zYXgKVElMYYaJe8bpLHnCwDKhdHeFQ.woff2);
	}
	@font-face {
		font-family: 'Abel';
		font-style: normal;
		font-weight: 400;
		src: url(https://fonts.gstatic.com/s/abel/v18/MwQ5bhbm2POE2V9BPQ.woff2) format('woff2');
		unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
	}
	
	* {
		font-family: 'IBM Plex Sans', sans-serif;
	}
	
	.ps-room {
		display: grid;
		grid-template-columns: minmax(0, 802px) minmax(190px, 1fr);
	}

	.ps-room.small-layout {
		grid-template-columns: minmax(0, 1fr);
		grid-template-rows: max-content;
	}
	
	.whatdo {
		display: flex;
		align-items: center;
		flex-wrap: wrap;
		gap: 0.6rem 0.2rem;
	}
	
	.whatdo .healthy {
		margin-right: auto;
	}
	
	.ps-room .battle {
		display: flex;
		inset: 0;
		min-width: 100%;
		min-height: 100%;
		position: relative;
		overflow: auto;
		background: none;
		border: none;
		grid-column-start: 1;
		grid-row-start: 1;
	}
	
	.ps-room .battle .innerbattle {
		width: 100%;
		height: 100%;
		overflow: hidden;
		max-width: 802px;
	}
	
	.ps-room .battle-controls {
		width: 100%;
	}
	
	.ps-room:not(.small-layout) .battle-controls {
		box-sizing: border-box;
		padding: 8px;
		max-width: 802px;
		top: 0 !important;
		position: relative;
		grid-column-start: 1;
		grid-row-start: 2;
	}
	
	.ps-room:not(.small-layout) .battle-log {
		position: relative;
		inset: 0;
		top: 0;
		grid-column-start: 2;
		grid-row-start: span 2;
	}
	
	.ps-room.ps-room:not(.small-layout) .battle-log-add {
		position: relative;
		grid-column-start: 2;
		left: 0;
		width: auto;
		grid-row-start: 3;
		height: max-content;
	}
	
	.ps-room .foehint {
		position: absolute;
	}
	
	.battle-chat-toggle {
		z-index: 10;
	}
	
	/********************
