	.statbar { font-size: 1.2em; transform: translateY(-8px); }
	
	.leftbar { background-color: transparent; border: none; }
	
	.rightbar { background-color: transparent; border: none; }
	
	.trainer {
		text-align: center;
		position: absolute;
		left: 0;
		right: 0;
		bottom: 75px;
	}
	
	.trainer-far { bottom: 75px; }
	
	.trainer strong {
		display: block;
		font-size: 8pt;
		margin-bottom: 2px;
		word-wrap: break-word;
		color: #fff;
		text-shadow: -1px -1px #000, 0 -1px #000, 1px -1px #000, 1px 0 #000, 1px 1px #000, 0 1px #000, -1px 1px #000, -1px 0 #000;
	}
	
	.statbar.rstatbar {
		background-image: url("https://i.imgur.com/HUfwjFb.png");
		background-size: contain;
		background-repeat: no-repeat;
		background-position: center;
		width: 181px;
		box-sizing: border-box;
		height: 80px;
		padding-top: 11px;
		transform: translateY(-11px);
	}
	.statbar.rstatbar strong { margin: 0 2px 0 10px; }
	.statbar.rstatbar .hpbar {  margin-left: 10px; }
	.statbar.lstatbar {
		background-image: url("https://i.imgur.com/u2GdVEi.png");
		background-size: contain;
		background-repeat: no-repeat;
		background-position: center;
		width: 197px;
		box-sizing: border-box;
		height: 80px;
		padding-top: 18px;
		transform: translateY(-18px);
	}
	.statbar.lstatbar strong {  margin: 0 20px 0 8px; }
	.statbar.lstatbar .hpbar { margin-left: 8px; }
	
	.statbar.lstatbar.leftstatbar { 
		z-index: 1;
	}
	.statbar.lstatbar.rightstatbar .status { 
		transform: rotateY( 48deg ) translate3d(-38px, 14px, -97px);
		width: 140px;
	}
	.statbar.rstatbar.rightstatbar { 
		z-index: 1;
	}
	.statbar.rstatbar.leftstatbar .status { 
		width: 148px; 
		transform: rotateY( 48deg ) translate3d(-4px, 14px, -97px);
	}
	
	.statbar strong {
		font-family: 'Encode Sans Condensed';
		display: flex;
		align-items: center;
		margin: 0;
		color: #fff;
		font-weight: 400;
		text-align: start;
		text-shadow: -1px -1px #000, 0 -1px #000, 1px -1px #000, 1px 0 #000, 1px 1px #000, 0 1px #000, -1px 1px #000, -1px 0 #000;
	}
	.statbar strong small {
		color: #ebc465;
		font-weight: 600;
	}
	.statbar strong small:nth-child(1) { margin-left: auto; }
	
	.statbar img[alt~="M"], .statbar img[alt~="F"] { margin-left: auto; }
	.statbar img[alt~="M"] {
		content: url("https://i.imgur.com/qZTiMw3.png");
		width: 13px;
		height: 13px;
	}
	.statbar img[alt~="F"] {
		content: url(https://i.imgur.com/aAyWAi2.png);
		width: 13px;
		height: 13px;
	}
	
	.statbar .hpbar {
		position: absolute;
		border: 1px solid #161616;
		border-width: 1px 1.5px 0px 2.5px;
		background: #161616;
		padding: 1px;
		height: 10px;
		border-radius: 8px 3px 10px 8px !important;
		transform-style: preserve-3d;
		transform-origin: left;
		transform: perspective( 380px ) rotateY( -48deg ) translateX(-3px);
	}
	
	.statbar .hpbar .hptext {
		position: absolute;
		color: #EEEEEE;
		text-align: center;
		left: 20px !important;
		top: 7px;
		right: unset;
		font-size: 12px;
		background: none;
		text-shadow: -1px -1px #000, 0 -1px #000, 1px -1px #000, 1px 0 #000, 1px 1px #000, 0 1px #000, -1px 1px #000, -1px 0 #000;
		transform: rotateY(48deg) translateZ(24px);
		font-weight: bold;
	}
	
	.statbar .hpbar .hptextborder { display: none !important; }
	
	.statbar .hpbar .hp {
		height: 6px;
		border-top: 1px solid rgba(0,255,8,1);
		background: linear-gradient(90deg, rgba(163,255,26,1) 0%, rgba(0,255,8,1) 100%);
		border-bottom: 1px solid rgba(0,255,8,1);
		border-right: 1px solid rgba(0,255,8,1);
		border-radius: 8px 3px 10px 8px !important;
	}
	
	.statbar .hpbar .hp-yellow {
		border-top-color: rgba(254,170,50,1);
		background: linear-gradient(90deg, rgba(255,250,31,1) 0%, rgba(254,170,50,1) 100%);
		border-bottom-color: rgba(254,170,50,1);
		border-right-color: rgba(254,170,50,1);
	}
	
	.statbar .hpbar .hp-red {
		border-top-color: #dc1509;
		background: #dc1509;
		border-bottom-color: #dc1509;
		border-right-color: #dc1509;
	}
	
	.statbar .hpbar .prevhp {
		background: #3a3a3a;
		height: 8px;
		border-radius: 8px 3px 10px 8px !important;
	}
	.statbar .hpbar .prevhp-yellow { background-color: #3a3a3a; }
	.statbar .hpbar .prevhp-red { background-color: #3a3a3a; }
	
	.statbar .status {
		display: flex;
		gap: 2px;
		justify-content: start;
		align-items: center;
		flex-wrap: wrap;
		min-height: 10px;
		font-size: 8pt;
		color: #fff;
		text-transform: uppercase;
		position: absolute;
		width: 200px;
		transform: rotateY( 48deg ) translate3d(24px, 14px, -97px);
	}
	
	.statbar span {
		padding: 1px 3px;
		border: 1px solid #f40;
		color: #fff !important;
		border-radius: 0;
	}
	
	.hpbar span.bad {
		border-color: #4c0603;
		background: #a61000;
		text-shadow: -1px -1px #4c0603, 0 -1px #4c0603, 1px -1px #4c0603, 1px 0 #4c0603, 1px 1px #4c0603, 0 1px #4c0603, -1px 1px #4c0603, -1px 0 #4c0603;
	}
	.hpbar span.good {
		background: #00a636;
		border-color: #034c16;
		text-shadow: -1px -1px #034c16, 0 -1px #034c16, 1px -1px #034c16, 1px 0 #034c16, 1px 1px #034c16, 0 1px #034c16, -1px 1px #034c16, -1px 0 #034c16;
	}
	.hpbar span.neutral {
		background: #d0d0d0;
		border-color: #797979;
		text-shadow: -1px -1px #797979, 0 -1px #797979, 1px -1px #797979, 1px 0 #797979, 1px 1px #797979, 0 1px #797979, -1px 1px #797979, -1px 0 #797979;
	}
	.hpbar img.pixelated {
		height: 17.5px;
		top: 0;
