	@keyframes statusBlink {
		0%   { opacity: 1; }
		30%  { opacity: 0; }
		40%  { opacity: 0; }
		70% { opacity: 1; }
	}
	
	.statbar span.par, 
	.statbar span.frz, 
	.statbar span.brn, 
	.statbar span.psn, 
	.statbar span.slp {
		opacity: 1;
		animation: statusBlink 3s infinite;
		animation-delay: 2s;
		position: absolute;
		right: 13px;
		top: -38px;
		height: 15px;
		background: none;
		color: transparent;
	}
	
	.statbar span.par { content: url(https://i.imgur.com/377GRJI.png); }
