	@keyframes bobbing {
		0%   { transform: translate(0px, -50%) rotateY(180deg) }
		50%  { transform: translate(-3px, -50%) rotateY(180deg) }
		100%   { transform: translate(0px, -50%) rotateY(180deg) }
	}
	
	.movemenu button:not(:disabled):hover small.type::before {
		content: "";
		background: url(https://i.imgur.com/hJE4Jxm.png);
		background-size: contain;
		background-repeat: no-repeat;
		background-position: center;
		width: 20px;
		height: 20px;
		position: absolute;
		right: 4px;
		top: 50%;
		z-index: 1;
		animation: bobbing 1s infinite;
	}
	
	.movemenu button:not(:disabled):active small.type::before {
		animation: none;
		transform: translate(-6px, -50%) rotateY(180deg);
		transition: transform all ease 0.05s;
	}
	
	@keyframes cameraMovementBase {
		10%   { transform: translate(0px, 0px) scale(1); }
