style.css:
@-moz-document url-prefix("https://lk.samgtu.ru") {
/*ОСНОВНЫЕ*/
    :root {
        --primary: #1443b8;
        --primary_lighter: #a3baf5;
        --primary_darker: #173582;
        --secondary: #f51933;
        --tertiary: #66d9ff;
        --background: #17181c;
        --surface: #22252a;
        --surface2: #292c32;
        --btn: #2b303b;
        --btn_pressed: #1c1f26;
        --text: #fff;
        --class: #1f2530;
        --class_personal: #52e08d;
        --class_unread: #f04444;
        --class_newtask: #eb9947;
        --class_projectwork: #44e8ee;
    }

    * {
        font-family: 'Golos Text', sans-serif;
    }

    body {
        color: var(--text);
        background-color: var(--background);
        line-height: 1.5;
    }

    .page-container,
    #page-content,
    .page-content {
        background: var(--background) !important;
    }

    a {
        color: var(--primary_lighter);
        text-decoration: none !important;
        transition: color ease-in-out 0.2s;
    }

    a:hover {
        color: #668bcc;
    }

    .table .table {
        background-color: var(--background);
    }

    .styled-button {
        font-family: 'Golos Text', sans-serif !important;
        font-size: 14px;
    }

    h1,
    h2 {
        font-weight: 600;
        line-height: 26px;
        color: var(--text);
    }

    h1 {
        margin: 50px 0 32px;
        font-size: 32px;
    }

    /* .container, .container-fluid {
    padding-left: 0;
    padding-right: 0;
}

.row {
    margin-left: 0;
    margin-right: 0;
} */
    .wrapper {
        max-width: 2000px;
    }

    /*MENU*/
    .header-sidebar,
    .header-sidebar .sidebar-content {
        height: initial;
        background: var(--surface);
    }

    .header-sidebar {
        border-bottom: none;
    }

    .sidebar {
        background: var(--surface);
        width: 75px;
        min-height: calc(100vh - 125px);
        transition: width 0.2s;
        overflow: hidden;
        padding-bottom: 40px;
    }

    #scrollbar-menu::-webkit-scrollbar-thumb {
        background-color: rgba(255, 255, 255, 0.25);
        background-image: none;
        border: 4px solid var(--surface);
        border-radius: 20px;
    }

    #scrollbar-menu::-webkit-scrollbar-track {
        background-color: var(--surface);
    }

    .main-menu {
        margin-top: 30px;
        padding: 0;
    }

    #scrollbar-menu > div > ul > li:nth-child(16),
    #scrollbar-menu > div > ul > li:nth-child(1) {
        display: none;
    }

    .main-menu__item {
        display: block;
        padding: 16px 16px 16px 27px !important;
    }

    .main-menu__item--active,
    .main-menu__item--dropdown--open {
        background: var(--surface2);
    }

    .main-menu__item--has-dropdown:after {
        content: '';
        position: absolute;
        width: 8px;
        height: 8px;
        transform: rotate(45deg);
        border: 2px solid #fff;
        border-left: 0;
        border-top: 0;
        right: 26px;
        top: 18px;
        transition: transform ease-out 0.3s, top 0.3s, border ease-in-out 0.2s;
    }

    .main-menu__item--dropdown--open:after {
        transform: rotate(-135deg);
        top: 22px;
    }

    .main-menu__link {
        line-height: 22px;
        font-weight: 500;
        transition: color ease-in-out 0.2s;
    }

    .main-menu__item:hover .main-menu__link {
        color: var(--tertiary);
        text-decoration: none;
    }

    .main-menu__item:hover .main-menu__icon {
        fill: var(--tertiary);
    }

    .main-menu__item:hover.main-menu__item--has-dropdown:after,
    .main-menu__item--dropdown--open:hover:after {
        border: 2px solid var(--tertiary);
        border-left: 0;
        border-top: 0;
    }

    .main-menu__icon {
        fill: #fff;
        width: 20px;
        height: 20px;
        margin-right: 20px;
        transition: fill ease-in-out 0.2s;
    }

    .toggle-menu {
        width: 20px;
        height: 20px;
        position: relative;
        margin: 0 27px;
    }

    .toggle-menu:hover:after,
    .toggle-menu:hover:before {
        background: var(--secondary);
    }

    .toggle-menu:hover:before {
        box-shadow: 0 6px 0 0 var(--secondary);
    }

    .logo {
        width: 150px;
        padding: 16px;
    }

    .toggle-menu:before {
        top: 2px;
        -webkit-box-shadow: 0 6px 0 0 #fff;
        box-shadow: 0 6px 0 0 #fff;
    }

    .toggle-menu:after {
        bottom: 4px;
    }

    .toggle-menu:after,
    .toggle-menu:before {
        height: 2px;
        transition: all ease-in-out 0.2s;
    }

    /*end MENU*/
    /*HEADER*/
    .header {
        height: initial;
        background-color: var(--background);
        align-items: center;
    }

    .header > .header-content .container-fluid {
        display: flex;
        align-items: center;
        justify-content: space-between;
        gap: 24px;
    }

    .header > .header-content .container-fluid:before,
    .header > .header-content .container-fluid:after {
        content: none;
    }

    .current-user {
        margin: 0;
        float: none;
        font-weight: 400;
        display: flex;
        align-items: center;
    }

    .current-user__icon-box {
        line-height: initial !important;
        text-align: initial;
        vertical-align: initial;
        border-radius: 50%;
        float: initial;
        margin-right: 16px;
    }

    .current-user__icon,
    .header-link__icon {
        width: 16px;
        height: 16px;
        fill: var(--text);
        transition: fill ease-in-out 0.2s;
    }

    .header-links {
        float: none;
        margin: 0;
        display: flex;
        gap: 16px;
        flex-grow: 1;
        justify-content: flex-end;
    }

    .header-link__item:nth-child(1) {
        display: none;
    }

    .header-link__item {
        width: 40px;
        height: 40px;
        margin-left: 0;
    }

    .header-link,
    .current-user__icon-box {
        width: 40px;
        height: 40px;
        background-color: transparent;
        border: 1px solid rgba(255, 255, 255, 0.25);
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .current-user:hover .current-user__name,
    .current-user:hover .current-user__icon,
    .header-link:hover .header-link__icon {
        color: var(--primary_lighter);
        fill: var(--primary_lighter);
    }

    .current-user__name {
        font-size: 18px;
        color: var(--text);
        margin-bottom: 4px;
        font-weight: 600;
        transition: color ease-in-out 0.2s;
    }

    .current-user__info {
        font-size: 14px;
        color: rgba(255, 255, 255, 0.4);
    }

    .logout {
        float: none;
        color: var(--text);
        margin: 0;
        display: flex;
        order: 999;
        align-items: center;
        font-weight: 400;
        transition: color ease-in-out 0.2s;
    }

    .logout:hover {
        color: var(--primary_lighter);
        text-decoration: none;
    }

    .logout:hover .logout__icon {
        fill: var(--primary_lighter);
    }

    .logout__icon {
        width: 20px;
        height: 20px;
        margin-right: 8px;
        fill: var(--text);
        transition: fill ease-in-out 0.2s;
    }

    /*end HEADER*/
    .distance-learning-index {
        padding-left: 0;
        padding-right: 0;
    }

    .menu-btns {
        margin-top: 20px;
        padding-top: 0;
        padding-bottom: 0;
        margin-bottom: 16px;
    }

    .menu-btns > .btn-group,
    .menu-btns > .btn-group-vertical {
        display: flex;
        vertical-align: middle;
        flex-wrap: wrap;
        align-items: center;
        row-gap: 8px;
        column-gap: 24px;
    }

    .menu-btns > .btn-group > .btn {
        padding: 0;
        text-transform: initial;
        font-size: 20px !important;
        font-weight: 600;
        letter-spacing: 0;
        background: none !important;
        border: none;
        margin: 0;
        text-align: left;
        flex-grow: 0;
        margin-bottom: 0;
        color: rgba(255, 255, 255, 0.25);
    }

    .menu-btns > .btn-group > .btn[href = "https://samgtu.ru/distance-learning/instructions-for-students"]
    {
        display: none;
    }

    .menu-btns .active:after,
    .menu-btns a:after,
    .menu-btns a:after {
        width: 0;
        height: 2px;
        background-color: var(--primary_lighter);
        content: '';
        display: block;
        margin: 4px auto;
        transition: width 0.3s ease-out;
    }

    .menu-btns .active,
    .menu-btns a:hover,
    .menu-btns a:focus {
        color: var(--text) !important;
        box-shadow: none;
    }

    .menu-btns .active:after,
    .menu-btns a:hover:after,
    .menu-btns a:focus:after {
        width: 100%;
    }

    .btn-secondary:not(:disabled):not(.disabled):active:focus,
    .btn-secondary:not(:disabled):not(.disabled).active:focus,
    .show > .btn-secondary.dropdown-toggle:focus {
        box-shadow: none;
    }

    .square {
        display: none !important;
    }

    .lgnd {
        border: none;
        padding: 0;
        background: none;
        margin: 0px;
        color: var(--background);
        line-height: 20px;
        font-weight: 400;
    }

    .lgnd > .lgnd:nth-child(1) {
        background-color: var(--class);
        color: var(--text);
    }

    .lgnd > .lgnd:nth-child(2) {
        background-color: var(--class_personal);
    }

    .lgnd > .lgnd:nth-child(3) {
        background-color: var(--class_unread);
        color: var(--text);
    }

    .lgnd > .lgnd:nth-child(4) {
        background-color: var(--class_newtask);
    }

    .lgnd > .lgnd:nth-child(5) {
        background-color: var(--class_projectwork);
    }

    .lgnd > .lgnd {
        border-radius: 8px;
        padding: 4px 10px;
        border: none;
    }

    .lgnd.full-wifth {
        display: flex;
        gap: 8px;
        flex-wrap: wrap;
    }

    #calendar {
        margin-top: 20px !important;
    }

    .fc-button {
        display: inline-block;
        font-weight: 400;
        padding: 8px 16px;
        font-size: 14px;
        line-height: 1.5;
        border-radius: 8px;
    }

    .fc-button-primary {
        color: #fff;
        background-color: var(--btn);
        border: none;
        transition: background-color 0.2s ease-in;
    }

    .fc-button-primary:not(:disabled):hover,
    .fc-button-primary:not(:disabled).fc-button-active {
        background-color: var(--btn_pressed);
        border: none;
    }

    .fc-button-primary:disabled {
        color: #fff;
        background-color: rgba(255, 255, 255, 0.05);
        border: none;
        cursor: not-allowed;
    }

    .fc-button .fc-icon {
        font-size: 2rem;
    }

    .fc-toolbar > * > :not(:first-child) {
        margin-left: 16px;
    }

    .fc-toolbar h2 {
        font-size: 20px;
        font-weight: 700;
        margin: 0;
        text-transform: uppercase;
        color: var(--text);
    }

    .fc-toolbar.fc-header-toolbar {
        margin-bottom: 16px;
    }

    .fc-unthemed th,
    .fc-unthemed td,
    .fc-unthemed thead,
    .fc-unthemed tbody,
    .fc-unthemed .fc-divider,
    .fc-unthemed .fc-row,
    .fc-unthemed .fc-content,
    .fc-unthemed .fc-popover,
    .fc-unthemed .fc-list-view,
    .fc-unthemed .fc-list-heading td {
        border-color: rgba(255, 255, 255, 0.1);
    }

    .fc table {
        font-size: 16px;
    }

    /*.fc th,
.fc td {
    padding: 4px;
    vertical-align: top;
}*/
    .fc-head {
        display: block;
        margin-bottom: 20px;
        background-color: var(--surface);
        overflow: hidden;
        border-radius: 10px;
        border: 2px solid;
    }

    td.fc-head-container.fc-widget-header {
        padding: 16px;
        border: none;
    }

    .project_dates {
        background: rgba(68, 232, 238, 0.25);
    }

    .fc-unthemed td.fc-today {
        background: none;
    }

    .fc-day-header {
        text-transform: uppercase;
        color: #fff;
    }

    .fc-day-top.fc-today .fc-day-number::before {
        display: inline;
        position: relative;
        background: var(--primary);
        border-radius: 8px;
        content: 'cегодня';
        padding: 6px;
        right: 10px;
        color: rgba(255, 255, 255, 0.5);
        font-size: 12px;
    }

    .fc-dayGrid-view .fc-week-number,
    .fc-dayGrid-view .fc-day-number {
        padding: 8px;
    }

    td.fc-event-container {
        padding: 4px;
    }

    .fc-day-grid-event .fc-content {
        padding: 10px;
        text-overflow: ellipsis;
    }

    .fc-day-grid-event .fc-time {
        font-weight: 600 !important;
        margin-bottom: 4px;
        display: block;
    }

    .fc-event {
        font-size: 0.85em;
        line-height: 1.3;
        border-radius: 4px;
        border: none;
        transition: all ease-out 0.2s;
    }

    .fc-event,
    .fc-event-dot {
        background-color: var(--class);
        /* default BACKGROUND color */
    }

    .fc-event,
    .fc-event:hover {
        color: var(--text);
    }

    .fc-event:hover {
        transform: scale(0.95);
    }

    .popper,
    .tooltip {
        font-family: 'Proxima Nova';
        width: 250px;
        border-radius: 10px;
        border: none;
        padding: 16px;
        background-color: rgba(50, 50, 50, 0.75);
        backdrop-filter: blur(15px);
        box-shadow: 0px 4px 16px 0px rgba(0, 0, 0, 0.2);
    }

    .tooltip .tooltip-arrow,
    .popper .popper__arrow {
        border-color: rgba(50, 50, 50, 0.5);
    }

    .tooltip-inner {
        max-width: 450px;
        background: none;
        color: var(--text);
    }

    .side-block--bg--magnolia {
        background: var(--surface);
        font-weight: 400;
        border-radius: 8px;
    }

    .side-block--bg--magnolia .side-block__title {
        color: #fff;
        font-size: 32px;
        font-weight: 600;
        line-height: 26px;
        margin-bottom: 32px;
        margin-top: 16px;
    }

    .announcing-item__text {
        padding-bottom: 32px;
    }

    .read-more {
        color: #7ba2ee;
        text-decoration: none;
    }

    .footer {
        background-color: var(--surface);
    }

    .footer .wrapper:after {
        content: 'Dark mod by Lampadov';
        margin-left: 20px;
        opacity: 0.25;
    }

    *[style='background-color: #fca746'],
    *[style='background-color:#fca746;border-color:#fca746'] {
        background-color: var(--class) !important;
    }

    *[style='background-color:#fca746;border-color:#fca746'] .fc-time:after,
*[style='background-color:#fca746;border-color:#000000'] .fc-time:after {
        display: block;
        position: absolute;
        left: calc(100% - 16px - 10px);
        top: 10px;
        width: 16px;
        height: 16px;
        background: var(--class_newtask);
        border-radius: 50%;
        content: '';
    }

    *[style='background-color: #ed1746'],
    *[style='background-color:#ed1746;border-color:#ed1746'] {
        background-color: var(--class) !important;
    }

    *[style='background-color:#ed1746;border-color:#ed1746'] .fc-time:after {
        display: block;
        position: absolute;
        left: calc(100% - 16px - 10px);
        top: 10px;
        width: 16px;
        height: 16px;
        background: var(--class_unread);
        border-radius: 50%;
        content: '';
    }

    form,
    .grid-view {
        margin: 0px auto 48px !important;
        padding: 32px !important;
        background: var(--surface) !important;
        border-radius: 8px;
    }

    .grid-view p {
        line-height: 1.5;
        font-weight: 400;
        width: initial !important;
    }

    .table {
        color: #fff;
    }

    .big-icon {
        width: 32px !important;
        height: 32px !important;
    }

    .icon__ok {
        fill: var(--green);
    }

    .btn-success,
    .btn-primary {
        margin-bottom: 20px;
        padding: 8px 16px;
    }

    .alert-success {
        color: var(--green);
        background-color: var(--surface2);
        border: none;
        margin: 0;
        margin-bottom: 16px;
        padding: 16px;
        font-size: 18px;
        font-weight: 600;
        border-radius: 8px;
    }

    .chat-page {
        min-height: 330px;
        border: none;
        margin-bottom: 48px;
        background-color: var(--surface);
    }

    .dialog-title {
        padding: 16px 20px;
        background: var(--btn);
        border: none;
        width: 100%;
        margin-bottom: 0;
    }

    .dialogues-list {
        border-right: 1px solid #202228;
    }

    .friend-list--dialogues {
        height: 100%;
        overflow-x: hidden;
        overflow-y: auto;
    }

    .friend--dialog-active,
    .friend--dialog-selector:hover {
        background: var(--btn);
    }

    .friend--dialog-selector {
        padding: 16px 16px;
    }

    .friend__name {
        color: #fff;
        font-weight: 400;
    }

    .friend__message-time {
        color: rgba(255, 255, 255, 0.5);
        font-size: 14px;
        font-weight: 400;
    }

    .dialogue-messages::-webkit-scrollbar,
    .friend-list--dialogues::-webkit-scrollbar {
        width: 14px;
        background-color: rgba(0, 0, 0, 0);
    }

    .dialogue-messages::-webkit-scrollbar-thumb,
    .friend-list--dialogues::-webkit-scrollbar-thumb {
        background-color: #fff;
        border-radius: 20px;
        border: 5px solid var(--surface);
    }

    .dialog-title__users {
        padding-top: 0;
    }

    .dialogue-messages {
        overflow-x: hidden;
        overflow-y: auto;
        display: none;
        padding: 20px;
        height: 100%;
    }

    .message--in-dialog {
        border-bottom: 0;
        padding: 20px;
    }

    .message {
        overflow: hidden;
    }

    .message__autor {
        color: rgba(255, 255, 255, 0.25);
        margin-bottom: 8px;
    }

    .btn-link {
        color: var(--primary_lighter);
    }

    .message__text {
        font-weight: 400;
    }

    .message--unread {
        background: var(--btn);
        border-radius: 8px;
    }

    #dialogue-messages
        > div.message.message--in-dialog.ng-scope.message--unread
        > div.message__meta
        > div.message__autor.ng-binding:after {
        content: ' \2022';
        font-size: 20px;
        color: #fff;
    }

    .dialog-form-box {
        background: var(--surface);
        padding: 16px 20px;
    }

    .dialog-form-box .message-fl {
        margin: 0;
    }

    .message-send-form {
        align-items: center;
    }

    textarea.adaptive-textarea {
        height: auto;
        padding: 12px 16px;
        display: block;
        overflow: hidden;
    }

    textarea {
        box-shadow: none;
        background-color: transparent;
        color: rgba(255, 255, 255, 0.5);
        font-size: 14px;
        font-weight: 400;
        border: none;
        resize: none;
        line-height: 20px;
    }

    .attach-file,
    .message-send {
        margin-bottom: 0;
        position: initial;
        overflow: hidden;
        padding: 16px;
    }

    .attach-file svg,
    .message-send svg,
    .search-field-box svg {
        fill: var(--primary_lighter);
        width: 20px;
        height: 20px;
        margin: 0;
    }

    .gray {
        background-color: var(--surface);
    }

    .wait-content {
        background-color: var(--surface) !important;
        left: 0 !important;
        top: 0 !important;
        bottom: 0 !important;
        right: 0 !important;
        margin: auto !important;
        border-radius: 8px !important;
    }

    .fc-unthemed .fc-divider,
    .fc-unthemed .fc-popover .fc-header,
    .fc-unthemed .fc-list-heading td {
        background: var(--surface);
    }

    .fc-list-item.fc-has-url:hover td {
        background: var(--btn) !important;
        transition: all ease-in-out 0.2s !important;
    }

    .custom-checkbox + div::before {
        border: 2px solid var(--surface);
        padding: 0;
        text-align: center;
        transition: all 0.3s;
        font-size: 16px;
        margin-right: 8px;
        border-radius: 4px;
        cursor: pointer;
    }

    .custom-checkbox:hover + div::before {
        border: 2px solid var(--btn);
    }

    .custom-checkbox:checked + div::before {
        background: var(--secondary);
        border: none;
    }

    .breadcrumb {
        display: none;
    }

    #feedbackForm {
        backdrop-filter: blur(10px);
        background: rgba(0, 0, 0, 0.5);
        align-content: center;
        align-items: center;
        justify-content: center;
    }

    #feedbackForm .modal-content {
        background-color: var(--background);
        border: none !important;
        border-radius: 8px;
        box-shadow: none;
    }

    #feedbackForm .modal-content .modal-header {
        padding: 16px !important;
        border-bottom: none;
        justify-content: space-between;
        align-items: center;
    }

    .modal-header .modal-title {
        width: initial;
        font-size: 16px;
        max-height: initial;
        margin: 0;
        line-height: 1.5;
    }

    .custom-modal-header .close[data-dismiss='modal'] {
        position: initial;
        right: 0;
        top: 0;
        font-size: 30px;
        background: initial;
        width: 30px;
        height: 30px;
        text-align: center;
        line-height: 20px;
        margin: 0;
        padding: 0;
        vertical-align: top;
    }

    #feedbackForm form {
        background: none !important;
    }

    #create-form > div:nth-child(8) > button {
        margin: 0;
    }

    #feedbackForm .form-control {
        box-shadow: none !important;
        background-color: var(--surface2) !important;
        outline: none !important;
        color: #fff !important;
        font-weight: 400 !important;
