style.css:
@-moz-document url-prefix("http://alfavika.ru/chat.html") {
/* --- theme colors --- */
:root {
    --bgColor1: #323235;
    --bgColor2: #43464c;
    --bgColor3: #54545f;
    --textColor: #33bb33;
    --linkColor: #ffee00;
    --infoColor: #cecece;
    
    --myNickColor: #00ff00;
    --myMsgColor: #00ff00;
    --myNickHighlight: #fff800;
    
    --botNickColor: var(--linkColor);
    --questionColor: var(--infoColor);
    
    --otherNickColor: var(--infoColor);
    --otherMsgColor: var(--infoColor);

    --inputBorderColor: #003300;
    --buttonBgColor1: #336633;
    --buttonTextColor1: var(--infoColor);
    --buttonBgColor2: #aaa800;
    --buttonTextColor2: #000000;
    
    --shadowColor1: #000000;
    --shadowColor2: #222222;
}

/* --- BODY and stuff --- */

body {
    background: linear-gradient(var(--bgColor2), var(--bgColor1)) !important;
    color: var(--textColor);
}

a:link, a:visited {
    color: var(--linkColor);
}

a:hover {
    text-decoration: none;
}

/* --- HEADER --- */

.horizontalcssmenu ul li a {
    color: var(--textColor);
    border: 2px solid var(--bgColor1);
    border-left-width: 0;
}

.horizontalcssmenu ul li a#you b[style^='color:red']{
    color: var(--myNickColor) !important;
}

.horizontalcssmenu ul li ul li a {
    background-color: var(--bgColor3);
    padding: 4px;
}

.horizontalcssmenu ul li ul li a:hover {
    background-color: var(--textColor);
    color: #ffffff !important;
}

#panelAuth {
    margin-top: 8px;
    background-color: var(--bgColor1) !important;
    box-shadow: 0px 0px 5px var(--shadowColor1);
}

#panelAuth input[type=button] {
    border: 1px solid var(--inputBorderColor);
    background-color: var(--buttonBgColor1);
    color: #fff;
    box-shadow: 0 0 10px var(--inputBorderColor);
}

#panelAuth input {
    border: 1px solid var(--inputBorderColor);
    border-radius: 4px;
    background-color: var(--bgColor3);
    color: var(--myMsgColor);
    outline-width: 0;
}

#rule {
    background: var(--bgColor2) !important;
    position: relative;
    box-shadow: 0px 0px 14px var(--shadowColor1);
}

/*
#StSel {
    background-color: var(--bgColor2);
    color: var(--textColor);
    outline-width: 0;
    font-family: Verdana;
    font-size: 14px;
    height: 24px;
    border: 2px solid var(--bgColor1);
}
*/

#StSel {
    display: none;
}

#ChBxScr {
    outline-width: 0;
}

#authansw {
    color: var(--infoColor) !important;
    margin-left: 20px;
    font-style: normal !important;
}

#authansw::before {
    content:'üçï';
}

/* --- SIDEBAR --- */

#sidebar {
    padding-top: 5px;
}

#usersdiv {
    padding: 0px 5px;
}

/* --- FOOTER --- */

#sdiv #send, #sdiv #ButtonSend {
    font-size: 18px;
    line-height: 28px;
    padding-left: 10px;
    outline-width: 0;
}

#sdiv #send {
    border: 2px solid var(--inputBorderColor);
    border-radius: 5px;
    
    background-color: var(--bgColor3);
    color: var(--myMsgColor);
}

#sdiv #ButtonSend {
    border: 2px solid var(--inputBorderColor);
    border-radius: 5px;
    box-shadow: 0 0 8px var(--inputBorderColor);
    background-color: var(--buttonBgColor1);
    color: var(--buttonTextColor1);
}

/* --- CHAT AREA --- */
#chatdiv {
    padding-top: 10px;
}

#chatdiv div {
    padding: 2px 5px 2px 5px
}

#chatdiv .dw {
    margin-left: 25% !important;
    margin-right: 25% !important;
    background-color: var(--bgColor2);
    border: 0px solid #000;
    padding: 0 10px 10px 10px;
    margin-bottom: 12px;
}

#chatdiv .dw div b[style^='margin-top:8px'] {
    color: var(--questionColor);
    font-size: 16px; 
    line-height: 20px;
}

#chatdiv .dw div span[style^='background-color:#E0FFFF'] {
    background-color: var(--bgColor2) !important;
    font-weight: bold;
}

.BotStyle1 {
    color: var(--botNickColor);
}

.MyNickStyle1 {
    color: var(--myNickColor);
}

.MyMesStyle1 {
    color: var(--myMsgColor);
}

#chatdiv .dw div b[style^='color:red'], .MesStyle1 b[style^='color:red'], .MyMesStyle1 b[style^='color:red'] {
    color: var(--myNickHighlight) !important;
}

.UserStyle1, .UserStyle1:hover {
    color: var(--otherNickColor);
}

.MesStyle1 {
    color: var(--otherMsgColor);
}

.hiddenMes, .hiddenMes b {
    filter: blur(5px);
    color: var(--infoColor) !important;
}

.hiddenMes:hover, .hiddenMes:hover b {
    filter: none;
    color: var(--infoColor) !important;
}

/* --- DIALOGS --- */

#reportdiv, .DialogBox {
    border: 2px solid var(--shadowColor2) !important;
    border-radius: 10px;
    box-shadow: 0 0 20px var(--shadowColor2);
    background-color: var(--bgColor2) !important;
}

#reportdiv input, .DialogBox input, .DialogBox select {
    outline-width: 0;
    background-color: var(--bgColor3);
    border: 1px solid var(--inputBorderColor);
    color: var(--infoColor);
    padding: 4px 8px;
    margin-top: 4px;
}

#reportdiv input[type=button], .DialogBox input[type=button] {
    border-radius: 5px;
    background-color: var(--buttonBgColor1);
}

#reportdiv input[type=button]:hover, .DialogBox input[type=button]:hover {
    box-shadow: 0 0 4px var(--shadowColor2);
    cursor: pointer;
}

#reportdiv #repclose, .DialogBox #DialogBoxCancel {
    background-color: var(--buttonBgColor2);
    color: var(--buttonTextColor2);
}

.DialogBox #DialogBoxCmdLine {
    background-color: var(--bgColor1) !important;
    color: var(--linkColor);
}

.DialogBox label {
    padding-right: 4px;
}

#DialogBoxContent p:first-child {
    color: var(--linkColor);
    border: 1px dashed var(--infoColor);
    border-width: 0 0 1px 0;
    font-size:16px;
    line-height: 20px;
}

#reportdiv div:last-of-type, .DialogBox div:last-of-type {
    margin-top: 16px !important;
}

/* --- scrollbars --- */
::-webkit-scrollbar {
    width: 0.6em;
    height: 0.25em;
    box-shadow: 0 0 2px var(--shadowColor2);
}
::-webkit-scrollbar-button {
    height: 2em;
    background: #000;
}
::-webkit-scrollbar-track-piece {
    background: #333;
}
::-webkit-scrollbar-thumb {
    background: #000;
}

}
